UPDATE script_texts SET content_loc1='우리, 네가 준비될 때까지 기다린다.' WHERE entry=-1000605; /* content: We wait until you ready. */
UPDATE script_texts SET content_loc1='집에 갈 시간이야!' WHERE entry=-1000606; /* content: Home time! */
