UPDATE script_texts SET content_loc1='%s|1이;가; 아기를 가리킵니다.' WHERE entry=-1000636; /* content: %s points to the infant. */
UPDATE script_texts SET content_loc1='%s|1이;가; 훌쩍입니다.' WHERE entry=-1000637; /* content: %s sobs. */
UPDATE script_texts SET content_loc1='신께서 우리를 버렸소! 이미론의 귀에 들어가기 전에 죽여야 하오!' WHERE entry=-1000638; /* content: The gods have forsaken us! We must dispose of it before Ymiron is notified! */
UPDATE script_texts SET content_loc1='안 돼! 그러지 마세요! 제발! 우리 아이라고요!' WHERE entry=-1000639; /* content: NO! You cannot! I beg of you! It is our child! */
UPDATE script_texts SET content_loc1='여보, 그럼 어쩌자는 거요? 다른 방법이 없잖소. 그들이 알면 우리 둘 다 처형하고 말 거요.' WHERE entry=-1000640; /* content: Then what are we to do, wife? The others cannot find out. Should they learn of this aberration, we will all be executed. */
UPDATE script_texts SET content_loc1='내가... 내가 숨기겠어요. 여기서 아주 멀리... 안전한 장소를 찾을 때까지...' WHERE entry=-1000641; /* content: I... I will hide it. I will hide it until I find it a home, far away from here... */
