UPDATE script_texts SET content_loc1='아니면 이걸 받게 될 거예요.' WHERE entry=-1000659; /* content: Or THIS is what you get. */
UPDATE script_texts SET content_loc1='여기 좀 후끈하군요. 이동할까요?' WHERE entry=-1000660; /* content: It\'s getting a little hot over here. Shall we move on? */
