UPDATE script_texts SET content_loc1='나만큼 널 증오하는 이가 또 있을까? 일리단! 네게 받아야 할 빚이 남았다!' WHERE entry=-1564103; /* content: Their fury pales before mine, Illidan. We have some unsettled business between us. */
UPDATE script_texts SET content_loc1='아, 기나긴 사냥이 마침내 끝을 맺는구나. 바로 오늘 정의는 실현되리라!' WHERE entry=-1564105; /* content: My long hunt is finally over. Today, Justice will be done! */
UPDATE script_texts SET content_loc1='만 년 동안 응어리진 증오를 보여주마!' WHERE entry=-1564106; /* content: Feel the hatred of ten thousand years! */
UPDATE script_texts SET content_loc1='아, 끝났군. 네 놈이 졌다.' WHERE entry=-1564107; /* content: It is finished. You are beaten. */
UPDATE script_texts SET content_loc1='네가 이겼다... 마이에브. 하지만, 사냥감이 없는 사냥꾼은... 살아갈 힘을 잃는 법... 너도... 내가 없으면... 아무것도 아니야...' WHERE entry=-1564108; /* content: You have won... Maiev. But the huntress... is nothing without the hunt. You... are nothing... without me. */
UPDATE script_texts SET content_loc1='놈이 옳았어. 공허감이... 밀려오는군. 난 아무것도 아니야.' WHERE entry=-1564109; /* content: He\'s right. I feel nothing... I am... nothing. */
UPDATE script_texts SET content_loc1='용사들이여, 안녕히...' WHERE entry=-1564110; /* content: Farewell, champions. */
UPDATE script_texts SET content_loc1='진정한 힘에 무지한 족속들이여!' WHERE entry=-1564114; /* content: You know nothing of power! */
UPDATE script_texts SET content_loc1='이건 나이샤의 몫이다!' WHERE entry=-1564116; /* content: That is for Naisha! */
