UPDATE script_texts SET content_loc1='부하들아, 이 반역자에게 응당한 처벌을 내려라!' WHERE entry=-1564121; /* content: Come, my minions. Deal with this traitor as he deserves! */
UPDATE script_texts SET content_loc1='너무 쉽구나!' WHERE entry=-1564124; /* content: This is too easy! */
