UPDATE script_texts SET content_loc1='혼이여, 이리 돌아오라!' WHERE entry=-1568036; /* content: Spirit, come back to me! */
UPDATE script_texts SET content_loc1='깨끗이 베어 주마!' WHERE entry=-1568037; /* content: Me gonna carve ya now! */
