UPDATE script_texts SET content_loc1='스카발드, 이 무능한 놈 같으니! 돌아와라. 도움이 좀 돼 보란 말이다!' WHERE entry=-1574020; /* content: Skarvald, you incompetent slug! Return and make yourself useful! */
UPDATE script_texts SET content_loc1='%s|1이;가; 울부짖습니다!' WHERE entry=-1574022; /* content: %s roars! */
UPDATE script_texts SET content_loc1='주인님! 당신께서 주신 일을 행했습니다. 이제, 축복을 내려 주소서!' WHERE entry=-1575000; /* content: My liege! I have done as you asked, and now beseech you for your blessing! */
UPDATE script_texts SET content_loc1='이 감각은... 상상을 초월합니다. 저는 당신의 것입니다, 왕이시여.' WHERE entry=-1575002; /* content: The sensation is... beyond my imagining. I am yours to command, my king. */
UPDATE script_texts SET content_loc1='첫 번째 시험이 기다린다. 초대받지 않은 손님들을 처치해라.' WHERE entry=-1575003; /* content: Your first test awaits you. Destroy our uninvited guests. */
UPDATE script_texts SET content_loc1='당신의 이름으로 저들에게 죽음을 선사하겠습니다! 오라, 스컬지의 적이여! 내 너희에게 리치 왕의 강대함을 보여주겠다!' WHERE entry=-1575004; /* content: I will be happy to slaughter them in your name! Come, enemies of the Scourge! I will show you the might of the Lich King! */
UPDATE script_texts SET content_loc1='너희의 영혼까지 짓밟아주마!' WHERE entry=-1575005; /* content: I will vanquish your soul! */
UPDATE script_texts SET content_loc1='멍청한 놈... 리치 왕의 힘에 도전하다니...' WHERE entry=-1575006; /* content: You were a fool to challenge the power of the Lich King! */
UPDATE script_texts SET content_loc1='죽음을 피할 순 없다.' WHERE entry=-1575011; /* content: Your end is inevitable. */
UPDATE script_texts SET content_loc1='요르구울 막!' WHERE entry=-1575012; /* content: Yor-guul mak! */
UPDATE script_texts SET content_loc1='남길 말이라도?' WHERE entry=-1575013; /* content: Any last words? */
UPDATE script_texts SET content_loc1='내가 어떻게... 여기까지.. 왔는데...' WHERE entry=-1575014; /* content: Nooo! I did not come this far... to... */
UPDATE script_texts SET content_loc1='여기가 어디지? 나... 너희... 밟아주마!' WHERE entry=-1575015; /* content: What this place? I will destroy you! */
UPDATE script_texts SET content_loc1='쉬운 일이지!' WHERE entry=-1575017; /* content: An easy task! */
UPDATE script_texts SET content_loc1='이젠 겁이 좀 나나 보군...' WHERE entry=-1575026; /* content: Not so brash now, are you? */
UPDATE script_texts SET content_loc1='피도 눈물도 없는 것들아! 불쌍한 비룡을 괴롭히다니, 가만두지 않겠다!' WHERE entry=-1575029; /* content: You motherless knaves! Your corpses will make fine morsels for my new drake! */
UPDATE script_texts SET content_loc1='검은 폭풍의 비요른이여! 그대의 존재로 저를 축복하소서!' WHERE entry=-1575032; /* content: Bjorn of the Black Storm! Honor me now with your presence! */
UPDATE script_texts SET content_loc1='이제 무엇이... 날... 기다릴꼬...' WHERE entry=-1575040; /* content: What... awaits me... now? */
UPDATE script_texts SET content_loc1='%s|1이;가; 숨을 깊게 들이마십니다.' WHERE entry=-1575041; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc1='여기엔 내가 참 많지.' WHERE entry=-1576002; /* content: There\'s plenty of me to go around. */
UPDATE script_texts SET content_loc1='이제 끝을 볼 때다!' WHERE entry=-1576003; /* content: Now to finish the job! */
UPDATE script_texts SET content_loc1='운명을... 저주한다.' WHERE entry=-1576005; /* content: Damn the... luck. */
UPDATE script_texts SET content_loc1='혼돈이 손짓한다.' WHERE entry=-1576006; /* content: Chaos beckons. */
UPDATE script_texts SET content_loc1='실재가... 무너진다.' WHERE entry=-1576007; /* content: Reality... unwoven. */
UPDATE script_texts SET content_loc1='파괴할 수 없다.' WHERE entry=-1576008; /* content: Indestructible. */
UPDATE script_texts SET content_loc1='맞는 말이다.' WHERE entry=-1576010; /* content: Of course. */
UPDATE script_texts SET content_loc1='안 돼!' WHERE entry=-1576011; /* content: Noo! */
UPDATE script_texts SET content_loc1='으아아아아!' WHERE entry=-1576015; /* content: Aaggh! */
UPDATE script_texts SET content_loc1='지킨다고? 왜? 그건 아무 의미도 없다. 그래, 그래, 그렇지... 사실은 빼앗고 싶지? 이젠 나도 알겠어!' WHERE entry=-1576016; /* content: Preserve? Why? There\'s no truth in it. No no no... only in the taking! I see that now! */
UPDATE script_texts SET content_loc1='멈춰라. 마지막 순간을 즐기란 말이다.' WHERE entry=-1576017; /* content: Stay. Enjoy your final moments. */
UPDATE script_texts SET content_loc1='끝내라! 어서 끝내! 안 그러면 알렉스트라자께 맹세코 다시는 빛을 못 보게 해주마!' WHERE entry=-1576018; /* content: Finish it! Finish it! Kill me, or I swear by the Dragonqueen you\'ll never see daylight again! */
UPDATE script_texts SET content_loc1='이게 정말 원하던 거지!' WHERE entry=-1576019; /* content: Now we\'ve come to the truth! */
UPDATE script_texts SET content_loc1='용의 여왕... 생명의 어머니시여... 저를... 지키소서...' WHERE entry=-1576020; /* content: Dragonqueen... Life-Binder... preserve... me. */
UPDATE script_texts SET content_loc1='%s|1이;가; 혼돈의 균열을 엽니다!' WHERE entry=-1576021; /* content: %s opens a Chaotic Rift! */
UPDATE script_texts SET content_loc1='내 부하들이 쓰러졌구나. 하지만 또 다른 녀석들이 등장할 게다.' WHERE entry=-1578001; /* content: Clearly my pets failed. Perhaps another demonstration is in order. */
UPDATE script_texts SET content_loc1='여전히 반항이냐. 여전히 잘못된 신념에 매달리느냐. 이번에도 살아남는다면, 중심원에서 날 찾을 수 있을 게다.' WHERE entry=-1578002; /* content: Still you fight. Still you cling to misguided principles. If you survive, you\'ll find me in the center ring. */
UPDATE script_texts SET content_loc1='어리석은 족속들!' WHERE entry=-1578003; /* content: Poor blind fools! */
UPDATE script_texts SET content_loc1='마법의 지배자의 힘을... 약간... 아주 약간 느껴봐라!' WHERE entry=-1578004; /* content: A taste... just a small taste... of the Spell-Weaver\'s power! */
UPDATE script_texts SET content_loc1='불안정한 에너지라... 하지만 네놈들을 처치하려면 위험을 감수해야지!' WHERE entry=-1578005; /* content: So much unstable energy... but worth the risk to destroy you! */
UPDATE script_texts SET content_loc1='내가 누차 얘기했건만!' WHERE entry=-1578006; /* content: If only you understood! */
UPDATE script_texts SET content_loc1='이제 알겠느냐? 알겠냔 말이다!' WHERE entry=-1578007; /* content: Now do you see? Do you?! */
UPDATE script_texts SET content_loc1='유감스럽지만, 어쩔 수 없다!' WHERE entry=-1578008; /* content: Unfortunate, but necessary. */
UPDATE script_texts SET content_loc1='얼간이 같은 놈들! 너희가 지금 무슨 짓을 하고 있는지, 전혀 모르는구나. 지맥의 흐름을 방해하게 놔둘 것 같으냐? 내 너희를 완전히 끝장내 주마!' WHERE entry=-1578010; /* content: Simpletons! You cannot comprehend the forces you have set in motion. The ley line conduit will not be disrupted! Your defeat shall be absolute! */
UPDATE script_texts SET content_loc1='무례한 침입자 놈들, 정신이 나갔구나! 저 아래 내던져 주마!' WHERE entry=-1578011; /* content: You brash interlopers are out of your element! I will ground you! */
UPDATE script_texts SET content_loc1='불길을 잡는 건 식은 죽 먹기지.' WHERE entry=-1578013; /* content: It is trivial to extinguish your fire! */
UPDATE script_texts SET content_loc1='자연의 마법은 이제 너희 편이 아니다.' WHERE entry=-1578014; /* content: No magic of nature will help you now! */
UPDATE script_texts SET content_loc1='무례하고, 오만한 것들... 가만두지 않겠다!' WHERE entry=-1578015; /* content: Such insolence... such arrogance... must be PUNISHED! */
UPDATE script_texts SET content_loc1='하, 네가 속한 대지로 돌아가라!' WHERE entry=-1578017; /* content: Back to the earth with you! */
UPDATE script_texts SET content_loc1='떨어지니 즐거우냐?' WHERE entry=-1578018; /* content: Enjoy the fall! */
UPDATE script_texts SET content_loc1='내 사전엔 자비란 없다!' WHERE entry=-1578020; /* content: There will be no mercy! */
UPDATE script_texts SET content_loc1='적을 없애라! 날려 버려!' WHERE entry=-1578021; /* content: Blast them! Destroy them! */
UPDATE script_texts SET content_loc1='타협은 없다! 공격!' WHERE entry=-1578022; /* content: Take no prisoners! Attack! */
UPDATE script_texts SET content_loc1='지금 공격하라! 모두 처치해!' WHERE entry=-1578023; /* content: Strike now! Obliterate them! */
UPDATE script_texts SET content_loc1='%s|1이;가; 영혼의 차원으로 전환하자 변형물이 생성됩니다!' WHERE entry=-1578024; /* content: Anomalies form as %s shifts into the Astral Plane! */
UPDATE script_texts SET content_loc1='%s|1이;가; 강화된 신비한 폭발을 시전합니다!' WHERE entry=-1578025; /* content: %s begins to cast Empowered Arcane Explosion! */
UPDATE script_texts SET content_loc1='난 이미 경고했다!' WHERE entry=-1578026; /* content: You were warned! */
UPDATE script_texts SET content_loc1='마력의 눈은 우리 것이다!' WHERE entry=-1578027; /* content: The Oculus is ours! */
UPDATE script_texts SET content_loc1='그들은... 너무 강하다! 그들의 힘을... 과소평가했어...' WHERE entry=-1578028; /* content: They are... too strong! Underestimated their... fortitude. */
UPDATE script_texts SET content_loc1='%s|1이;가; 하늘빛 고리 경비대장을 부릅니다!' WHERE entry=-1578029; /* content: %s calls an Azure Ring Captain! */
UPDATE script_texts SET content_loc1='%s|1이;가; 하늘 저 멀리 날아갑니다.' WHERE entry=-1578030; /* content: %s flies away. */
UPDATE script_texts SET content_loc1='모두 쓸어 주마.' WHERE entry=-1580001; /* content: I will purge you! */
UPDATE script_texts SET content_loc1='진정한 고통은 이제 시작이다!' WHERE entry=-1580002; /* content: Your pain has only begun! */
UPDATE script_texts SET content_loc1='킬제덴의 이름으로!' WHERE entry=-1580003; /* content: In the name of Kil\'jaeden! */
UPDATE script_texts SET content_loc1='난 이미 경고했다!' WHERE entry=-1580004; /* content: You were warned! */
UPDATE script_texts SET content_loc1='나는 완전히 깨어났다! 모두 없애 주마!' WHERE entry=-1580005; /* content: My awakening is complete! You shall all perish! */
UPDATE script_texts SET content_loc1='어서요! 날 잃어 가고 있어!' WHERE entry=-1580008; /* content: Hurry! There is not much of me left! */
UPDATE script_texts SET content_loc1='크나큰 빚을 졌습니다. 킬제덴을 쓰러뜨린다면, 이 세계 역시 그대들에게 빚을 지는 겁니다.' WHERE entry=-1580009; /* content: I am forever in your debt. Once we have triumphed over Kil\'jaeden, this entire world will be in your debt as well. */
UPDATE script_texts SET content_loc1='네 고통은 내 행복이지!' WHERE entry=-1580012; /* content: Your misery is my delight! */
UPDATE script_texts SET content_loc1='피를 보고야 말겠다.' WHERE entry=-1580013; /* content: I will watch you bleed! */
UPDATE script_texts SET content_loc1='말하지 않았나? 나는 항상 이긴다!' WHERE entry=-1580015; /* content: Haven\'t you heard? I always win! */
UPDATE script_texts SET content_loc1='장난은 이걸로 끝이다!' WHERE entry=-1580016; /* content: I have toyed with you long enough! */
UPDATE script_texts SET content_loc1='불쌍한 도마뱀 같으니! 널 기다리는 건 죽음뿐이다!' WHERE entry=-1580017; /* content: Puny lizard! Death is the only answer you\'ll find here! */
UPDATE script_texts SET content_loc1='이대로 뭉개 주마!' WHERE entry=-1580019; /* content: I will crush you! */
UPDATE script_texts SET content_loc1='꽤 괜찮았다만, 이 정도론 아직 멀었다!' WHERE entry=-1580020; /* content: That was fun, but I still await a true challenge! */
UPDATE script_texts SET content_loc1='버러지 같은 놈들!' WHERE entry=-1580023; /* content: Perish, insect! */
UPDATE script_texts SET content_loc1='넌 내 밥이다!' WHERE entry=-1580024; /* content: You are meat! */
UPDATE script_texts SET content_loc1='별거 아니군!' WHERE entry=-1580025; /* content: Too easy! */
UPDATE script_texts SET content_loc1='자, 어서 덤벼 봐라!' WHERE entry=-1580026; /* content: Bring the fight to me! */
UPDATE script_texts SET content_loc1='으하하! 영광스러운 전투를 위하여!' WHERE entry=-1580027; /* content: Another day, another glorious battle! */
UPDATE script_texts SET content_loc1='난 싸우기 위해 산다!' WHERE entry=-1580028; /* content: I live for this! */
UPDATE script_texts SET content_loc1='멈추세요, 영웅들이여! 이 악마를 처치하기 전에 알아내야 할 게 있습니다!' WHERE entry=-1580031; /* content: Hold, friends! There is information to be had before this devil meets his fate! */
UPDATE script_texts SET content_loc1='악마여, 안비나는 어디 있나? 칼렉은 어떻게 된 거지?' WHERE entry=-1580032; /* content: Where is Anveena, demon? What has become of Kalec? */
UPDATE script_texts SET content_loc1='그들이 어디 있는지 말해라!' WHERE entry=-1580033; /* content: You will tell me where they are! */
UPDATE script_texts SET content_loc1='킬제덴 님께 영광을! 맞서는 자에게 죽음을!' WHERE entry=-1580036; /* content: Glory to Kil\'jaeden! Death to all who oppose! */
UPDATE script_texts SET content_loc1='주인님을 위하여!' WHERE entry=-1580037; /* content: I kill for the master! */
UPDATE script_texts SET content_loc1='혼돈...' WHERE entry=-1580049; /* content: Chaos... */
UPDATE script_texts SET content_loc1='어둠이 불꽃을 지피리라!' WHERE entry=-1580052; /* content: Shadow to the aid of fire! */
UPDATE script_texts SET content_loc1='알리테스! 네 불꽃은 내 안에 살아 있어!' WHERE entry=-1580053; /* content: Alythess! Your fire burns within me! */
UPDATE script_texts SET content_loc1='이낙 크레율!' WHERE entry=-1580055; /* content: Ee-nok Kryul! */
UPDATE script_texts SET content_loc1='내가... 희미해진다.' WHERE entry=-1580056; /* content: I... fade. */
UPDATE script_texts SET content_loc1='너희에게 시간이란 사치품이다!' WHERE entry=-1580057; /* content: Time is a luxury you no longer possess! */
UPDATE script_texts SET content_loc1='불꽃이 어둠을 돋우리라!' WHERE entry=-1580058; /* content: Fire to the aid of shadow! */
UPDATE script_texts SET content_loc1='사크로래쉬!' WHERE entry=-1580059; /* content: Sacrolash! */
UPDATE script_texts SET content_loc1='네 운은 이제 다했다!' WHERE entry=-1580063; /* content: Your luck has run its course! */
UPDATE script_texts SET content_loc1='이 순간을 위해 모든 계획을 세웠다!' WHERE entry=-1580064; /* content: All my plans have led to this! */
UPDATE script_texts SET content_loc1='나는 충분히 기다렸다!' WHERE entry=-1580066; /* content: I have waited long enough! */
UPDATE script_texts SET content_loc1='나를 실망시키면 영원히 고통받으리라!' WHERE entry=-1580067; /* content: Fail me and suffer for eternity! */
UPDATE script_texts SET content_loc1='저 소녀를 빨아들여라! 빈 껍데기만 남을 때까지 힘을 모조리 빼앗아!' WHERE entry=-1580068; /* content: Drain the girl! Drain her power until there is nothing but a vacant shell! */
UPDATE script_texts SET content_loc1='혼돈!' WHERE entry=-1580074; /* content: Chaos! */
UPDATE script_texts SET content_loc1='파괴!' WHERE entry=-1580075; /* content: Destruction! */
UPDATE script_texts SET content_loc1='망각!' WHERE entry=-1580076; /* content: Oblivion! */
UPDATE script_texts SET content_loc1='나를 부정할 수는 없다! 이 세계는 멸망하리라!' WHERE entry=-1580077; /* content: I will not be denied! This world shall fall! */
UPDATE script_texts SET content_loc1='으아! 태양샘의 마력이... 나를... 거부한다! 무슨 짓을 한 거지? 무슨 짓을 한 거냐???' WHERE entry=-1580079; /* content: Aggghh! The powers of the Sunwell... turn... against me! What have you done? What have you done??? */
UPDATE script_texts SET content_loc1='난 이제 주인님만을 섬겨요.' WHERE entry=-1580082; /* content: I serve only the Master now. */
UPDATE script_texts SET content_loc1='하지만 난... 잊어버렸어요. 어떻게 돌아가야 하는지 모르겠어요.' WHERE entry=-1580084; /* content: But I\'m... lost. I cannot find my way back. */
UPDATE script_texts SET content_loc1='칼렉... 당신이에요?' WHERE entry=-1580086; /* content: Kalec... Kalec? */
UPDATE script_texts SET content_loc1='다른 수정구에 힘을 불어넣었습니다! 어서요!' WHERE entry=-1580092; /* content: I have empowered another orb! Use it quickly! */
UPDATE script_texts SET content_loc1='다른 수정구가 준비됐습니다! 서두르세요!' WHERE entry=-1580093; /* content: Another orb is ready! Make haste! */
UPDATE script_texts SET content_loc1='모든 힘을 수정구에 실었습니다! 이제 그대들의 몫입니다!' WHERE entry=-1580094; /* content: I have channeled all I can! The power is in your hands! */
UPDATE script_texts SET content_loc1='필멸의 영웅들이여, 오늘 이 승리는 오래전에 예견되었다네. 내 형제가 고통스럽게 내뱉은 패배의 울부짖음은 온 우주에 울려 퍼질 걸세. 그리고 불타는 성전에 맞서 싸우는 이들에게 새로운 희망을 전파하겠지.' WHERE entry=-1580095; /* content: Mortal heroes - your victory here today was foretold long ago. My brother\'s anguished cry of defeat will echo across the universe - bringing renewed hope to all those who still stand against the Burning Crusade. */
UPDATE script_texts SET content_loc1='그대들이 쓰러뜨려야만 했던 엔트로피우스는 한때는 고귀한 나루, 므우루였다네. 생전에 므우루는 막대한 빛과 희망을 뿜는 존재였지. 한동안, 눈먼 이들이 그 힘을 훔치려고 했다네...' WHERE entry=-1580098; /* content: The creature Entropius, whom you were forced to destroy, was once the noble naaru, M\'uru. In life, M\'uru channeled vast energies of LIGHT and HOPE. For a time, a misguided few sought to steal those energies... */
UPDATE script_texts SET content_loc1='저희의 불손함은 용서받을 길이 없습니다. 가장 고귀한 존재 중 하나를 더럽혔으니까요. 이 죄는 씻을 수가 없을 겁니다.' WHERE entry=-1580099; /* content: Our arrogance was unpardonable. We damned one of the most noble beings of all. We may never atone for this sin. */
UPDATE script_texts SET content_loc1='축복받은 조상님들이여! 느껴집니다... 크나큰 사랑이... 넘치는 은총이! 도저히... 말로 표현할 수가 없어요...' WHERE entry=-1580104; /* content: Blessed ancestors! I feel it... so much love... so much grace... there are... no words... impossible to describe... */
UPDATE script_texts SET content_loc1='구원일세, 젊은이. 우리 모두를 감싸 안는...' WHERE entry=-1580105; /* content: Salvation, young one. It waits for us all. */
UPDATE script_texts SET content_loc1='%s|1이;가; 숨을 깊게 들이마십니다.' WHERE entry=-1580107; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc1='놀아줄 시간이 없다!' WHERE entry=-1585000; /* content: You only waste my time! */
UPDATE script_texts SET content_loc1='힘을 원해! 끝없는 힘을!' WHERE entry=-1585001; /* content: My hunger knows no bounds! */
UPDATE script_texts SET content_loc1='흥, 기분 전환이 됐군.' WHERE entry=-1585003; /* content: Enough distractions! */
UPDATE script_texts SET content_loc1='나는 무적이다!' WHERE entry=-1585004; /* content: I am invincible! */
UPDATE script_texts SET content_loc1='억제할 수... 없다...' WHERE entry=-1585008; /* content: Un...con...tainable. */
UPDATE script_texts SET content_loc1='삼켰... 다...' WHERE entry=-1585010; /* content: Con...sume. */
UPDATE script_texts SET content_loc1='이젠 화가 나는데.' WHERE entry=-1585015; /* content: Now I\'m getting annoyed. */
UPDATE script_texts SET content_loc1='시작이 좋은걸.' WHERE entry=-1585017; /* content: I call that a good start. */
UPDATE script_texts SET content_loc1='혼자란 외로운 법이지.' WHERE entry=-1585020; /* content: One is such a lonely number. */
UPDATE script_texts SET content_loc1='아, 너무 시시했어.' WHERE entry=-1585021; /* content: It\'s been a kick, really. */
UPDATE script_texts SET content_loc1='복수여, 타올라라!' WHERE entry=-1585024; /* content: Vengeance burns! */
UPDATE script_texts SET content_loc1='로데론의 모든 병사는 즉시 스트라솔름 입구로 집결하라. 그리고 아서스 왕자님의 명령을 기다려라.' WHERE entry=-1595000; /* content: All soldiers of Lordaeron should immediately report to the entrance of Stratholme, and await further orders from Prince Arthas. */
UPDATE script_texts SET content_loc1='상자를 잘 처리하셨어요! 다음 임무를 알려 드릴 테니 스트라솔름 입구로 와서 제게 말을 거세요!' WHERE entry=-1595001; /* content: Good work with the crates! Come talk to me in front of Stratholme for your next assignment! */
UPDATE script_texts SET content_loc1='스컬지 군대가 축제의 거리 관문 근처에서 목격되었습니다!' WHERE entry=-1595003; /* content: Scourge forces have been spotted near the Festival Lane Gate! */
UPDATE script_texts SET content_loc1='스컬지 군대가 왕의 광장 분수대 근처에서 목격되었습니다!' WHERE entry=-1595004; /* content: Scourge forces have been spotted near the King\'s Square fountain! */
UPDATE script_texts SET content_loc1='스컬지 군대가 상가 관문 근처에서 목격되었습니다!' WHERE entry=-1595005; /* content: Scourge forces have been spotted near the Market Row Gate! */
UPDATE script_texts SET content_loc1='스컬지 군대가 마을회관 근처에서 목격되었습니다!' WHERE entry=-1595006; /* content: Scourge forces have been spotted near the Town Hall! */
UPDATE script_texts SET content_loc1='스컬지 군대가 장로의 광장 관문 근처에서 목격되었습니다!' WHERE entry=-1595007; /* content: Scourge forces have been spotted near the Elder\'s Square Gate! */
UPDATE script_texts SET content_loc1='용사들이여, 당장 마을회관으로 집결해라. 말가니스를 상대해야 한다.' WHERE entry=-1595008; /* content: Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis. */
UPDATE script_texts SET content_loc1='나를 따르라. 내가 인도하겠다.' WHERE entry=-1595009; /* content: Follow me, I know the way through. */
UPDATE script_texts SET content_loc1='아아, 마침내 오셨군요, 아서스 왕자님. 딱 적절한 때에 와주셔서 기쁩니다.' WHERE entry=-1595010; /* content: Ah, you\'ve finally arrived Prince Arthas. You\'re here just in the nick of time. */
UPDATE script_texts SET content_loc1='그래. 나도 자네를 구할 수 있어 기쁘네.' WHERE entry=-1595011; /* content: Yes, I\'m glad I could get to you before the plague. */
UPDATE script_texts SET content_loc1='이건 무슨 조화지?' WHERE entry=-1595012; /* content: What is this sorcery? */
UPDATE script_texts SET content_loc1='이해하려고 애쓸 것 없다, 아서스. 너는 그저 죽어주면 그만이야.' WHERE entry=-1595013; /* content: There\'s no need for you to understand, Arthas. All you need to do is die. */
UPDATE script_texts SET content_loc1='이건 스컬지가 아닌데... 대체 무슨 수작이지? 어쨌든 서두르자!' WHERE entry=-1595014; /* content: Mal\'Ganis appears to have more than Scourge in his arsenal. We should make haste. */
UPDATE script_texts SET content_loc1='사악한 놈들이 더 있군! 전투를 준비해라!' WHERE entry=-1595015; /* content: More vile sorcery! Be ready for anything! */
UPDATE script_texts SET content_loc1='자, 가요.' WHERE entry=-1595016; /* content: Let\'s move on. */
UPDATE script_texts SET content_loc1='뒤를 조심해라. 놈들은 우릴 포위하고 있어.' WHERE entry=-1595017; /* content: Watch your backs: they have us surrounded in this hall. */
UPDATE script_texts SET content_loc1='말가니스가 잔꾀를 부리는군.' WHERE entry=-1595018; /* content: Mal\'Ganis is not making this easy. */
UPDATE script_texts SET content_loc1='바퀴벌레 같은 놈들...' WHERE entry=-1595019; /* content: They\'re very persistent. */
UPDATE script_texts SET content_loc1='이젠 어떻게 방해할 작정이지?' WHERE entry=-1595020; /* content: What else can he put in my way? */
UPDATE script_texts SET content_loc1='아서스 메네실 왕자, 오늘 엄청난 암흑이 네 영혼을 사로잡았다. 네가 다른 이에게 안겨주려고 했던 죽음은, 오늘 널 찾아올 것이다.' WHERE entry=-1595021; /* content: Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own. */
UPDATE script_texts SET content_loc1='난 로데론을 지킬 뿐이다. 네가 무슨 짓을 해도 날 막을 수는 없어.' WHERE entry=-1595022; /* content: I do what I must for Lordaeron, and neither your words nor your actions will stop me. */
UPDATE script_texts SET content_loc1='저 책장 뒤에, 말가니스에게 가는 지름길이 있다.' WHERE entry=-1595023; /* content: The quickest path to Mal\'Ganis lies behind that bookshelf ahead. */
UPDATE script_texts SET content_loc1='오래 걸리진 않을 것이다.' WHERE entry=-1595024; /* content: This will only take a moment. */
UPDATE script_texts SET content_loc1='비밀 통로가 남아 있어 다행이군.' WHERE entry=-1595025; /* content: I\'m relieved this secret passage still works. */
UPDATE script_texts SET content_loc1='되도록 빨리 빠져나가야겠군. 언데드에게 죽지 않아도 불에 타 죽게 생겼어.' WHERE entry=-1595026; /* content: Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might. */
UPDATE script_texts SET content_loc1='잠시 쉬며 숨을 돌려라. 하지만 곧 이동해야 한다.' WHERE entry=-1595027; /* content: Rest a moment and clear your lungs, but we must move again soon. */
UPDATE script_texts SET content_loc1='그만! 이제 움직이자. 말가니스가 기다리고 있다.' WHERE entry=-1595028; /* content: That\'s enough; we must move again. Mal\'Ganis awaits. */
UPDATE script_texts SET content_loc1='그나마 낫군. 상가에는 아직 불길이 번지지 않았다. 말가니스는 저 너머 십자군 광장에 있을 것이다. 이동할 준비가 되면 내게 보고해라.' WHERE entry=-1595029; /* content: At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward. */
UPDATE script_texts SET content_loc1='정의는 실현될 것이다.' WHERE entry=-1595030; /* content: Justice will be done. */
UPDATE script_texts SET content_loc1='이제 결판을 낼 때다, 말가니스! 너와, 내가 말이다...' WHERE entry=-1595031; /* content: We\'re going to finish this right now, Mal\'Ganis. Just you... and me. */
UPDATE script_texts SET content_loc1='너의 여정은 이제 막 시작이다, 젊은 왕자여. 병사들을 모아 극한의 땅, 노스렌드로 나를 찾아와라. 그곳에서 모든 일이 결판날 것이다. 네 진정한 운명도 그곳에서 시작되지.' WHERE entry=-1595032; /* content: Your journey has just begun, young prince. Gather your forces and meet me in the arctic land of Northrend. It is there that we shall settle the score between us. It is there that your true destiny will unfold. */
UPDATE script_texts SET content_loc1='세상 끝까지라도 널 쫓아갈 것이다! 들리나? 끝까지 말이다!' WHERE entry=-1595033; /* content: I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth! */
UPDATE script_texts SET content_loc1='오늘 큰 공을 세웠군. 말가니스가 남긴 건 모두 너희 것이다. 보상으로 받아 둬라. 난 이제 노스렌드 원정 준비를 시작해야겠다.' WHERE entry=-1595034; /* content: You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend. */
UPDATE script_texts SET content_loc1='이리 와라, 로데론의 병사여! 도움이 필요하다!' WHERE entry=-1595035; /* content: Protect your prince, soldiers of Lordaeron! I am in need of aid! */
UPDATE script_texts SET content_loc1='말가니스는 이 대가를 치를 것이다.' WHERE entry=-1595037; /* content: Mal\'Ganis will pay for this. */
UPDATE script_texts SET content_loc1='너를 살려 둘 수는 없다.' WHERE entry=-1595038; /* content: I can\'t afford to spare you. */
UPDATE script_texts SET content_loc1='장애물이 하나 줄었군.' WHERE entry=-1595039; /* content: One less obstacle to deal with. */
UPDATE script_texts SET content_loc1='살덩이는 무쇠에 상대가 안 되지!' WHERE entry=-1599002; /* content: Flesh is no match for iron! */
UPDATE script_texts SET content_loc1='무쇠의 군대가 세계를 뒤덮을 거다!' WHERE entry=-1599003; /* content: Armies of iron will smother the world! */
UPDATE script_texts SET content_loc1='왜 이렇게 된 거죠?' WHERE entry=-1599006; /* content: Why must it be this way? */
UPDATE script_texts SET content_loc1='넌 당해도 싸다!' WHERE entry=-1599007; /* content: You had it coming! */
UPDATE script_texts SET content_loc1='시간을 낭비했군요... 돌이킬 수 없는 시간을!' WHERE entry=-1599010; /* content: So much lost time... that you\'ll never get back! */
UPDATE script_texts SET content_loc1='무의미한 희생이 아니길 빕니다!' WHERE entry=-1599019; /* content: I hope this is all worth it! */
UPDATE script_texts SET content_loc1='해답을 찾을 시간입니다! 이제 가보자고요!' WHERE entry=-1599020; /* content: Time to get some answers! Let\'s get this show on the road! */
UPDATE script_texts SET content_loc1='마도란이시여! 저건 또 뭐야? 오! 잠깐! 알 것 같은데...' WHERE entry=-1599023; /* content: What in the name o\' Madoran did THAT do? Oh! Wait: I just about got it... */
UPDATE script_texts SET content_loc1='이제 잘 보시라고요! 제가 눈 깜짝할 사이에 정보를...' WHERE entry=-1599026; /* content: Now keep an eye out! I\'ll have this licked in two shakes of a-- */
UPDATE script_texts SET content_loc1='아이고, 좋은 소리 같진 않네요. 에... 문제가 한두 개 생길지도...' WHERE entry=-1599028; /* content: Oh, that doesn\'t sound good. We might have a complication or two... */
UPDATE script_texts SET content_loc1='허허, 해보겠다는 거냐, 엉? 이쪽도 방법이 있지!' WHERE entry=-1599030; /* content: Ah, you want to play hardball, eh? That\'s just my game! */
UPDATE script_texts SET content_loc1='조금만 있으면 제가... 어, 어라?' WHERE entry=-1599031; /* content: Couple more minutes and I\'ll-- */
UPDATE script_texts SET content_loc1='상승했다고? 좀 좋은 소식은 없나?' WHERE entry=-1599033; /* content: Heightened? What\'s the good news? */
UPDATE script_texts SET content_loc1='그래, 이게 문제였군! 이제 방법을 알았으니...' WHERE entry=-1599034; /* content: So that was the problem? Now I\'m makin\' progress... */
UPDATE script_texts SET content_loc1='위협 지수가 높습니다. 공허 분석을 종료합니다. 안전성 검증을 시작합니다.' WHERE entry=-1599035; /* content: Critical threat index. Void analysis diverted. Initiating sanitization protocol. */
UPDATE script_texts SET content_loc1='잠깐! 내가 권한을 쥐고 있는데, 검증 따위 받을 것 같으냐!' WHERE entry=-1599036; /* content: Hang on! Nobody\'s gonna\' be sanitized as long as I have a say in it! */
UPDATE script_texts SET content_loc1='무슨 수호자?' WHERE entry=-1599052; /* content: What protectors? */
UPDATE script_texts SET content_loc1='잠깐, 잠깐! 에시르와 바니르가 전쟁을 했다고? 왜?' WHERE entry=-1599056; /* content: Hold everything! The Aesir and Vanir went to war? Why? */
UPDATE script_texts SET content_loc1='그렇다고 할 수 있습니다.' WHERE entry=-1599059; /* content: Essentially that is correct. */
UPDATE script_texts SET content_loc1='로켄?! 이거 정말 귀찮아졌군... 무쇠 드워프는 무찌른 것 같습니다만, 아직 저 어딘가에 무쇠 브리쿨을 대량으로 뽑아내는 기계가 있나 봅니다!' WHERE entry=-1599062; /* content: Loken?! That\'s downright bothersome... We might\'ve neutralized the iron dwarves, but I\'d lay odds there\'s another machine somewhere else churnin\' out a whole mess o\' these iron vrykul! */
UPDATE script_texts SET content_loc1='입구 근처의 문 뒤에 무엇이 숨어 있는지 확인해봐야겠습니다. 조용히 그쪽으로 이동할게요. 문 앞에서 만나서 같이 들어갑시다.' WHERE entry=-1599064; /* content: I think it\'s time to see what\'s behind the door near the entrance. I\'m going to sneak over there, nice and quiet. Meet me at the door and I\'ll get us in. */
UPDATE script_texts SET content_loc1='너무 배고파! 밥 줘!' WHERE entry=-1600001; /* content: So hungry! Must feed! */
UPDATE script_texts SET content_loc1='터져라, 뻥!' WHERE entry=-1600003; /* content: Corpse go boom! */
UPDATE script_texts SET content_loc1='이래 봤자... 아무 소용없다.' WHERE entry=-1600006; /* content: Your efforts... are in vain. */
UPDATE script_texts SET content_loc1='와서 나를 보호해라! 어서! 망할 놈들아!' WHERE entry=-1600008; /* content: Bolster my defenses! Hurry, curse you! */
UPDATE script_texts SET content_loc1='부질없는 짓인 줄 잘 알고 있겠지!' WHERE entry=-1600009; /* content: Surely you can see the futility of it all! */
UPDATE script_texts SET content_loc1='그냥 단념하고 목숨을 바쳐라!' WHERE entry=-1600010; /* content: Just give up and die already! */
UPDATE script_texts SET content_loc1='%s|1이;가; 도움을 요청합니다.' WHERE entry=-1600011; /* content: %s calls for assistance. */
UPDATE script_texts SET content_loc1='타론자는 모든 걸 안다! 필멸자 따위가 불멸의 제국을 무너뜨릴 수는 없어!' WHERE entry=-1600012; /* content: Tharon\'ja sees all! The work of mortals shall not end the eternal dynasty! */
UPDATE script_texts SET content_loc1='네 육신은 이제 타론자 거다!' WHERE entry=-1600015; /* content: Your flesh serves Tharon\'ja now! */
UPDATE script_texts SET content_loc1='연약한 껍데기도 써먹을 데가 있지!' WHERE entry=-1600016; /* content: Tharon\'ja has a use for your mortal shell! */
UPDATE script_texts SET content_loc1='안 돼! 너무... 너무 짧았어!' WHERE entry=-1600017; /* content: No! A taste... all too brief! */
UPDATE script_texts SET content_loc1='감히 여기엘 오다니...' WHERE entry=-1601002; /* content: You were foolish to come. */
UPDATE script_texts SET content_loc1='아눕아락 님의 뜻이다!' WHERE entry=-1601003; /* content: As Anub\'Arak commands! */
UPDATE script_texts SET content_loc1='문을 보호해야 한다. 공격! 모두 박살을 내버려라!' WHERE entry=-1601005; /* content: We must hold the gate. Attack! Tear them limb from limb! */
UPDATE script_texts SET content_loc1='어떤 수를 써서라도 문을 지켜야 한다! 가서 놈들을 쓸어버려!' WHERE entry=-1601006; /* content: The gate must be protected at all costs. Rip them to shreds! */
UPDATE script_texts SET content_loc1='발소리가 들리는데... 경계 태세를 취해라.' WHERE entry=-1601008; /* content: I hear footsteps. Be on your guard. */
UPDATE script_texts SET content_loc1='산 자가 왔나 보군. 준비해라.' WHERE entry=-1601009; /* content: I sense the living. Be ready. */
UPDATE script_texts SET content_loc1='%s|1이;가; 동굴 위로 올라갑니다!' WHERE entry=-1601013; /* content: %s moves up the tunnel! */
UPDATE script_texts SET content_loc1='영원한 고통이 너희를 기다린다!' WHERE entry=-1601015; /* content: Eternal agony awaits you! */
UPDATE script_texts SET content_loc1='네가 자초한 일이다.' WHERE entry=-1601017; /* content: You have made your choice. */
UPDATE script_texts SET content_loc1='아움 날락크, 쉬.' WHERE entry=-1601020; /* content: Auum na-l ak-k-k-k, isshhh. */
UPDATE script_texts SET content_loc1='메뚜기 떼에는 갑옷도 쓸모없다!' WHERE entry=-1601021; /* content: Your armor is useless against my locusts! */
UPDATE script_texts SET content_loc1='우우낙 히티쉬 틱크크!' WHERE entry=-1601023; /* content: Uunak-hissss tik-k-k-k-k! */
UPDATE script_texts SET content_loc1='문이 파괴됐다! 서둘러라! 부대를 편성해, 침입자를 막아라!' WHERE entry=-1601025; /* content: The gate has been breached! Quickly, divert forces to deal with these invaders! */
UPDATE script_texts SET content_loc1='이제 시간이 없다! 남은 병력은 모두 침입자를 공격하라!' WHERE entry=-1601026; /* content: There\'s no time left! All remaining forces, attack the invaders! */
UPDATE script_texts SET content_loc1='우리 아버지 자식 중에 내가 제일 세! 넌 이제 큰일 났다!' WHERE entry=-1602000; /* content: I am the greatest of my father\'s sons! Your end has come! */
UPDATE script_texts SET content_loc1='살덩이 따위는 약해!' WHERE entry=-1602002; /* content: Flesh... is... weak! */
UPDATE script_texts SET content_loc1='...' WHERE entry=-1602003; /* content: ... */
UPDATE script_texts SET content_loc1='뭔가 잘못됐어... 살덩이 따위가... 강할 리가...' WHERE entry=-1602004; /* content: How can it be...? Flesh is not... stronger! */
UPDATE script_texts SET content_loc1='막아 봐라! 그럴 수 있다면 말이다!' WHERE entry=-1602005; /* content: Defend yourself, for all the good it will do! */
UPDATE script_texts SET content_loc1='%s|1이;가; 전투 태세로 전환합니다!' WHERE entry=-1602006; /* content: %s switches to Battle Stance! */
UPDATE script_texts SET content_loc1='으아아아! 무쇠와 강철의 분노를 맛보아라!' WHERE entry=-1602007; /* content: GRAAAAAH! Behold the fury of iron and steel! */
UPDATE script_texts SET content_loc1='%s|1이;가; 광폭 태세로 전환합니다!' WHERE entry=-1602008; /* content: %s switches to Berserker Stance! */
UPDATE script_texts SET content_loc1='있는 힘껏 쳐 봐라!' WHERE entry=-1602009; /* content: Give me your worst! */
UPDATE script_texts SET content_loc1='%s|1이;가; 방어 태세로 전환합니다!' WHERE entry=-1602010; /* content: %s switches to Defensive Stance! */
UPDATE script_texts SET content_loc1='주인님... 손님이 왔습니다.' WHERE entry=-1602015; /* content: Master... you have guests. */
UPDATE script_texts SET content_loc1='파멸은 아주 자그만 불꽃에서 시작되지.' WHERE entry=-1602016; /* content: The slightest spark shall be your undoing. */
UPDATE script_texts SET content_loc1='안전한 곳은 없다!' WHERE entry=-1602017; /* content: No one is safe! */
UPDATE script_texts SET content_loc1='희망이 있는 줄 아느냐? 꿈 깨라!' WHERE entry=-1602018; /* content: What hope is there for you? None! */
UPDATE script_texts SET content_loc1='주인님께서 미래를 보여주셨다. 하지만 너희는 거기 없어! 아제로스는 어둠 속에서 다시 태어날 것이다. 요그사론이 풀려날 것이다! 판테온은 무너지리라!' WHERE entry=-1602020; /* content: My master has shown me the future, and you have no place in it. Azeroth will be reborn in darkness. Yogg-Saron shall be released! The Pantheon shall fall! */
UPDATE script_texts SET content_loc1='미약한 존재여...' WHERE entry=-1602021; /* content: Only mortal... */
