UPDATE script_texts SET content_loc1='어둠의 주인님을 받들어. 리치 왕을 위하여. 너희에게. 죽음을. 안기리라.' WHERE entry=-1649056; /* content: In the name of our dark master. For the Lich King. You. Will. Die. */
UPDATE script_texts SET content_loc1='너흰 모두 끝장이야!' WHERE entry=-1649057; /* content: You are finished! */
