UPDATE script_texts SET content_loc1='스컬지를 막을 수는 없다...' WHERE entry=-1649059; /* content: The Scourge cannot be stopped... */
UPDATE script_texts SET content_loc1='하찮은 것!' WHERE entry=-1649061; /* content: Unworthy! */
