UPDATE script_texts SET content_loc1='여기가 네 무덤이 되리라!' WHERE entry=-1649064; /* content: This place will serve as your tomb! */
UPDATE script_texts SET content_loc1='프을라크 쉬얼!' WHERE entry=-1649065; /* content: F-lakkh shir! */
