UPDATE locales_npc_text SET Text0_0_loc8='Похоже, твое сердце разбито… Приходи, когда оправишься от этого потрясения.',Text0_1_loc8='Похоже, твое сердце разбито… Приходи, когда оправишься от этого потрясения.' WHERE entry=8283; /* Text0_0: You look like you\'ve had your heart broken. Come back when you\'re not so sad. */ /* Text0_1: You look like you\'ve had your heart broken. Come back when you\'re not so sad. */
