UPDATE locales_npc_text SET Text0_0_loc8='Я с тобой поговорю, если от тебя будет пахнуть получше. Например, одеколоном.',Text0_1_loc8='Я с тобой поговорю, если от тебя будет пахнуть получше. Например, одеколоном.' WHERE entry=8287; /* Text0_0: I\'d talk to you if you smelled better. Cologne would be nice. */ /* Text0_1: I\'d talk to you if you smelled better. Cologne would be nice. */
UPDATE locales_npc_text SET Text0_0_loc8='Как, у тебя нет для меня знака любви? Ты что же, не любишь меня?',Text0_1_loc8='Как, у тебя нет для меня знака любви? Ты что же, не любишь меня?' WHERE entry=8288; /* Text0_0: What, you don\'t have a token to give me? Don\'t you love me? */ /* Text0_1: What, you don\'t have a token to give me? Don\'t you love me? */
