UPDATE locales_npc_text SET Text0_0_loc8='Чем могу помочь, |3-6($C)?',Text0_1_loc8='Чем могу помочь, |3-6($C)?' WHERE entry=12541; /* Text0_0: How may I help you, $c? */ /* Text0_1: How may I help you, $c? */
UPDATE locales_npc_text SET Text0_0_loc8='Я могу лишить тебя всех талантов, чтобы затем ты $gсмог:смогла; избрать новые.$b$bНо помни: с каждым разом это будет обходиться все дороже.',Text0_1_loc8='Я могу лишить тебя всех талантов, чтобы затем ты $gсмог:смогла; избрать новые.$b$bНо помни: с каждым разом это будет обходиться все дороже.' WHERE entry=12542; /* Text0_0: I can erase all your talents so that you may choose new ones.$b$bThis becomes more expensive each time you do it. */ /* Text0_1: I can erase all your talents so that you may choose new ones.$b$bThis becomes more expensive each time you do it. */
