UPDATE locales_npc_text SET Text0_0_loc8='Яблочкина вместе с его тележкой с фруктами часто можно встретить в юго-западной части Даларана, неподалеку от Серебряного Анклава.',Text0_1_loc8='Яблочкина вместе с его тележкой с фруктами часто можно встретить в юго-западной части Даларана, неподалеку от Серебряного Анклава.' WHERE entry=14106; /* Text0_0: Applebough\'s fruit cart is usually in southwest Dalaran, near the Silver Enclave. */ /* Text0_1: Applebough\'s fruit cart is usually in southwest Dalaran, near the Silver Enclave. */
