UPDATE locales_npc_text SET Text0_0_loc8='Приветствую тебя, герой. Я продаю изготовленные мной для жрецов доспехи послушника из Багрового Легиона.',Text0_1_loc8='Приветствую тебя, герой. Я продаю изготовленные мной для жрецов доспехи послушника из Багрового Легиона.' WHERE entry=15452; /* Text0_0: Greetings, hero. I craft and sell Crimson Acolyte armor for priests.$B */ /* Text0_1: Greetings, hero. I craft and sell Crimson Acolyte armor for priests.$B */
