UPDATE locales_npc_text SET Text0_0_loc8='Сделано лучшими мастерами всего Тельдрассила, на руках принесено из Калимдора в Азерот – в знак крепнущего союза между народом Великого Древа и здешними людьми. Если тебе чего-то нужно – только спроси.',Text0_1_loc8='Сделано лучшими мастерами всего Тельдрассила, на руках принесено из Калимдора в Азерот – в знак крепнущего союза между народом Великого Древа и здешними людьми. Если тебе чего-то нужно – только спроси.' WHERE entry=1233;
