UPDATE locales_npc_text SET Text2_0_loc8='Пирог вышел отличный, хотите?',Text2_1_loc8='Пирог вышел отличный, хотите?',Text3_0_loc8='У вас неудачный день? Попробуйте вишневого пирога!',Text3_1_loc8='У вас неудачный день? Попробуйте вишневого пирога!' WHERE entry=2355;
UPDATE locales_npc_text SET Text4_0_loc8='Этот вишневый пирог так вкусен, что даже взрослый гном бы прослезился.',Text4_1_loc8='Этот вишневый пирог так вкусен, что даже взрослый гном бы прослезился.',Text5_0_loc8='Попробуйте этого вишневого пирога – вы сразу улыбнетесь, вот увидите!',Text5_1_loc8='Попробуйте этого вишневого пирога – вы сразу улыбнетесь, вот увидите!' WHERE entry=2355;
