UPDATE locales_npc_text SET Text0_0_loc8='Армана Кромвеля в Квартале Магов сложно не заметить. Он всегда стоит на причале.',Text0_1_loc8='Армана Кромвеля в Квартале Магов сложно не заметить. Он всегда стоит на причале.',Text1_0_loc8='Арман Кромвель обычно стоит на причале в Квартале Магов. Он ловит очень вкусную рыбу. Попробуй!',Text1_1_loc8='Арман Кромвель обычно стоит на причале в Квартале Магов. Он ловит очень вкусную рыбу. Попробуй!' WHERE entry=3534;
UPDATE locales_npc_text SET Text0_0_loc8='Тебе нужна Марта Альестар. Она во внутреннем круге Района Фармацевтов.',Text0_1_loc8='Тебе нужна Марта Альестар. Она во внутреннем круге Района Фармацевтов.',Text1_0_loc8='Тебе нужно пойти в Район Фармацевтов на внутреннем кольце. Там Марта Альестар научит тебя собирать цветы.',Text1_1_loc8='Тебе нужно пойти в Район Фармацевтов на внутреннем кольце. Там Марта Альестар научит тебя собирать цветы.' WHERE entry=3535;
