UPDATE locales_npc_text SET Text0_0_loc8='Будь $Gосторожен, незнакомец:осторожна, незнакомка;. В нашем городе не терпят еретиков.',Text0_1_loc8='Будь $Gосторожен, незнакомец:осторожна, незнакомка;. В нашем городе не терпят еретиков.',Text1_0_loc8='Я за тобой слежу, |3-6($R).' WHERE entry=4119;
