UPDATE locales_npc_text SET Text0_0_loc8='Мое знание – твое знание, моя мудрость – твоя мудрость. Чем могу служить, $N?',Text0_1_loc8='Мое знание – твое знание, моя мудрость – твоя мудрость. Чем могу служить, $N?' WHERE entry=4438;
