UPDATE locales_npc_text SET Text0_0_loc8='Я слышала, что у Денеба Уокера, укрывшегося в Стромгарде, есть книга, которая, бесспорно, поможет вам улучшить вашу технику оказания первой помощи.',Text0_1_loc8='Я слышала, что у Денеба Уокера, укрывшегося в Стромгарде, есть книга, которая, бесспорно, поможет вам улучшить вашу технику оказания первой помощи.' WHERE entry=7027;
