UPDATE locales_npc_text SET Text0_0_loc8='<Нарайн прикладывает нераспечатанный конверт ко лбу.>$B$BЧто это… Мммм… Бархат! $B$BЯ прав?$B$BЕсли ты здесь, чтобы научиться новой профессии, воспользуйся вон той книгой… $B$B<Нарайн указывает на "Прорицание для чайников".>',Text1_0_loc8='Молчите! Шшш! Вы здесь из-за Испепелителя?! Да… да… Нет! Виноват. $B$BЧем я могу помочь вам, |3-6($R)? $B$BЕсли вы здесь, чтобы попробовать вновь выучить навыки, используйте вон ту книгу… $B$B*Нарайн указывает на "Прорицание для чайников"*.' WHERE entry=7902;
