UPDATE locales_npc_text SET Text0_0_loc8='Ты хочешь узнать насчет продовольствия? Ладно. По последним данным запасы Орды таковы: постные стейки из волчатины – $2095w из $2096w, пятнистый желтохвост – $2102w из $2103w, запеченный лосось – $2105w из $2106w.' WHERE entry=7948;
UPDATE locales_npc_text SET Text0_0_loc8='Приветствую вас, |3-6($R). Я – командир Мощный Молот, полномочный представитель Альянса. Если у вас, $Gмолодой человек:девушка;, есть вопросы об успехах Альянса в подготовке к войне, не стесняйтесь их задавать.' WHERE entry=7949;
UPDATE locales_npc_text SET Text0_0_loc8='Отличный вопрос, |3-6($C). Последнее, что я слышал, – это то, что Альянсу удалось выплавить $1997w из $1998w медных слитков, $2002w из $2003w слитков стали и $2011w из $2012w слитков тория, нужных для фронта' WHERE entry=7951;
