UPDATE locales_npc_text SET Text0_0_loc8='Первый этап нашей борьбы против Плети прошел хорошо. Мы добились некоторых успехов. Надеюсь, моя помощь приведет тебя к дальнейшим победам.',Text0_1_loc8='Первый этап нашей борьбы против Плети прошел хорошо. Мы добились некоторых успехов. Надеюсь, моя помощь приведет тебя к дальнейшим победам.' WHERE entry=8527;
