UPDATE locales_npc_text SET Text0_0_loc8='Вы разыграете серию сцен, которые заставят орков бастиона Вечной Скорби поверить, что на их базу напали огры клана Боевого Молота, и устроите другую серию событий, которая убедит огров клана Боевого Молота в том, что орки бастиона Вечной Скорби напали на их базу.$B$BСделай так, и ты избавишь нас от обеих проблем.$B$BОгры клана Тяжелого Кулака получат назад свои территории, а наш народ будет защищен от нападений Тяжелого Кулака, Боевого Молота и Совета Теней.' WHERE entry=9369;
