UPDATE locales_npc_text SET Text0_0_loc8='Наша скромная таверна находится на севере Лазурной заставы. Челлана хорошо о тебе позаботится.',Text0_1_loc8='Наша скромная таверна находится на севере Лазурной заставы. Челлана хорошо о тебе позаботится.' WHERE entry=10074;
