UPDATE locales_npc_text SET Text0_0_loc8='Если ищешь мастера магии, тебе нужен Семид. Он дает уроки в восточной части Лазурной заставы вместе с Руадой.',Text0_1_loc8='Если ищешь мастера магии, тебе нужен Семид. Он дает уроки в восточной части Лазурной заставы вместе с Руадой.' WHERE entry=10081;
UPDATE locales_npc_text SET Text0_0_loc8='Почтенная Туллас – благородная женщина и верный защитник Света. Она ведет уроки в приличной гостинице.',Text0_1_loc8='Почтенная Туллас – благородная женщина и верный защитник Света. Она ведет уроки в приличной гостинице.' WHERE entry=10083;
