UPDATE locales_npc_text SET Text0_0_loc8='Насчет оружейного дела – тебе необходимо поговорить с Бульвайфом, живущим в оружейной лавке Лесной Границы в Стальгорне.',Text0_1_loc8='Насчет оружейного дела – тебе необходимо поговорить с Бульвайфом, живущим в оружейной лавке Лесной Границы в Стальгорне.' WHERE entry=10751;
