UPDATE locales_npc_text SET Text0_0_loc8='Веселиться в честь рождения одного из злейших врагов – затея, мягко говоря, странная. Но мы праздники союзников не пропускаем!',Text0_1_loc8='Веселиться в честь рождения одного из злейших врагов – затея, мягко говоря, странная. Но мы праздники союзников не пропускаем!' WHERE entry=11962;
