UPDATE locales_page_text SET text_loc8='Имя: Кобальт$BСтатус: мертв$BПримечание: упал с лестницы, разбился насмерть. Жаль, мог бы еще помучиться.' WHERE entry=3178;
UPDATE locales_page_text SET text_loc8='Имя: Гармония$BСтатус: мертва$BПримечание: имя этого рыцарь смерти упоминается в связи с нарушением правил поведения солдата Плети. Была убита лично Королем-личом.' WHERE entry=3179;
UPDATE locales_page_text SET text_loc8='Имя: Мелт$BСтатус: жив*$BПримечание: обращен в вурдалака.' WHERE entry=3180;
UPDATE locales_page_text SET text_loc8='Имя: Милтон$BСтатус: жив$BПримечание: любит долгие прогулки' WHERE entry=3181;
