UPDATE creature_ai_texts SET content_loc8='Сдерживайте их, сейчас я спущу псов!' WHERE entry=-179; /* content: Hold them back while I release the warhounds! */
UPDATE creature_ai_texts SET content_loc8='Мои псы проголодались, сейчас они сожрут вас!' WHERE entry=-180; /* content: My pets are hungry and they will dine on your flesh! */
