UPDATE creature_ai_texts SET content_loc8='Признайтесь и мы освободим Вас.' WHERE entry=-185; /* content: Confess and we shall set you free. */
UPDATE creature_ai_texts SET content_loc8='Здесь живые!' WHERE entry=-186; /* content: The living are here! */
UPDATE creature_ai_texts SET content_loc8='% вызывает своих воргов!' WHERE entry=-187; /* content: %s calls for his worgs! */
UPDATE creature_ai_texts SET content_loc8='Премерзкий трогг, если это, конечно, он. Умри!' WHERE entry=-188; /* content: A foul trogg if ever I saw one.  Die! */
UPDATE creature_ai_texts SET content_loc8='Слабость туманит мой взор, но я знаю - ты, должно быть, трогг. Умри, злобная тварь!' WHERE entry=-189; /* content: This sickness clouds my vision, but I know you must be a trogg.  Die foul invader! */
