UPDATE creature_ai_texts SET content_loc8='Вы сталкивались во многих схватках, жаль, они все были напрасными. Скоро ваши люди преклонят колени пред моим лордом!' WHERE entry=-192; /* content: It can get really hot here in the Badlands. But at least it\'s a dry heat. */
UPDATE creature_ai_texts SET content_loc8='Вы ничто, я отвечаю более высоким требованиям!' WHERE entry=-193; /* content: Gizmos... check. Doothingies... check. Wizzers... check. */
UPDATE creature_ai_texts SET content_loc8='Тёмный Властелин смеётся над вами!' WHERE entry=-194; /* content: One day I\'ll be a real boy. */
UPDATE creature_ai_texts SET content_loc8='Твоё время выходит!' WHERE entry=-195; /* content: Brzt... click... whir... */
UPDATE creature_ai_texts SET content_loc8='Твои дни сочтены!' WHERE entry=-196; /* content: It is dangerous here, master. But I shall help stand guard. */
UPDATE creature_ai_texts SET content_loc8='Никого не осталось проверить меня!?' WHERE entry=-197; /* content: We can make it better, stronger, faster. We have the technology. We have the magic. */
UPDATE creature_ai_texts SET content_loc8='Вам здесь не место! Убить их!' WHERE entry=-198; /* content: You should not be here! Slay them! */
UPDATE creature_ai_texts SET content_loc8='Страдай, презренный червяк!' WHERE entry=-199; /* content: Suffer you despicable insect! */
UPDATE creature_ai_texts SET content_loc8='Ты будешь лёгкой добычей, $C.' WHERE entry=-200; /* content: You will be easy prey, $c. */
