UPDATE creature_ai_texts SET content_loc8='НЕТ!!! Оно положит дубильную кислоту в чан, или оно снова получит колотушкой!' WHERE entry=-203; /* content: NO!!!  It puts the tannin in the basket, or it gets the mallet again! */
