UPDATE creature_ai_texts SET content_loc8='Все ваши усилия были напрасны, ибо Древо Жизни уже иссыхает! Скоро, сердце вашего мира перестанет биться!' WHERE entry=-216; /* content: All of your efforts have been in vain, for the draining of the World Tree has already begun! Soon, the heart of your world shall beat no more! */
UPDATE creature_ai_texts SET content_loc8='Мир, который вы защищаете, обречен! Бегите прочь и, быть может, вы продлите свои жалкие жизни!' WHERE entry=-217; /* content: You are defenders of a doomed world! Flee here, and perhaps you will prolong your pathetic lives! */
UPDATE creature_ai_texts SET content_loc8='Надежда умирает последней!' WHERE entry=-218; /* content: Your hopes are lost! */
UPDATE creature_ai_texts SET content_loc8='Крикни в последний раз!' WHERE entry=-219; /* content: Scream for me! */
UPDATE creature_ai_texts SET content_loc8='Вы, смертные, умираете слишком быстро!' WHERE entry=-220; /* content: Pity, no time for a slow death! */
UPDATE creature_ai_texts SET content_loc8='Часы... еще... идут.' WHERE entry=-221; /* content: The clock... is still... ticking. */
UPDATE creature_ai_texts SET content_loc8='Рой хочет есть!' WHERE entry=-222; /* content: The swarm is eager to feed! */
UPDATE creature_ai_texts SET content_loc8='Мор и гибель на вас!' WHERE entry=-223; /* content: Pestilence upon you! */
UPDATE creature_ai_texts SET content_loc8='У тебя усталый вид...' WHERE entry=-224; /* content: You look tired... */
UPDATE creature_ai_texts SET content_loc8='Сладких снов...' WHERE entry=-225; /* content: Sweet dreams... */
UPDATE creature_ai_texts SET content_loc8='Пусть прольется огненный дождь!' WHERE entry=-226; /* content: Let fire rain from above! */
UPDATE creature_ai_texts SET content_loc8='Да запылают небо и земля!' WHERE entry=-227; /* content: Earth and sky shall burn! */
UPDATE creature_ai_texts SET content_loc8='Я из тебя амулет сделаю!' WHERE entry=-228; /* content: I gonna make you into mojo! */
UPDATE creature_ai_texts SET content_loc8='Убить тебя будет не сложно.' WHERE entry=-229; /* content: Killing you be easy. */
UPDATE creature_ai_texts SET content_loc8='Ты скоро будешь совсем мертвый!' WHERE entry=-230; /* content: You be dead soon! */
UPDATE creature_ai_texts SET content_loc8='Я тебя порешу!' WHERE entry=-231; /* content: I\'ll cut you! */
UPDATE creature_ai_texts SET content_loc8='Такое насекомое Братству не помеха.' WHERE entry=-232; /* content: The Brotherhood will not be hampered by insects. */
