UPDATE creature_ai_texts SET content_loc8='ДЕРЖИ СВОИ ГРЯЗНЫЕ ДРАККАРСКИЕ РУЧОНКИ ОТ НИХ ПОДАЛЬШЕ!' WHERE entry=-956; /* content: YOU KEEP YOUR FILTHY DRAKKARI HANDS OFF THEM! */
