UPDATE creature_ai_texts SET content_loc8='Там кто-то есть? Друг мой Тирион, ты ли это? Явился ли ты, чтоб спасти нас?' WHERE entry=-982; /* content: Is someone there? Tirion my old friend, is that you? Have you come to save us? */
UPDATE creature_ai_texts SET content_loc8='Как вы смеете! Я вас уничтожу!' WHERE entry=-983; /* content: You dare! Now feel my wrath! */
