UPDATE creature_ai_texts SET content_loc8='Я сдаюсь! Не руби меня!' WHERE entry=-992; /* content: I surrender! Stay your blade! */
UPDATE creature_ai_texts SET content_loc8='Я пойду с тобой. Только ребят моих пощади – они хорошие парни, ошиблись просто...' WHERE entry=-993; /* content: I will go with you. Just spare my men. They\'re good lads who\'ve only made a mistake... */
UPDATE creature_ai_texts SET content_loc8='Детоубийца! Душегуб! Ты заплатишь за это!' WHERE entry=-994; /* content: Calf killer! Murderer! You will pay for this! */
UPDATE creature_ai_texts SET content_loc8='ПАЛАЧ! УМРИ!' WHERE entry=-995; /* content: Butcher! Die */
UPDATE creature_ai_texts SET content_loc8='Хочешь почувствовать вкус к жизни – отведай пива с завода Т\'чали Вуду!' WHERE entry=-1001; /* content: If you wanna live life to the fullest, you gotta try T\'chali\'s Voodoo Brew! */
UPDATE creature_ai_texts SET content_loc8='Эй, милашка! Подойди поближе!' WHERE entry=-1006; /* content: Hey! Sweet cheeks, over here! */
UPDATE creature_ai_texts SET content_loc8='Йоу! Мамаша! Думаешь, такая большая, что и со мной сладишь?' WHERE entry=-1009; /* content: Yo! Big lady! You think you\'re big enough to handle me? */
UPDATE creature_ai_texts SET content_loc8='Они убили сотни наших! Мы едва сумели уйти живыми! Помогите!' WHERE entry=-1012; /* content: They killed hundreds! We barely escaped with our lives! Help! */
