UPDATE creature_ai_texts SET content_loc8='Песчаная Буря непобедима!' WHERE entry=-1036; /* content: The Sandfury reign supreme! */
UPDATE creature_ai_texts SET content_loc8='Почувствуй ярость песков!' WHERE entry=-1037; /* content: Feel the fury of the sands! */
UPDATE creature_ai_texts SET content_loc8='Умри, чужеземное отродье!' WHERE entry=-1038; /* content: Die, outlander! */
UPDATE creature_ai_texts SET content_loc8='Пусть начнется казнь!' WHERE entry=-1039; /* content: Let the executions begin! */
UPDATE creature_ai_texts SET content_loc8='Справедливость восстановлена!' WHERE entry=-1040; /* content: Justice is done! */
UPDATE creature_ai_texts SET content_loc8='Передай надзирателю, что эта тюрьма теперь наша!' WHERE entry=-1041; /* content: Tell the Warden this prison is ours now! */
UPDATE creature_ai_texts SET content_loc8='Я растопчу твой череп!' WHERE entry=-1042; /* content: I\'ll crush your skull beneath my boot! */
UPDATE creature_ai_texts SET content_loc8='Смерть тюремщикам!' WHERE entry=-1043; /* content: Death to the Warden\'s men! */
UPDATE creature_ai_texts SET content_loc8='Свежее мясо!' WHERE entry=-1044; /* content: Fresh meat! */
UPDATE creature_ai_texts SET content_loc8='Опять посыльные Смотрящей!' WHERE entry=-1045; /* content: More of the Warden\'s errand boys! */
UPDATE creature_ai_texts SET content_loc8='Тартек и гадкий дракон собираются тебя убить, тупица!' WHERE entry=-1046; /* content: Tartek and nasty dragon going to kill you! You so dumb. */
UPDATE creature_ai_texts SET content_loc8='%s окружает себя барьером бездны. Рядом с барьером сгущается зловещая тень.' WHERE entry=-1047; /* content: %s surrounds itself with a void barrier as a dark shadow coalesces nearby! */
UPDATE creature_ai_texts SET content_loc8='Не прикасаться к спорам!' WHERE entry=-1049; /* content: No Touch Spores! */
UPDATE creature_ai_texts SET content_loc8='Кто прервал мою медитацию?!' WHERE entry=-1050; /* content: Who disturbs my meditation?! */
UPDATE creature_ai_texts SET content_loc8='Что это значит?! Я еще не окончил свой пир!' WHERE entry=-1051; /* content: What is the meaning of this?! I have not yet finished my feast! */
UPDATE creature_ai_texts SET content_loc8='Я плохейший из плохих, крутейший из крутых! Ко мне, мои стихии, давайте морозить и править!' WHERE entry=-1052; /* content: I am the baddest of the bad, the coolest of the cool! To my side, my elements, let us freeze and rule! */
UPDATE creature_ai_texts SET content_loc8='Я король грязи, нет никого выше меня! На помощь, слуги, – помогите своему повелителю!' WHERE entry=-1053; /* content: I\'m the king of dirt, there is none higher! To my aid, minions - assist your sire! */
UPDATE creature_ai_texts SET content_loc8='%s гибнет, но его изломанное тело снова поднимается!' WHERE entry=-1054; /* content: %s collapses but the broken body rises again! */
UPDATE creature_ai_texts SET content_loc8='С почтой не баловаться!' WHERE entry=-1055; /* content: No tampering with the mail! */
UPDATE creature_ai_texts SET content_loc8='Это наше сокровище, увалень!' WHERE entry=-1056; /* content: That\'s our treasure, you lubber! */
UPDATE creature_ai_texts SET content_loc8='Эй! Прочь от наших сокровищ!' WHERE entry=-1057; /* content: Hey!  Get away from our treasure! */
UPDATE creature_ai_texts SET content_loc8='Эй, ребята, полегче со мной... это мой первый раз.' WHERE entry=-1059; /* content: Hey folks, go easy on me... it\'s my first time. */
UPDATE creature_ai_texts SET content_loc8='Я никогда не встречал таурена, который бы мне не понравился...' WHERE entry=-1060; /* content: I never met a tauren I didn\'t like... */
UPDATE creature_ai_texts SET content_loc8='На вкус!!!' WHERE entry=-1061; /* content: To eat!! */
UPDATE creature_ai_texts SET content_loc8='Я на прошлой неделе был в Златоземье. А тебе приходилось там бывать?' WHERE entry=-1062; /* content: Last week I was in Goldshire. Have you been there? */
UPDATE creature_ai_texts SET content_loc8='Этот город такой маленький, что я попросил ребенка нарисовать мне карту. Он нарисовал ее на обороте плаката о розыске разбойников из Братства Справедливости...' WHERE entry=-1063; /* content: That town\'s so small, I asked a kid to draw me a map. He drew it on the back of a Defias wanted poster... */
UPDATE creature_ai_texts SET content_loc8='Точно!' WHERE entry=-1064; /* content: to scale!! */
UPDATE creature_ai_texts SET content_loc8='Время для исполнения роли! Я называю этот номер "Глупый таурен"!' WHERE entry=-1065; /* content: Impersonation time!! I call this, "The Silly Tauren." */
UPDATE creature_ai_texts SET content_loc8='Комедиант откашливается.' WHERE entry=-1066; /* content: The comedian clears his throat. */
UPDATE creature_ai_texts SET content_loc8='Комедиант изображает таурена.' WHERE entry=-1067; /* content: The comedian impersonates a tauren. */
UPDATE creature_ai_texts SET content_loc8='Таверна Златоземья славится видом на озеро...' WHERE entry=-1068; /* content: Goldshire\'s inn advertises a lakeside view... */
UPDATE creature_ai_texts SET content_loc8='Мимо моего окна даже мурлок проплывал.' WHERE entry=-1069; /* content: I saw a murloc swim by my window. */
UPDATE creature_ai_texts SET content_loc8='Да что такое с женщинами? Постоянно они...' WHERE entry=-1070; /* content: What\'s the deal with women? I mean they are always like... */
UPDATE creature_ai_texts SET content_loc8='Комик изображает женщину.' WHERE entry=-1071; /* content: The comedian impersonates a human female. */
UPDATE creature_ai_texts SET content_loc8='А гномы еще хуже! Они вечно говорят что-то такое...' WHERE entry=-1072; /* content: And then gnomes are even worse! They are always saying stuff like... */
UPDATE creature_ai_texts SET content_loc8='Комик изображает гнома.' WHERE entry=-1073; /* content: The comedian impersonates a gnome. */
UPDATE creature_ai_texts SET content_loc8='Сколько кобольдов нужно, чтобы сменить фитиль у фонаря?' WHERE entry=-1074; /* content: How many kobolds does it take to change a lantern wick? */
UPDATE creature_ai_texts SET content_loc8='Ты не получишь свечку!' WHERE entry=-1075; /* content: You no take candle! */
UPDATE creature_ai_texts SET content_loc8='Эй! Эй! Эй! Это пьяница Ралик... У него такая толстая мамаша, что ей впору на обложку "Плей-огра"...' WHERE entry=-1077; /* content: Hey Hey Hey! It\'s Raliq the drunk... His momma\'s so fat she didn\'t just make the front cover of Ogre Today... */
UPDATE creature_ai_texts SET content_loc8='Она и на заднюю обложку попадет!' WHERE entry=-1078; /* content: She made the back cover too! */
UPDATE creature_ai_texts SET content_loc8='На твоем месте я бы не доверял гоблинам...' WHERE entry=-1079; /* content: Used to be you couldn\'t trust a goblin... */
UPDATE creature_ai_texts SET content_loc8='Кого я обманываю? Если торгуешь с гоблинами, стоит обшить кошелек железом.' WHERE entry=-1080; /* content: Who am I kiddin\'? If you trade with goblins you\'d better have iron plating in your coinpurse. */
UPDATE creature_ai_texts SET content_loc8='Мне нравятся эльфийки крови. Особенно когда они говорят такое...' WHERE entry=-1081; /* content: I love blood elf women. Especially when they say stuff like this... */
UPDATE creature_ai_texts SET content_loc8='Комик изображает эльфа крови.' WHERE entry=-1082; /* content: The comedian impersonates a blood elf. */
UPDATE creature_ai_texts SET content_loc8='Неужели ваши дела так плохи, что вы призываете в армию домашний скот?' WHERE entry=-1083; /* content: How desperate do you have to be for allies that you recruit your livestock? */
UPDATE creature_ai_texts SET content_loc8='У меня есть собака, но я не даю ей меч.' WHERE entry=-1084; /* content: I own a dog, but I\'m not giving him a sword. */
UPDATE creature_ai_texts SET content_loc8='И последняя пародия. Угадать трудно, но, может, вам удастся...' WHERE entry=-1085; /* content: One last impersonation. It\'s tough, but let\'s see if you can guess who it is... */
UPDATE creature_ai_texts SET content_loc8='Комик изображает орка.' WHERE entry=-1086; /* content: The comedian impersonates an orc. */
UPDATE creature_ai_texts SET content_loc8='Теперь, когда концерт окончен, может быть, кто-нибудь проведет меня по монастырю Алого ордена?' WHERE entry=-1087; /* content: Now that the show\'s done, can someone run me through Scarlet Monestary? */
UPDATE creature_ai_texts SET content_loc8='Какая милая компания. Даже дворфийки все без бород.' WHERE entry=-1088; /* content: What a good looking crowd. The dwarven women even shaved. */
UPDATE creature_ai_texts SET content_loc8='Уже все? Вы отличные зрители. Берегите себя, спокойной вам ночи.' WHERE entry=-1089; /* content: Is it over already? You\'ve been great folks. Walk safely and have a good night. */
UPDATE creature_ai_texts SET content_loc8='А вы замечали, что все эти милые орочьи детишки на одно лицо?' WHERE entry=-1090; /* content: Have you ever noticed that all those cute, orc kids look the same? */
UPDATE creature_ai_texts SET content_loc8='Как жаль, что они не остаются милашками. В Степях надо ввести какой-то обряд посвящения, типа "Залезь на жуткое дерево и свались оттуда".' WHERE entry=-1091; /* content: It\'s too bad they don\'t stay cute. There must be some right of passage in the Barrens called, "Climb ugly tree and fall out." */
UPDATE creature_ai_texts SET content_loc8='Комик изображает женщину таурена.' WHERE entry=-1092; /* content: The comedian impersonates a female tauren. */
UPDATE creature_ai_texts SET content_loc8='Вот хорошая шутка: откуда в арбузах вода?' WHERE entry=-1093; /* content: Here\'s a good one, why do watermelons have water in them? */
UPDATE creature_ai_texts SET content_loc8='Потому что их сажают весной!' WHERE entry=-1094; /* content: Because they are planted in the spring! */
UPDATE creature_ai_texts SET content_loc8='Эй, это же пьяница Райлик... У него такая толстая мамаша, жуть! Когда она летит в Острогорье, с нее деньги берут в расчете за каждый фует веса!' WHERE entry=-1095; /* content: Hey, it\'s Raliq the drunk... His momma\'s so fat, when she flies to Blade\'s Edge they charged her by the pound! */
UPDATE creature_ai_texts SET content_loc8='Как называется сломанный бумеранг?' WHERE entry=-1096; /* content: What do you call a broken boomerang? */
UPDATE creature_ai_texts SET content_loc8='Палка!' WHERE entry=-1097; /* content: A stick! */
UPDATE creature_ai_texts SET content_loc8='Всем спасибо! И продолжайте хлопать, тут где-то муха летает, и одному из вас суждено ее поймать! Хорошей ночи!' WHERE entry=-1098; /* content: Thank you everyone! And keep clapping, there is a fly in here and one of you are bound to get it! Good night! */
UPDATE creature_ai_texts SET content_loc8='Можно пару магических усилений! Помираю тут...' WHERE entry=-1099; /* content: Can I get a buff? I\'m dying up here! */
UPDATE creature_ai_texts SET content_loc8='Представьте, что элекк залез на ваш забор... что бы это могло значить?' WHERE entry=-1100; /* content: What time is it when an Elekk sits on your fence? */
UPDATE creature_ai_texts SET content_loc8='Это значит, что пора делать новый забор!' WHERE entry=-1101; /* content: Time to get a new fence!! */
UPDATE creature_ai_texts SET content_loc8='Значит, эльфы крови думают, что еще чуть-чуть - и они будут править миром...' WHERE entry=-1102; /* content: So, the Blood Elves think they\'re one step away from ruling the world... */
UPDATE creature_ai_texts SET content_loc8='Скорее уж двенадцать шагов!' WHERE entry=-1103; /* content: Twelve steps is more like it! */
UPDATE creature_ai_texts SET content_loc8='Обычно гномы – милый и дружелюбный народ...' WHERE entry=-1104; /* content: The gnomes used to be a nice respectful people... */
UPDATE creature_ai_texts SET content_loc8='В другой раз я увидел, как один такой идет по улице и покрикивает: "Свяжешься с гномом – не дойдешь до дому!"' WHERE entry=-1105; /* content: The other day I saw one walking down the street shouting, "Once you go gnome, you never go home!" */
UPDATE creature_ai_texts SET content_loc8='Комик изображает женщину дворфа.' WHERE entry=-1106; /* content: The comedian impersonates a female dwarf. */
UPDATE creature_ai_texts SET content_loc8='Говорит как-то гном таурену, что бывал в Огненных Недрах...' WHERE entry=-1107; /* content: So this gnome tells a tauren he\'s been to Molten Core. */
UPDATE creature_ai_texts SET content_loc8='А таурен говорит: "Да враки это все!"' WHERE entry=-1108; /* content: The tauren says, "That\'s a load of bull." */
UPDATE creature_ai_texts SET content_loc8='Мне кажется, или воины Орды – самые уродливые из наших противников?' WHERE entry=-1109; /* content: Is it just me, or is the Horde about the ugliest enemy we could find? */
UPDATE creature_ai_texts SET content_loc8='Вы, ребята, не обижайтесь! Я только отметил необычайную красоту эльфов крови. Трудности перевода, сами понимаете.' WHERE entry=-1110; /* content: Just so you guys don\'t feel left out, all I said was how attractive the blood elves are. It loses something in the translation. */
UPDATE creature_ai_texts SET content_loc8='Мы с женой прожили счастливо 23 года.' WHERE entry=-1111; /* content: My wife and I were happy for 23 years... */
UPDATE creature_ai_texts SET content_loc8='А потом мы встретились!' WHERE entry=-1112; /* content: And then we met! */
UPDATE creature_ai_texts SET content_loc8='Мне нравятся ночные эльфийки. Особенно когда они говорят такое...' WHERE entry=-1113; /* content: I love night elf women. Especially when they say stuff like this... */
UPDATE creature_ai_texts SET content_loc8='Комик изображает ночного эльфа.' WHERE entry=-1114; /* content: The comedian impersonates a night elf. */
UPDATE creature_ai_texts SET content_loc8='Комик изображает нежить.' WHERE entry=-1115; /* content: The comedian impersonates an undead. */
UPDATE creature_ai_texts SET content_loc8='Вот $n – убейте $gего:ее;!' WHERE entry=-1119; /* content: Kill $N! */
UPDATE creature_ai_texts SET content_loc8='Вы недостойны предстать перед Королем-личом!' WHERE entry=-1120; /* content: You are not worthy to face the Lich King! */
UPDATE creature_ai_texts SET content_loc8='Господин, я подвел вас...' WHERE entry=-1121; /* content: Master, I have failed... */
UPDATE creature_ai_texts SET content_loc8='Мужчины, женщины и дети... Никто не избежал гнева господина. Вы разделите их участь.' WHERE entry=-1122; /* content: Men... Women... and Children. None were spared the Master\'s wrath. Your death will be no different. */
UPDATE creature_ai_texts SET content_loc8='Марвин, добей их...' WHERE entry=-1123; /* content: ..Marwyn..! Finish them..! */
UPDATE creature_ai_texts SET content_loc8='Сопливый червяк!' WHERE entry=-1124; /* content: Sniveling maggot! */
UPDATE creature_ai_texts SET content_loc8='Стратхольмские детишки – и те защищались отчаяннее!' WHERE entry=-1125; /* content: The children of Stratholme fought with more ferocity! */
UPDATE creature_ai_texts SET content_loc8='Как приятен страх...' WHERE entry=-1126; /* content: Fear...so exhilarating! */
UPDATE creature_ai_texts SET content_loc8='Как сладостно отчаяние...' WHERE entry=-1127; /* content: Despair...so delicious */
UPDATE creature_ai_texts SET content_loc8='Солдаты Лордерона, восстаньте по зову господина!' WHERE entry=-1128; /* content: Soldiers of Lordaeron, rise to meet your master\'s call! */
UPDATE creature_ai_texts SET content_loc8='Я не стану вам врать, солдаты. Бьют нас тут крепко! Ну что, кто из вас истинный орк, у кого есть силы огрызнуться?' WHERE entry=-1129; /* content: I\'m not gonna lie to you, soldiers. We\'re getting our butts out there! Now which one of you is orc enough to do something about it? */
UPDATE creature_ai_texts SET content_loc8='Поверить не могу, что Папаша Колесун надеется убедить хоть кого-нибудь отправиться в Манагорн Б\'наар!' WHERE entry=-1130; /* content: I can\'t belive that Papa Wheeler thinks he\'s going to dupe anyone into going out to Manaforge B\'naar! */
UPDATE creature_ai_texts SET content_loc8='Нельзя, чтобы из-за неудачного сервомеханизма застопорилась постройка ракеты Пустоты X-52!' WHERE entry=-1131; /* content: We can\'t let a failing servo slow down the construction of the X-52 Nether-Rocket! */
UPDATE creature_ai_texts SET content_loc8='Глыбень Пустоты?! Да он размажет тебя в лепешку! Нет уж, спасибо!' WHERE entry=-1132; /* content: Netherock?! That thing\'ll squish you flat with one step of its massive foot! No thanks! */
UPDATE creature_ai_texts SET content_loc8='Этот жезл тебе дал клятый Сломленный, презренный червь, верно? А он сказал тебе, что предал свое племя? Неважно, вы оба за это заплатите!' WHERE entry=-1133; /* content: That Broken worm gave you that staff, didn\'t he? Did he also tell you he\'s the one that sold out his tribe? No matter, you will both pay for this! */
UPDATE creature_ai_texts SET content_loc8='Мы найдем то, что ищет хозяин! Провал для нас немыслим!' WHERE entry=-1134; /* content: We will find what the Master is looking for! Failure is not an option! */
UPDATE creature_ai_texts SET content_loc8='Повелители Змей непобедимы!' WHERE entry=-1135; /* content: None can stand against the serpent lords! */
UPDATE creature_ai_texts SET content_loc8='Говори, никчемный беженец! Что делал твой караван в Костяных пустошах?!' WHERE entry=-1136; /* content: Speak, filthy refugee! What was your caravan doing in the Bone Wastes? */
UPDATE creature_ai_texts SET content_loc8='Нападай, Гроза Плети. Сейчас я покажу господину, кто из нас может называться чемпионом!' WHERE entry=-1137; /* content: Come Scourgebane, I\'ll show the Lich King which one of us is truly worthy of the title, champion! */
UPDATE creature_ai_texts SET content_loc8='Может быть... ты и прав, Крок.' WHERE entry=-1138; /* content: What a pitiful choice of an ally Crok. */
UPDATE creature_ai_texts SET content_loc8='Иди, $n. Взгляни, что ждет тебя в Кошмаре...' WHERE entry=-1139; /* content: Come, $n. See what the Nightmare brings... */
UPDATE creature_ai_texts SET content_loc8='Кошмар нельзя остановить!' WHERE entry=-1140; /* content: The Nightmare cannot be stopped! */
UPDATE creature_ai_texts SET content_loc8='%s выжимает остатки жизни из |3-1($n) и поглощает $gего:ее; душу.' WHERE entry=-1141; /* content: %s squeezes the last bit of life out of $n and swallows their soul. */
UPDATE creature_ai_texts SET content_loc8='Копай быстрее, ничтожество! Не заставляй меня показывать тебе, что такое настоящая боль!' WHERE entry=-1142; /* content: Dig faster, wretch!  Do not force me to show you the true meaning of pain! */
UPDATE creature_ai_texts SET content_loc8='Не останавливайся! Ты умрешь тысячью смертей, если хоть подумаешь о том, чтобы выпустить кирку!' WHERE entry=-1143; /* content: Do not stop!  I promise you a thousand deaths if you even think about putting down that pick! */
UPDATE creature_ai_texts SET content_loc8='Быстрее, мразь! Хозяин не обрадуется, если мы не найдем кристалл Ата\'мала, который ему нужен.' WHERE entry=-1144; /* content: Faster, scum!  The Master won\'t be pleased if we don\'t find the Ata\'mal crystal he\'s after. */
UPDATE creature_ai_texts SET content_loc8='Жалкое Отребье, приказываю тебе убить чужаков! И только попробуй ослушаться – я превращу твою жизнь в нескончаемую боль!' WHERE entry=-1145; /* content: Slay these intruders, filthy Dreghood!  I promise you neverending pain if you disobey me! */
UPDATE creature_ai_texts SET content_loc8='Смеешь вмешиваться в дела господина? Ты заплатишь, $gглупый смертный:глупая смертная;!' WHERE entry=-1146; /* content: You dare interfere with the Master\'s endeavors?  You will pay, foolish mortal! */
UPDATE creature_ai_texts SET content_loc8='Вперед, мелкие проходимцы! Покажите этим болванам, что с нашим господином шутки плохи!' WHERE entry=-1147; /* content: Go, you little wretches!  Show these fools that our Master is not to be trifled with! */
UPDATE creature_ai_texts SET content_loc8='Даже не думайте отступать, негодяи! Обещаю, если вы не сразите врагов, будете умолять меня о смерти!' WHERE entry=-1148; /* content: Do not even think of fleeing, Broken wretches!  I promise you a fate worse than death if you don\'t slay these intruders! */
UPDATE creature_ai_texts SET content_loc8='Может быть... Ты прав... Крок.' WHERE entry=-1149; /* content: Perhaps... you were right... Crok. */
UPDATE creature_ai_texts SET content_loc8='Они не должны прорваться к Синдрагосе, скорей... остановите их!' WHERE entry=-1150; /* content: You must not approach the Frost Queen, quickly... stop them! */
UPDATE creature_ai_texts SET content_loc8='Жди моего приказа, я сам с этим справлюсь!' WHERE entry=-1151; /* content: Wait for me orders. I handle dis myself! */
