UPDATE db_script_string SET content_loc8='Араккоа прячутся... повсюду!' WHERE entry=2000000187; /* content: The arakkoa are hidden... everywhere! */
UPDATE db_script_string SET content_loc8='Когда мы прибудем?' WHERE entry=2000000187; /* content: When we arrive ? */
UPDATE db_script_string SET content_loc8='Куда мы направляемся?' WHERE entry=2000000188; /* content: Where we going ? */
UPDATE db_script_string SET content_loc8='Никогда бы не подумала, что это будет масло маны! Уж оно-то всем нужно – за исключением Грека, конечно.' WHERE entry=2000000189; /* content: I never thought it would turn out to be mana oil! There\'s going to be plenty of demand for that, minus Grek, of course! */
