UPDATE db_script_string SET content_loc8='А что, если мы закопаем этот осколок в землю и посмотрим, что будет?' WHERE entry=2000000182; /* content: I have an idea. Let\'s plant this shard in the ground and see what happens. */
UPDATE db_script_string SET content_loc8='Я никогда остановлюсь. Никогда...' WHERE entry=2000000183; /* content: I\'ll never stop. Never... */
UPDATE db_script_string SET content_loc8='Зло, исходящее из осколков кристаллов, привлекло одного из колоссов. Скорее останови его!' WHERE entry=2000000183; /* content: The evil emanating from the crystal shard has lured one of the colossi. Put it down quick! */
UPDATE db_script_string SET content_loc8='Привет, Чариса. Это мой список, вы можете найти мне все это, и особенно последний компонент?' WHERE entry=2000000184; /* content: Hello, Charys. Here my list, can you find me all that, and especially the last ingredient? */
UPDATE db_script_string SET content_loc8='Конечно, Пейдж. Но будь хорошей.' WHERE entry=2000000185; /* content: Of course, Paige. But be nice. */
UPDATE db_script_string SET content_loc8='Спасибо, Чариса. Пойдем Пейдж, мой ангел.' WHERE entry=2000000186; /* content: Thank you, Charys. Let us go Paige, my angel. */
UPDATE db_script_string SET content_loc8='Когда мы прибудем?' WHERE entry=2000000187; /* content: When we arrive ? */
