UPDATE script_texts SET content_loc8='Тареты здесь нет. Отправимся в город.' WHERE entry=-1560011; /* content: Taretha isn\'t here. Let\'s head into town. */
UPDATE script_texts SET content_loc8='Ее здесь нет.' WHERE entry=-1560012; /* content: She\'s not here. */
UPDATE script_texts SET content_loc8='Тралл! Выйди – и встреть свою судьбу!' WHERE entry=-1560013; /* content: Thrall! Come outside and face your fate! */
UPDATE script_texts SET content_loc8='Тралл загнал себя в ловушку в часовне. Теперь он не сможет сбежать.' WHERE entry=-1560016; /* content: Thrall\'s trapped himself in the chapel. He can\'t escape now. */
UPDATE script_texts SET content_loc8='Он здесь, останови его!' WHERE entry=-1560017; /* content: He\'s here, stop him! */
UPDATE script_texts SET content_loc8='У нас сколько угодно времени...' WHERE entry=-1560018; /* content: We have all the time in the world.... */
UPDATE script_texts SET content_loc8='От нас не убежишь' WHERE entry=-1560019; /* content: You cannot escape us! */
UPDATE script_texts SET content_loc8='Не думайте, что можете победить!' WHERE entry=-1560020; /* content: Do not think you can win! */
UPDATE script_texts SET content_loc8='Используй меня повторно' WHERE entry=-1560021; /* content: REUSE ME */
UPDATE script_texts SET content_loc8='Используй меня повторно' WHERE entry=-1560022; /* content: REUSE ME */
UPDATE script_texts SET content_loc8='Отлично. Вперед!' WHERE entry=-1560023; /* content: Very well then. Let\'s go! */
UPDATE script_texts SET content_loc8='Я больше никогда не буду скован!' WHERE entry=-1560026; /* content: I\'ll never be chained again! */
UPDATE script_texts SET content_loc8='Отлично. Мельница Таррен к западу отсюда. У нас мало времени...' WHERE entry=-1560027; /* content: Very well. Tarren Mill lies just west of here. Since time is of the essence... */
UPDATE script_texts SET content_loc8='Поехали!' WHERE entry=-1560028; /* content: Let\'s ride! */
UPDATE script_texts SET content_loc8='Тарета, должно быть, в таверне. Пошли.' WHERE entry=-1560029; /* content: Taretha must be in the inn. Let\'s go. */
UPDATE script_texts SET content_loc8='Тарета! Что это за мерзкое волшебство?' WHERE entry=-1560030; /* content: Taretha! What foul magic is this? */
UPDATE script_texts SET content_loc8='Что это было?' WHERE entry=-1560031; /* content: Who or what was that? */
UPDATE script_texts SET content_loc8='Нет!' WHERE entry=-1560032; /* content: No! */
UPDATE script_texts SET content_loc8='Прощай, Тарета. Я никогда не забуду твоей доброты.' WHERE entry=-1560033; /* content: Goodbye, Taretha. I will never forget your kindness. */
UPDATE script_texts SET content_loc8='Я заслужил свободу!' WHERE entry=-1560038; /* content: I have earned my freedom! */
UPDATE script_texts SET content_loc8='Я больше не раб!' WHERE entry=-1560040; /* content: I am a slave no longer! */
UPDATE script_texts SET content_loc8='Блэкмур ответит за все!' WHERE entry=-1560041; /* content: Blackmoore has much to answer for! */
UPDATE script_texts SET content_loc8='Я был вынужден это сделать!' WHERE entry=-1560042; /* content: You have forced my hand! */
UPDATE script_texts SET content_loc8='До этого не должно было дойти!' WHERE entry=-1560043; /* content: It should not have come to this! */
UPDATE script_texts SET content_loc8='Я не хотел этого!' WHERE entry=-1560044; /* content: I did not ask for this! */
UPDATE script_texts SET content_loc8='Я ваш должник, незнакомцы.' WHERE entry=-1560045; /* content: I am truly in your debt, strangers. */
UPDATE script_texts SET content_loc8='Спасибо, незнакомцы. Вы подарили мне надежду.' WHERE entry=-1560046; /* content: Thank you, strangers. You have given me hope. */
UPDATE script_texts SET content_loc8='Я не упущу свой шанс. Я пойду навстречу своей судьбе.' WHERE entry=-1560047; /* content: I will not waste this chance. I will seek out my destiny. */
UPDATE script_texts SET content_loc8='Я свободна! Спасибо всем вам!' WHERE entry=-1560048; /* content: I\'m free! Thank you all! */
UPDATE script_texts SET content_loc8='Тралл, ты сбежал!' WHERE entry=-1560049; /* content: Thrall, you escaped! */
UPDATE script_texts SET content_loc8='Пусть отдохнет.' WHERE entry=-1560050; /* content: That\'s enough out of him. */
UPDATE script_texts SET content_loc8='Это заклинание должно стереть их память о нас и о том, что сейчас произошло. Все, что они теперь должны помнить, – это то, на что бы походила действительность без вмешательства в ход времени. Отлично. Тралл отправится навстречу своей судьбе, а Тарета...' WHERE entry=-1560051; /* content: That spell should wipe their memories of us and what just happened. All they should remember now is what reality would be like without the attempted temporal interference. Well done. Thrall will journey on to find his destiny, and Taretha... */
UPDATE script_texts SET content_loc8='К сожалению, ее судьба предрешена.' WHERE entry=-1560052; /* content: Her fate is regrettably unavoidable. */
UPDATE script_texts SET content_loc8='Я рад, что с тобой все в порядке, Тарета. Без твоих друзей у нас ничего бы не вышло. Это все благодаря им.' WHERE entry=-1560054; /* content: I\'m glad you\'re safe, Taretha. None of this would have been possible without your friends. They made all of this happen. */
UPDATE script_texts SET content_loc8='Тралл, я вижу их первый раз в жизни.' WHERE entry=-1560055; /* content: Thrall, I\'ve never met these people before in my life. */
UPDATE script_texts SET content_loc8='Тогда кто они?' WHERE entry=-1560056; /* content: Then who are these people? */
UPDATE script_texts SET content_loc8='Думаю, я смогу все вам объяснить, если вы уделите мне минутку.' WHERE entry=-1560057; /* content: I believe I can explain everything to you two if you give me a moment of your time. */
UPDATE script_texts SET content_loc8='Вы умрете – во славу леди Вайш!' WHERE entry=-1564000; /* content: You will die in the name of Lady Vashj! */
UPDATE script_texts SET content_loc8='Постой-ка тут!' WHERE entry=-1564001; /* content: Stick around! */
UPDATE script_texts SET content_loc8='Земля начинает раскалываться!' WHERE entry=-1564012; /* content: The ground begins to crack open! */
UPDATE script_texts SET content_loc8='Славься, Акама!' WHERE entry=-1564017; /* content: Hail Akama! */
UPDATE script_texts SET content_loc8='Будешь со мной играть – поплатишься.' WHERE entry=-1564018; /* content: You play, you pay. */
UPDATE script_texts SET content_loc8='Не впечатляет.' WHERE entry=-1564019; /* content: I\'m not impressed. */
UPDATE script_texts SET content_loc8='Что, развлекаемся?' WHERE entry=-1564020; /* content: Enjoying yourselves? */
UPDATE script_texts SET content_loc8='Так... Вы тут по делу или ради моего удовольствия?' WHERE entry=-1564021; /* content: So... business or pleasure? */
UPDATE script_texts SET content_loc8='Не нужно стесняться...' WHERE entry=-1564023; /* content: Don\'t be shy. */
UPDATE script_texts SET content_loc8='Я вся... твоя.' WHERE entry=-1564024; /* content: I\'m all... yours. */
UPDATE script_texts SET content_loc8='Как пришло, так и ушло.' WHERE entry=-1564025; /* content: Easy come, easy go. */
UPDATE script_texts SET content_loc8='Какая счастливая кончина.' WHERE entry=-1564026; /* content: So much for a happy ending. */
UPDATE script_texts SET content_loc8='Хватит играть с моими чувствами!' WHERE entry=-1564027; /* content: Stop toying with my emotions! */
UPDATE script_texts SET content_loc8='Орда... уничтожит вас.' WHERE entry=-1564029; /* content: Horde will... crush you. */
UPDATE script_texts SET content_loc8='Больше! Больше крови!' WHERE entry=-1564031; /* content: More! I want more! */
UPDATE script_texts SET content_loc8='Есть хочу!' WHERE entry=-1564033; /* content: I hunger! */
UPDATE script_texts SET content_loc8='Я выпущу вам кишки!' WHERE entry=-1564035; /* content: I\'ll rip the meat from your bones! */
UPDATE script_texts SET content_loc8='Я отомщу!' WHERE entry=-1564038; /* content: Vengeance is mine! */
UPDATE script_texts SET content_loc8='Чего вы боитесь?' WHERE entry=-1564041; /* content: What are you afraid of? */
UPDATE script_texts SET content_loc8='У меня для вас кое-что есть...' WHERE entry=-1564044; /* content: I have something for you... */
UPDATE script_texts SET content_loc8='Я требую должного уважения!' WHERE entry=-1564045; /* content: YOU WILL SHOW THE PROPER RESPECT! */
UPDATE script_texts SET content_loc8='Вас ожидают лишь боль и страдания!' WHERE entry=-1564047; /* content: Pain and suffering are all that await you! */
UPDATE script_texts SET content_loc8='Не покидай меня!' WHERE entry=-1564048; /* content: Don\'t leave me alone! */
UPDATE script_texts SET content_loc8='Я не просил об этом!' WHERE entry=-1564050; /* content: I didn\'t ask for this! */
UPDATE script_texts SET content_loc8='Я не хочу обратно!' WHERE entry=-1564052; /* content: I don\'t want to go back! */
UPDATE script_texts SET content_loc8='Ну, и что мне теперь делать?' WHERE entry=-1564053; /* content: Now what do I do? */
UPDATE script_texts SET content_loc8='Используй меня повторно' WHERE entry=-1564054; /* content: REUSE ME */
UPDATE script_texts SET content_loc8='Ваши мечты осуществятся... Но за это придется заплатить.' WHERE entry=-1564055; /* content: You can have anything you desire... for a price. */
UPDATE script_texts SET content_loc8='Твоим возможностям далеко до твоих желаний.' WHERE entry=-1564058; /* content: Your reach exceeds your grasp. */
UPDATE script_texts SET content_loc8='Берегитесь: я жив!' WHERE entry=-1564062; /* content: Beware: I live! */
UPDATE script_texts SET content_loc8='Так... глупо.' WHERE entry=-1564063; /* content: So... foolish. */
UPDATE script_texts SET content_loc8='На колени!' WHERE entry=-1564066; /* content: On your knees! */
UPDATE script_texts SET content_loc8='Хотите испытать меня?' WHERE entry=-1564069; /* content: You wish to test me? */
UPDATE script_texts SET content_loc8='У меня есть более важные дела...' WHERE entry=-1564070; /* content: I have better things to do... */
UPDATE script_texts SET content_loc8='Бегите, или умрите!' WHERE entry=-1564071; /* content: Flee or die! */
UPDATE script_texts SET content_loc8='Всеобщий язык такой грубый... Бандал!' WHERE entry=-1564072; /* content: Common... such a crude language. Bandal! */
UPDATE script_texts SET content_loc8='Поиграли и хватит!' WHERE entry=-1564073; /* content: Enough games! */
UPDATE script_texts SET content_loc8='За Кель\'Талас! За Солнечный Колодец!' WHERE entry=-1564075; /* content: For Quel\'Thalas! For the Sunwell! */
UPDATE script_texts SET content_loc8='Второго шанса не будет!' WHERE entry=-1564079; /* content: No second chances! */
UPDATE script_texts SET content_loc8='Ты – моя добыча!' WHERE entry=-1564081; /* content: You are mine! */
UPDATE script_texts SET content_loc8='Анар\'ала белоре!' WHERE entry=-1564082; /* content: Anar\'alah belore! */
UPDATE script_texts SET content_loc8='У меня еще много сюрпризов!' WHERE entry=-1564083; /* content: I\'m full of surprises! */
UPDATE script_texts SET content_loc8='Ша-амоор ара машол?' WHERE entry=-1564084; /* content: Sha\'amoor ara mashal? */
UPDATE script_texts SET content_loc8='Селама ам\'оронор!' WHERE entry=-1564085; /* content: Selama am\'oronor! */
UPDATE script_texts SET content_loc8='Мое дело сделано.' WHERE entry=-1564087; /* content: My work is done. */
UPDATE script_texts SET content_loc8='Шорель-аран.' WHERE entry=-1564088; /* content: Shorel\'aran. */
UPDATE script_texts SET content_loc8='Неплохо!' WHERE entry=-1564089; /* content: Well done! */
UPDATE script_texts SET content_loc8='Вот это удар!' WHERE entry=-1564090; /* content: A glorious kill! */
UPDATE script_texts SET content_loc8='Белеса менор!' WHERE entry=-1564092; /* content: Belesa menoor! */
UPDATE script_texts SET content_loc8='Это... ваша судьба.' WHERE entry=-1564095; /* content: Destiny... awaits. */
UPDATE script_texts SET content_loc8='Акама. Я не удивлен твоей двуличностью. Давно нужно было убить тебя и твоих мерзких прихвостней.' WHERE entry=-1564097; /* content: Akama. Your duplicity is hardly surprising. I should have slaughtered you and your malformed brethren long ago. */
UPDATE script_texts SET content_loc8='Время пришло! Пора действовать!' WHERE entry=-1564100; /* content: The time has come! The moment is at hand! */
UPDATE script_texts SET content_loc8='Вы не готовы!' WHERE entry=-1564101; /* content: You are not prepared! */
UPDATE script_texts SET content_loc8='Это все, смертные? Это все, на что вы способны?' WHERE entry=-1564102; /* content: Is this it, mortals? Is this all the fury you can muster? */
UPDATE script_texts SET content_loc8='Их гнев не чета моему, Иллидан. У нас есть одно незаконченное дело.' WHERE entry=-1564103; /* content: Their fury pales before mine, Illidan. We have some unsettled business between us. */
UPDATE script_texts SET content_loc8='Наконец-то моя долгая охота завершилась. Сегодня свершится правосудие.' WHERE entry=-1564105; /* content: My long hunt is finally over. Today, Justice will be done! */
UPDATE script_texts SET content_loc8='Почувствуйте ненависть десяти тысяч лет.' WHERE entry=-1564106; /* content: Feel the hatred of ten thousand years! */
UPDATE script_texts SET content_loc8='Все кончено. Ты проиграл.' WHERE entry=-1564107; /* content: It is finished. You are beaten. */
UPDATE script_texts SET content_loc8='Ты победила, Майев. Но охотница... не может жить без охоты. И ты... никто... без меня.' WHERE entry=-1564108; /* content: You have won... Maiev. But the huntress... is nothing without the hunt. You... are nothing... without me. */
UPDATE script_texts SET content_loc8='Он прав. Я ничего не чувствую... Я – ничто...' WHERE entry=-1564109; /* content: He\'s right. I feel nothing... I am... nothing. */
UPDATE script_texts SET content_loc8='Прощайте, герои.' WHERE entry=-1564110; /* content: Farewell, champions. */
UPDATE script_texts SET content_loc8='Вы ничего не знаете о СИЛЕ!' WHERE entry=-1564114; /* content: You know nothing of power! */
UPDATE script_texts SET content_loc8='Это за Наишу!' WHERE entry=-1564116; /* content: That is for Naisha! */
UPDATE script_texts SET content_loc8='На этот раз – никакой тюрьмы!' WHERE entry=-1564118; /* content: There shall be no prison for you this time! */
UPDATE script_texts SET content_loc8='Придите, слуги мои, воздайте этому предателю по заслугам!' WHERE entry=-1564121; /* content: Come, my minions. Deal with this traitor as he deserves! */
UPDATE script_texts SET content_loc8='Слишком просто!' WHERE entry=-1564124; /* content: This is too easy! */
UPDATE script_texts SET content_loc8='Вы не достойны даже коснуться меня!' WHERE entry=-1564125; /* content: I will not be touched by rabble such as you! */
UPDATE script_texts SET content_loc8='Узрите ПЛАМЯ Аззинота!' WHERE entry=-1564126; /* content: Behold the flames of Azzinoth! */
UPDATE script_texts SET content_loc8='Взгляните в глаза Отступника!' WHERE entry=-1564127; /* content: Stare into the eyes of the Betrayer! */
UPDATE script_texts SET content_loc8='Узрите силу... истинного демона!' WHERE entry=-1564128; /* content: Behold the power... of the demon within! */
UPDATE script_texts SET content_loc8='Сломленные из племени Пеплоустов, ваш предводитель говорит!' WHERE entry=-1564130; /* content: Broken of the Ashtongue tribe, your leader speaks! */
UPDATE script_texts SET content_loc8='Я не смогу сделать это один...' WHERE entry=-1564132; /* content: I cannot do this alone... */
UPDATE script_texts SET content_loc8='Ты не один, Акама.' WHERE entry=-1564133; /* content: You are not alone, Akama. */
UPDATE script_texts SET content_loc8='Твой народ всегда будет с тобой!' WHERE entry=-1564134; /* content: Your people will always be with you! */
UPDATE script_texts SET content_loc8='Вы не сможете противостоять воинам Груула!' WHERE entry=-1565001; /* content: You will not defeat the hand of Gruul! */
UPDATE script_texts SET content_loc8='Пфе! Это ничего не доказывает!' WHERE entry=-1565003; /* content: Pah! Does not prove anything! */
UPDATE script_texts SET content_loc8='Хорошо, теперь сразитесь со мной!' WHERE entry=-1565005; /* content: Good, now you fight me! */
UPDATE script_texts SET content_loc8='Спасенья нет' WHERE entry=-1565012; /* content: No escape */
UPDATE script_texts SET content_loc8='%s увеличивается в размерах!' WHERE entry=-1565019; /* content: %s grows in size! */
UPDATE script_texts SET content_loc8='Духи ветра станут вашей погибелью!' WHERE entry=-1568000; /* content: Spirits of da wind be your doom! */
UPDATE script_texts SET content_loc8='Щас я вас сожгу!' WHERE entry=-1568001; /* content: I burn ya now! */
UPDATE script_texts SET content_loc8='Эй, хранители! Займитесь яйцами!' WHERE entry=-1568002; /* content: Where ma hatcha? Get to work on dem eggs! */
UPDATE script_texts SET content_loc8='Я покажу вам... что такое численное превосходство!' WHERE entry=-1568003; /* content: I show you strength... in numbers. */
UPDATE script_texts SET content_loc8='Ваше время вышло!' WHERE entry=-1568004; /* content: You done run outta time! */
UPDATE script_texts SET content_loc8='Ну вот тебе и конец!' WHERE entry=-1568005; /* content: It all be over now, mon! */
UPDATE script_texts SET content_loc8='Тазага-чуу!' WHERE entry=-1568006; /* content: Tazaga-choo! */
UPDATE script_texts SET content_loc8='Вперед, стражники! Начнем резню!' WHERE entry=-1568010; /* content: Get da move on, guards! It be killin\' time! */
UPDATE script_texts SET content_loc8='Стражники, не мешкайте! Кого вы боитесь больше – их или меня?!' WHERE entry=-1568011; /* content: Guards, go already! Who you more afraid of, dem... or me? */
UPDATE script_texts SET content_loc8='Стражники! Отправляйтесь туда и принесите мне их головы!' WHERE entry=-1568012; /* content: Ride now! Ride out dere and bring me back some heads! */
UPDATE script_texts SET content_loc8='Мое терпение на исходе! Вперед! Пускай они пожалеют о том, что появились на свет!' WHERE entry=-1568013; /* content: I be losin\' me patience! Go on: make dem wish dey was never born! */
UPDATE script_texts SET content_loc8='Что может быть лучше вечной службы духу медведя? Отдайте мне свои души!' WHERE entry=-1568014; /* content: What could be better than servin\' da bear spirit for eternity? Come closer now. Bring your souls to me! */
UPDATE script_texts SET content_loc8='Очень скоро вы умрете!' WHERE entry=-1568016; /* content: You be dead soon enough! */
UPDATE script_texts SET content_loc8='Я несу боль!' WHERE entry=-1568017; /* content: I bring da pain! */
UPDATE script_texts SET content_loc8='Хотели разбудить во мне зверя? Вам это удалось.' WHERE entry=-1568018; /* content: You call on da beast, you gonna get more dan you bargain for! */
UPDATE script_texts SET content_loc8='Амани будут править вновь!' WHERE entry=-1568022; /* content: Da Amani gonna rule again! */
UPDATE script_texts SET content_loc8='Я буду... ждать вас... в другом мире.' WHERE entry=-1568023; /* content: I... be waitin\' on da udda side.... */
UPDATE script_texts SET content_loc8='Я – охотник! Вы – добыча!' WHERE entry=-1568026; /* content: I be da predator! You da prey... */
UPDATE script_texts SET content_loc8='Пируйте, братья мои!' WHERE entry=-1568027; /* content: Feed, me bruddahs! */
UPDATE script_texts SET content_loc8='Не ной!' WHERE entry=-1568030; /* content: Ya got nothin\'! */
UPDATE script_texts SET content_loc8='Ничего у тебя не вышло!' WHERE entry=-1568031; /* content: Stop your cryin\'! */
UPDATE script_texts SET content_loc8='Вам не победить... мой дух...' WHERE entry=-1568032; /* content: You can\'t... kill... me spirit! */
UPDATE script_texts SET content_loc8='Появляется электрическая буря!' WHERE entry=-1568033; /* content: An Electrical Storm Appears! */
UPDATE script_texts SET content_loc8='На колени!! Склонитесь пред клыком и когтем!' WHERE entry=-1568034; /* content: Get on ya knees and bow.... to da fang and claw! */
UPDATE script_texts SET content_loc8='Мы с моим духом уничтожим вас!' WHERE entry=-1568035; /* content: I fight wit\' untamed spirit.... */
UPDATE script_texts SET content_loc8='Дух мой, вернись ко мне!' WHERE entry=-1568036; /* content: Spirit, come back to me! */
UPDATE script_texts SET content_loc8='Сейчас я тебя располосую!' WHERE entry=-1568037; /* content: Me gonna carve ya now! */
UPDATE script_texts SET content_loc8='Да я тебя на куски разрежу!' WHERE entry=-1568038; /* content: You gonna leave in pieces! */
UPDATE script_texts SET content_loc8='Чага... Чока-джинн.' WHERE entry=-1568042; /* content: Chaga... choka\'jinn. */
UPDATE script_texts SET content_loc8='Я жду вас, чужаки! Ваши смерти только сделают меня сильнее!' WHERE entry=-1568044; /* content: I be waitin, strangers. Your deaths gonna make me stronger! */
UPDATE script_texts SET content_loc8='Че ты там хотел сказать, а?' WHERE entry=-1568047; /* content: Dis a nightmare ya don\' wake up from! */
UPDATE script_texts SET content_loc8='Аззага чуууго зинн!' WHERE entry=-1568048; /* content: Azzaga choogo zinn! */
UPDATE script_texts SET content_loc8='Теперь вы принадлежите мне!' WHERE entry=-1568049; /* content: Your will belong ta me now! */
UPDATE script_texts SET content_loc8='Ваши души будут растерзаны!' WHERE entry=-1568051; /* content: Your soul gonna bleed! */
UPDATE script_texts SET content_loc8='Это ничего не меняет.' WHERE entry=-1568052; /* content: It not gonna make no difference. */
UPDATE script_texts SET content_loc8='Ваша смерть будет гораааздо страшнее!' WHERE entry=-1568053; /* content: You gonna die worse dan him. */
UPDATE script_texts SET content_loc8='Ха, мне все равно!' WHERE entry=-1568054; /* content: Dat no bodda me. */
UPDATE script_texts SET content_loc8='Сейчас-сейчас. Выучил вот пару новых фокусов... вместе с братишкой-медведем.' WHERE entry=-1568058; /* content: Got me some new tricks... like me brudda bear.... */
UPDATE script_texts SET content_loc8='Никому не спрятаться от орла!' WHERE entry=-1568059; /* content: Dere be no hidin\' from da eagle! */
UPDATE script_texts SET content_loc8='Знакомьтесь, мои новые братишки: клык и коготь!' WHERE entry=-1568060; /* content: Let me introduce you to me new bruddas: fang and claw! */
UPDATE script_texts SET content_loc8='Не нужно смотреть в небо, чтобы увидеть дракондора!' WHERE entry=-1568061; /* content: Ya don\' have to look to da sky to see da dragonhawk! */
UPDATE script_texts SET content_loc8='Огонь вас быстро убьет!' WHERE entry=-1568062; /* content: Fire kill you just as quick! */
UPDATE script_texts SET content_loc8='Сегодня духи попируют на славу! Начинайте церемонию, принесите пленников в жертву... освободите место для наших гостей!' WHERE entry=-1568068; /* content: Da spirits gonna feast today! Begin da ceremonies, sacrifice da prisoners... make room for our new guests! */
UPDATE script_texts SET content_loc8='Выбирайте, чужаки. Любой из моих жрецов будет рад вам помочь!' WHERE entry=-1568069; /* content: Take your pick, trespassers! Any of ma priests be happy to accommodate ya. */
UPDATE script_texts SET content_loc8='Не стесняйтесь. Ведь вы тут не первые гости... Те, кто нам служит, будут рады новичкам...' WHERE entry=-1568070; /* content: Don\'t be shy. Thousands have come before you. Ya not be alone in your service. */
UPDATE script_texts SET content_loc8='Вы потерпите неудачу, чужаки. Многие до вас пытались победить нас. И мы становились сильнее, убивая их.' WHERE entry=-1568071; /* content: Ya gonna fail, strangers. Many try before you, but dey only make us stronger! */
UPDATE script_texts SET content_loc8='Ваши усилия были напрасны. Ритуалы уже почти завершены.' WHERE entry=-1568072; /* content: Your efforts was in vain, trespassers. The rituals nearly be complete. */
UPDATE script_texts SET content_loc8='Скоро клетки опустеют. Всех узников принесут в жертву... а вы займете их места.' WHERE entry=-1568073; /* content: Soon da cages gonna be empty, da sacrifices be complete, and you gonna take dere places. */
UPDATE script_texts SET content_loc8='Время на исходе, чужаки. Скоро вы присоединитесь к душам тех, кого не смогли спасти.' WHERE entry=-1568074; /* content: Time be running low, strangers. Soon you gonna join da souls of dem ya failed to save. */
UPDATE script_texts SET content_loc8='Поторопитесь, жрецы! Нельзя позволить им прервать ритуалы!' WHERE entry=-1568075; /* content: Make haste, ma priests! Da rituals must not be interrupted! */
UPDATE script_texts SET content_loc8='Вы старались изо всех сил... Но теперь вы присоединитесь к тем, кто уже потерпел неудачу.' WHERE entry=-1568076; /* content: Ya make a good try... but now you gonna join da ones who already fall. */
UPDATE script_texts SET content_loc8='Смотрите же: каждое жертвоприношение укрепляет наши связи с миром духов. Скоро нас никто не сможет остановить!' WHERE entry=-1568078; /* content: Watch now. Every offering gonna strengthen our ties to da spirit world. Soon, we gonna be unstoppable! */
UPDATE script_texts SET content_loc8='Согласно моим подсчетам, если мы всей толпой ударим в гонг одновременно, печать на двери сломается, и мы сможем войти.' WHERE entry=-1568080; /* content: According to my calculations, if enough of us bang the gong at once the seal on these doors will break and we can enter. */
UPDATE script_texts SET content_loc8='%s постигает суть духа медведя!' WHERE entry=-1568082; /* content: %s absorbs the essence of the bear spirit! */
UPDATE script_texts SET content_loc8='%s впитывает сущность духа орла!' WHERE entry=-1568083; /* content: %s absorbs the essence of the eagle spirit! */
UPDATE script_texts SET content_loc8='%s поглощает сущность духа рыси!' WHERE entry=-1568084; /* content: %s absorbs the essence of the lynx spirit! */
UPDATE script_texts SET content_loc8='%s поглощает сущность духа дракондора!' WHERE entry=-1568085; /* content: %s absorbs the essence of the dragonhawk spirit! */
UPDATE script_texts SET content_loc8='Ваша кровь принадлежит мне!' WHERE entry=-1574000; /* content: Your blood is mine! */
UPDATE script_texts SET content_loc8='Не так быстро.' WHERE entry=-1574001; /* content: Not so fast. */
UPDATE script_texts SET content_loc8='Ночь... прими меня...' WHERE entry=-1574004; /* content: I join... the night. */
UPDATE script_texts SET content_loc8='Я раскрашу свое лицо вашей кровью!' WHERE entry=-1574005; /* content: I\'ll paint my face with your blood! */
UPDATE script_texts SET content_loc8='Моя жизнь... за бога смерти.' WHERE entry=-1574007; /* content: My life for the... death god! */
UPDATE script_texts SET content_loc8='Нет! Я смогу это сделать... я смогу...' WHERE entry=-1574008; /* content: No! I can do... better! I can... */
UPDATE script_texts SET content_loc8='Мюль орм агньёр!' WHERE entry=-1574009; /* content: Mjul orm agn gjor! */
UPDATE script_texts SET content_loc8='Я прирожденный воин!' WHERE entry=-1574010; /* content: I am a warrior born! */
UPDATE script_texts SET content_loc8='Далронн! Хватит ли у тебя смелости атаковать вместе со мной?' WHERE entry=-1574011; /* content: Dalronn! See if you can muster the nerve to join my attack! */
UPDATE script_texts SET content_loc8='Это... еще... не конец...' WHERE entry=-1574012; /* content: Not... over... yet. */
UPDATE script_texts SET content_loc8='Достойная... смерть.' WHERE entry=-1574013; /* content: A warrior\'s death. */
UPDATE script_texts SET content_loc8='Конечно, зачем напрягаться, пытаясь оценить обстановку, недоумок! Лучше сразу полезть на рожон!' WHERE entry=-1574016; /* content: By all means, don\'t assess the situation, you halfwit! Just jump into the fray! */
UPDATE script_texts SET content_loc8='До... скорой... встречи...' WHERE entry=-1574017; /* content: See... you... soon. */
UPDATE script_texts SET content_loc8='Славнее... нету... участи...' WHERE entry=-1574018; /* content: There\'s no... greater... glory. */
UPDATE script_texts SET content_loc8='Ты мне еще пригодишься.' WHERE entry=-1574019; /* content: You may serve me yet. */
UPDATE script_texts SET content_loc8='Скарвальд, некомпетентное ты ничтожество! Вернись и сделай хоть что-нибудь полезное!' WHERE entry=-1574020; /* content: Skarvald, you incompetent slug! Return and make yourself useful! */
UPDATE script_texts SET content_loc8='%s оглушительно ревет!' WHERE entry=-1574022; /* content: %s roars! */
UPDATE script_texts SET content_loc8='Мой господин! Я сделала, как вы велели, и теперь молю вас о благословении!' WHERE entry=-1575000; /* content: My liege! I have done as you asked, and now beseech you for your blessing! */
UPDATE script_texts SET content_loc8='Эти ощущения... невозможно передать. Приказывай, мой король.' WHERE entry=-1575002; /* content: The sensation is... beyond my imagining. I am yours to command, my king. */
UPDATE script_texts SET content_loc8='Твое первое испытание ждет. Уничтожь наших непрошенных гостей.' WHERE entry=-1575003; /* content: Your first test awaits you. Destroy our uninvited guests. */
UPDATE script_texts SET content_loc8='Я с радостью уничтожу их во имя тебя! Ну что ж, враги Плети! Я покажу вам всю силу Короля-лича!' WHERE entry=-1575004; /* content: I will be happy to slaughter them in your name! Come, enemies of the Scourge! I will show you the might of the Lich King! */
UPDATE script_texts SET content_loc8='Я порабощу ваши души!' WHERE entry=-1575005; /* content: I will vanquish your soul! */
UPDATE script_texts SET content_loc8='Только глупец мог бросить вызов власти Короля-лича!' WHERE entry=-1575006; /* content: You were a fool to challenge the power of the Lich King! */
UPDATE script_texts SET content_loc8='Конец неизбежен.' WHERE entry=-1575011; /* content: Your end is inevitable. */
UPDATE script_texts SET content_loc8='Йор-гуул мак!' WHERE entry=-1575012; /* content: Yor-guul mak! */
UPDATE script_texts SET content_loc8='Последнее желание?' WHERE entry=-1575013; /* content: Any last words? */
UPDATE script_texts SET content_loc8='Я не для того столько пережила... чтобы...' WHERE entry=-1575014; /* content: Nooo! I did not come this far... to... */
UPDATE script_texts SET content_loc8='Что это за... место? Я... уничтожу... вас!' WHERE entry=-1575015; /* content: What this place? I will destroy you! */
UPDATE script_texts SET content_loc8='Проще простого!' WHERE entry=-1575017; /* content: An easy task! */
UPDATE script_texts SET content_loc8='<Вы активируете переговорное устройство, и на нем появляется проекция дворфа. Это может быть только Бранн Бронзобород и никто иной.>

<Судя по его лицу, Бранн совсем не ожидал вас увидеть. Он принимается бешено жестикулировать.>' WHERE entry=-1575018; /* content:  */
UPDATE script_texts SET content_loc8='Хо, ну и где же теперь твоя дерзость?' WHERE entry=-1575026; /* content: Not so brash now, are you? */
UPDATE script_texts SET content_loc8='Ничтожные лакеи! Ваши трупы послужат хорошей закуской для моего нового дракона!' WHERE entry=-1575029; /* content: You motherless knaves! Your corpses will make fine morsels for my new drake! */
UPDATE script_texts SET content_loc8='Бьорн Черной Бури! Почти же меня своим присутствием!' WHERE entry=-1575032; /* content: Bjorn of the Black Storm! Honor me now with your presence! */
UPDATE script_texts SET content_loc8='Что... ждет меня... теперь?' WHERE entry=-1575040; /* content: What... awaits me... now? */
UPDATE script_texts SET content_loc8='%s глубоко вдыхает.' WHERE entry=-1575041; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc8='Меня на вас хватит!' WHERE entry=-1576002; /* content: There\'s plenty of me to go around. */
UPDATE script_texts SET content_loc8='Ну а теперь, покончим с этим!' WHERE entry=-1576003; /* content: Now to finish the job! */
UPDATE script_texts SET content_loc8='Фортуна... будь ты проклята!' WHERE entry=-1576005; /* content: Damn the... luck. */
UPDATE script_texts SET content_loc8='Хаос зовет.' WHERE entry=-1576006; /* content: Chaos beckons. */
UPDATE script_texts SET content_loc8='Реальность... расплетена.' WHERE entry=-1576007; /* content: Reality... unwoven. */
UPDATE script_texts SET content_loc8='Несокрушим!' WHERE entry=-1576008; /* content: Indestructible. */
UPDATE script_texts SET content_loc8='Конечно.' WHERE entry=-1576010; /* content: Of course. */
UPDATE script_texts SET content_loc8='Неет!' WHERE entry=-1576011; /* content: Noo! */
UPDATE script_texts SET content_loc8='Ааааа!' WHERE entry=-1576015; /* content: Aaggh! */
UPDATE script_texts SET content_loc8='Защищать? Зачем? Это неправильно. Нет, нет, нет... нужно только брать. Теперь я это понимаю!' WHERE entry=-1576016; /* content: Preserve? Why? There\'s no truth in it. No no no... only in the taking! I see that now! */
UPDATE script_texts SET content_loc8='Останьтесь. Насладитесь последними моментами своей жизни.' WHERE entry=-1576017; /* content: Stay. Enjoy your final moments. */
UPDATE script_texts SET content_loc8='Довольно! Хватит! Прикончите меня или, клянусь Королевой драконов, вы больше никогда не увидите дневного света!' WHERE entry=-1576018; /* content: Finish it! Finish it! Kill me, or I swear by the Dragonqueen you\'ll never see daylight again! */
UPDATE script_texts SET content_loc8='Ну вот мы и выяснили правду.' WHERE entry=-1576019; /* content: Now we\'ve come to the truth! */
UPDATE script_texts SET content_loc8='Королева драконов... Хранительница Жизни... защити... меня.' WHERE entry=-1576020; /* content: Dragonqueen... Life-Binder... preserve... me. */
UPDATE script_texts SET content_loc8='%s открывает хаотический разлом!' WHERE entry=-1576021; /* content: %s opens a Chaotic Rift! */
UPDATE script_texts SET content_loc8='Моих слуг постигла неудача... Возможно, потребуется еще одно предупреждение!' WHERE entry=-1578001; /* content: Clearly my pets failed. Perhaps another demonstration is in order. */
UPDATE script_texts SET content_loc8='Вы все еще сражаетесь. Все еще цепляетесь за свои ложные убеждения. Если выживете, встретимся в центральном круге!' WHERE entry=-1578002; /* content: Still you fight. Still you cling to misguided principles. If you survive, you\'ll find me in the center ring. */
UPDATE script_texts SET content_loc8='Несчастные слепые глупцы!' WHERE entry=-1578003; /* content: Poor blind fools! */
UPDATE script_texts SET content_loc8='Тень... лишь бледная тень... подлинного могущества Хранителя Магии!' WHERE entry=-1578004; /* content: A taste... just a small taste... of the Spell-Weaver\'s power! */
UPDATE script_texts SET content_loc8='Много неустойчивой энергии... но чтобы уничтожить вас, я готов рискнуть!' WHERE entry=-1578005; /* content: So much unstable energy... but worth the risk to destroy you! */
UPDATE script_texts SET content_loc8='Если бы вы только могли понять!' WHERE entry=-1578006; /* content: If only you understood! */
UPDATE script_texts SET content_loc8='Ну что, теперь тебе понятно? Или нет?' WHERE entry=-1578007; /* content: Now do you see? Do you?! */
UPDATE script_texts SET content_loc8='Сожалею, это было необходимо.' WHERE entry=-1578008; /* content: Unfortunate, but necessary. */
UPDATE script_texts SET content_loc8='Простаки! Вам и не ведомо, какие силы вы привели в действие. Поток энергии не прервется. Вас ждет полный разгром.' WHERE entry=-1578010; /* content: Simpletons! You cannot comprehend the forces you have set in motion. The ley line conduit will not be disrupted! Your defeat shall be absolute! */
UPDATE script_texts SET content_loc8='Нахальные невежды, вы все в небесах витаете! Сейчас я вас заземлю!' WHERE entry=-1578011; /* content: You brash interlopers are out of your element! I will ground you! */
UPDATE script_texts SET content_loc8='Потушить твой огонь – это мелочь!' WHERE entry=-1578013; /* content: It is trivial to extinguish your fire! */
UPDATE script_texts SET content_loc8='Никакая магия природы тебе уже не поможет!' WHERE entry=-1578014; /* content: No magic of nature will help you now! */
UPDATE script_texts SET content_loc8='Какая заносчивость... какая чванливость... Вы заслуживаете кары!' WHERE entry=-1578015; /* content: Such insolence... such arrogance... must be PUNISHED! */
UPDATE script_texts SET content_loc8='Назад, в землю, из которой ты $gвышел:вышла;!' WHERE entry=-1578017; /* content: Back to the earth with you! */
UPDATE script_texts SET content_loc8='Наслаждайся полетом!' WHERE entry=-1578018; /* content: Enjoy the fall! */
UPDATE script_texts SET content_loc8='Пощады не будет!' WHERE entry=-1578020; /* content: There will be no mercy! */
UPDATE script_texts SET content_loc8='Взорвать их! Изничтожить!' WHERE entry=-1578021; /* content: Blast them! Destroy them! */
UPDATE script_texts SET content_loc8='Пленных не брать! В атаку!' WHERE entry=-1578022; /* content: Take no prisoners! Attack! */
UPDATE script_texts SET content_loc8='Нанесите удар! Уничтожьте их!' WHERE entry=-1578023; /* content: Strike now! Obliterate them! */
UPDATE script_texts SET content_loc8='По мере того, как %s переходит в астральный мир, формируются аномалии.' WHERE entry=-1578024; /* content: Anomalies form as %s shifts into the Astral Plane! */
UPDATE script_texts SET content_loc8='%s начинает произносить заклинание усиленного чародейского взрыва!' WHERE entry=-1578025; /* content: %s begins to cast Empowered Arcane Explosion! */
UPDATE script_texts SET content_loc8='Я предупреждал тебя!' WHERE entry=-1578026; /* content: You were warned! */
UPDATE script_texts SET content_loc8='Окулус наш!' WHERE entry=-1578027; /* content: The Oculus is ours! */
UPDATE script_texts SET content_loc8='Они... слишком сильны. Я недооценил... их мужества.' WHERE entry=-1578028; /* content: They are... too strong! Underestimated their... fortitude. */
UPDATE script_texts SET content_loc8='%s призывает капитана лазурного кольца!' WHERE entry=-1578029; /* content: %s calls an Azure Ring Captain! */
UPDATE script_texts SET content_loc8='%s улетает.' WHERE entry=-1578030; /* content: %s flies away. */
UPDATE script_texts SET content_loc8='Я вас уничтожу!' WHERE entry=-1580001; /* content: I will purge you! */
UPDATE script_texts SET content_loc8='Вы еще не знаете, что такое боль!' WHERE entry=-1580002; /* content: Your pain has only begun! */
UPDATE script_texts SET content_loc8='Во имя Кил\'Джедена!' WHERE entry=-1580003; /* content: In the name of Kil\'jaeden! */
UPDATE script_texts SET content_loc8='Я предупреждал тебя!' WHERE entry=-1580004; /* content: You were warned! */
UPDATE script_texts SET content_loc8='Теперь я пробудился! И все вы погибнете!' WHERE entry=-1580005; /* content: My awakening is complete! You shall all perish! */
UPDATE script_texts SET content_loc8='Скорее! Пока я – это все еще я!' WHERE entry=-1580008; /* content: Hurry! There is not much of me left! */
UPDATE script_texts SET content_loc8='Я навечно в долгу перед тобой – как будет перед тобой в долгу весь мир, когда мы сумеем одолеть Кил\'джедена.' WHERE entry=-1580009; /* content: I am forever in your debt. Once we have triumphed over Kil\'jaeden, this entire world will be in your debt as well. */
UPDATE script_texts SET content_loc8='Приятно видеть ваши муки!' WHERE entry=-1580012; /* content: Your misery is my delight! */
UPDATE script_texts SET content_loc8='Я буду смотреть, как вы истекаете кровью.' WHERE entry=-1580013; /* content: I will watch you bleed! */
UPDATE script_texts SET content_loc8='Ты что, не слышал? Я всегда побеждаю!' WHERE entry=-1580015; /* content: Haven\'t you heard? I always win! */
UPDATE script_texts SET content_loc8='Надоело с вами возиться!' WHERE entry=-1580016; /* content: I have toyed with you long enough! */
UPDATE script_texts SET content_loc8='Жалкая ящерица! Ты найдешь здесь свою смерть!' WHERE entry=-1580017; /* content: Puny lizard! Death is the only answer you\'ll find here! */
UPDATE script_texts SET content_loc8='Я раздавлю вас!' WHERE entry=-1580019; /* content: I will crush you! */
UPDATE script_texts SET content_loc8='Хорошая разминка! А теперь я жду достойного противника!' WHERE entry=-1580020; /* content: That was fun, but I still await a true challenge! */
UPDATE script_texts SET content_loc8='Умри, насекомое!' WHERE entry=-1580023; /* content: Perish, insect! */
UPDATE script_texts SET content_loc8='Ты просто кусок мяса!' WHERE entry=-1580024; /* content: You are meat! */
UPDATE script_texts SET content_loc8='Проще простого!' WHERE entry=-1580025; /* content: Too easy! */
UPDATE script_texts SET content_loc8='Что ж, начинай бой!' WHERE entry=-1580026; /* content: Bring the fight to me! */
UPDATE script_texts SET content_loc8='Новый день, новая славная битва!' WHERE entry=-1580027; /* content: Another day, another glorious battle! */
UPDATE script_texts SET content_loc8='Я живу ради этого!' WHERE entry=-1580028; /* content: I live for this! */
UPDATE script_texts SET content_loc8='Постойте, друзья! Нам нужно кое-что узнать, прежде чем этот демон встретит свою смерть!' WHERE entry=-1580031; /* content: Hold, friends! There is information to be had before this devil meets his fate! */
UPDATE script_texts SET content_loc8='Где Анвина? Отвечай, демон! Что с Кейлеком?' WHERE entry=-1580032; /* content: Where is Anveena, demon? What has become of Kalec? */
UPDATE script_texts SET content_loc8='Ты расскажешь мне, что с ними случилось!' WHERE entry=-1580033; /* content: You will tell me where they are! */
UPDATE script_texts SET content_loc8='Слава Кил\'Джедену! Смерть его врагам!' WHERE entry=-1580036; /* content: Glory to Kil\'jaeden! Death to all who oppose! */
UPDATE script_texts SET content_loc8='Я несу смерть во имя господина!' WHERE entry=-1580037; /* content: I kill for the master! */
UPDATE script_texts SET content_loc8='Хаос...' WHERE entry=-1580049; /* content: Chaos... */
UPDATE script_texts SET content_loc8='Тень в помощь пламени!' WHERE entry=-1580052; /* content: Shadow to the aid of fire! */
UPDATE script_texts SET content_loc8='Алитесс! Твое пламя бушует во мне!' WHERE entry=-1580053; /* content: Alythess! Your fire burns within me! */
UPDATE script_texts SET content_loc8='И-нок Крайул!' WHERE entry=-1580055; /* content: Ee-nok Kryul! */
UPDATE script_texts SET content_loc8='Я... слабею.' WHERE entry=-1580056; /* content: I... fade. */
UPDATE script_texts SET content_loc8='Время – это роскошь, которой у тебя больше нет!' WHERE entry=-1580057; /* content: Time is a luxury you no longer possess! */
UPDATE script_texts SET content_loc8='Пламя в помощь тени!' WHERE entry=-1580058; /* content: Fire to the aid of shadow! */
UPDATE script_texts SET content_loc8='Сакролаш!' WHERE entry=-1580059; /* content: Sacrolash! */
UPDATE script_texts SET content_loc8='Удача отвернулась от вас!' WHERE entry=-1580063; /* content: Your luck has run its course! */
UPDATE script_texts SET content_loc8='Как долго я предвкушал этот момент!' WHERE entry=-1580064; /* content: All my plans have led to this! */
UPDATE script_texts SET content_loc8='Я ждал слишком долго!' WHERE entry=-1580066; /* content: I have waited long enough! */
UPDATE script_texts SET content_loc8='Если подведешь меня, будешь страдать вечно!' WHERE entry=-1580067; /* content: Fail me and suffer for eternity! */
UPDATE script_texts SET content_loc8='Вытягивайте силу из девчонки! Пусть от нее останется лишь безжизненная оболочка!' WHERE entry=-1580068; /* content: Drain the girl! Drain her power until there is nothing but a vacant shell! */
UPDATE script_texts SET content_loc8='ХАОС!' WHERE entry=-1580074; /* content: Chaos! */
UPDATE script_texts SET content_loc8='РАЗРУШЕНИЕ!' WHERE entry=-1580075; /* content: Destruction! */
UPDATE script_texts SET content_loc8='ЗАБВЕНИЕ!' WHERE entry=-1580076; /* content: Oblivion! */
UPDATE script_texts SET content_loc8='Никто не остановит меня! Этот мир падет!' WHERE entry=-1580077; /* content: I will not be denied! This world shall fall! */
UPDATE script_texts SET content_loc8='Ахх!.. Сила Солнечного Колодца... обратилась... против меня! Что вы наделали? Что вы наделали?!' WHERE entry=-1580079; /* content: Aggghh! The powers of the Sunwell... turn... against me! What have you done? What have you done??? */
UPDATE script_texts SET content_loc8='Теперь я повинуюсь только моему господину.' WHERE entry=-1580082; /* content: I serve only the Master now. */
UPDATE script_texts SET content_loc8='Но я... Я потерялась. Я не могу найти путь назад.' WHERE entry=-1580084; /* content: But I\'m... lost. I cannot find my way back. */
UPDATE script_texts SET content_loc8='Кейлек... Кейлек?' WHERE entry=-1580086; /* content: Kalec... Kalec? */
UPDATE script_texts SET content_loc8='Я наполнил энергией еще одну сферу! Быстрее используйте ее!' WHERE entry=-1580092; /* content: I have empowered another orb! Use it quickly! */
UPDATE script_texts SET content_loc8='Готова еще одна сфера! Торопитесь!' WHERE entry=-1580093; /* content: Another orb is ready! Make haste! */
UPDATE script_texts SET content_loc8='Я отдал все, что мог. Моя энергия в ваших руках!' WHERE entry=-1580094; /* content: I have channeled all I can! The power is in your hands! */
UPDATE script_texts SET content_loc8='Смертные герои – ваша победа была давно предсказана. Злобный крик моего поверженного брата будет услышан по всей вселенной, неся новую надежду тем, кто все еще не склонился перед Пылающим Легионом.' WHERE entry=-1580095; /* content: Mortal heroes - your victory here today was foretold long ago. My brother\'s anguished cry of defeat will echo across the universe - bringing renewed hope to all those who still stand against the Burning Crusade. */
UPDATE script_texts SET content_loc8='Энтропий, то существо, которое тебе пришлось уничтожить, некогда был благородным наару по имени М\'ууру. При жизни М\'ууру повелевал силами Света и Надежды. Нашлись заблудшие, которые пытались присвоить себе эти силы...' WHERE entry=-1580098; /* content: The creature Entropius, whom you were forced to destroy, was once the noble naaru, M\'uru. In life, M\'uru channeled vast energies of LIGHT and HOPE. For a time, a misguided few sought to steal those energies... */
UPDATE script_texts SET content_loc8='Мы были непростительно высокомерны. Мы предали проклятию благороднейшее из всех живущих существ. Не знаю, сможем ли мы искупить этот грех...' WHERE entry=-1580099; /* content: Our arrogance was unpardonable. We damned one of the most noble beings of all. We may never atone for this sin. */
UPDATE script_texts SET content_loc8='Благословенные предки!.. Я чувствую... столько любви... милосердия... Нет слов, чтобы описать это...' WHERE entry=-1580104; /* content: Blessed ancestors! I feel it... so much love... so much grace... there are... no words... impossible to describe... */
UPDATE script_texts SET content_loc8='Спасение, дитя мое; спасение ожидает всех нас.' WHERE entry=-1580105; /* content: Salvation, young one. It waits for us all. */
UPDATE script_texts SET content_loc8='%s глубоко вдыхает.' WHERE entry=-1580107; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc8='Я попусту теряю с вами время!' WHERE entry=-1585000; /* content: You only waste my time! */
UPDATE script_texts SET content_loc8='Мой голод неутолим!' WHERE entry=-1585001; /* content: My hunger knows no bounds! */
UPDATE script_texts SET content_loc8='Хватит размениваться на мелочи.' WHERE entry=-1585003; /* content: Enough distractions! */
UPDATE script_texts SET content_loc8='Я непобедим!' WHERE entry=-1585004; /* content: I am invincible! */
UPDATE script_texts SET content_loc8='Не... удержать.' WHERE entry=-1585008; /* content: Un...con...tainable. */
UPDATE script_texts SET content_loc8='Пог... лотить.' WHERE entry=-1585010; /* content: Con...sume. */
UPDATE script_texts SET content_loc8='Меня это начинает раздражать...' WHERE entry=-1585015; /* content: Now I\'m getting annoyed. */
UPDATE script_texts SET content_loc8='Это я называю удачным началом.' WHERE entry=-1585017; /* content: I call that a good start. */
UPDATE script_texts SET content_loc8='Один в поле не воин, да?' WHERE entry=-1585020; /* content: One is such a lonely number. */
UPDATE script_texts SET content_loc8='Неплохо повеселились, правда?' WHERE entry=-1585021; /* content: It\'s been a kick, really. */
UPDATE script_texts SET content_loc8='Месть жжет, как пламя!' WHERE entry=-1585024; /* content: Vengeance burns! */
UPDATE script_texts SET content_loc8='Все солдаты Лордерона должны немедленно явиться ко входу в Стратхольм и ожидать дальнейших приказов принца Артаса.' WHERE entry=-1595000; /* content: All soldiers of Lordaeron should immediately report to the entrance of Stratholme, and await further orders from Prince Arthas. */
UPDATE script_texts SET content_loc8='Ты хорошо $gпотрудился:потрудилась; над этими ящиками! Приходи ко мне к входу в Стратхольм за следующим поручением!' WHERE entry=-1595001; /* content: Good work with the crates! Come talk to me in front of Stratholme for your next assignment! */
UPDATE script_texts SET content_loc8='Силы Плети были замечены недалеко от Врат праздничной улицы!' WHERE entry=-1595003; /* content: Scourge forces have been spotted near the Festival Lane Gate! */
UPDATE script_texts SET content_loc8='У фонтана на Королевской площади замечены солдаты Плети!' WHERE entry=-1595004; /* content: Scourge forces have been spotted near the King\'s Square fountain! */
UPDATE script_texts SET content_loc8='Силы Плети были замечены возле Врат Торгового ряда!' WHERE entry=-1595005; /* content: Scourge forces have been spotted near the Market Row Gate! */
UPDATE script_texts SET content_loc8='У городской ратуши замечены солдаты Плети!' WHERE entry=-1595006; /* content: Scourge forces have been spotted near the Town Hall! */
UPDATE script_texts SET content_loc8='Силы Плети были замечены возле врат Площади Старейшины!' WHERE entry=-1595007; /* content: Scourge forces have been spotted near the Elder\'s Square Gate! */
UPDATE script_texts SET content_loc8='Герои, поспешите, встретимся у городской ратуши. Мы должны сразиться с Мал\'Ганисом на его территории!' WHERE entry=-1595008; /* content: Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis. */
UPDATE script_texts SET content_loc8='Идите за мной. Я знаю дорогу.' WHERE entry=-1595009; /* content: Follow me, I know the way through. */
UPDATE script_texts SET content_loc8='О, вы наконец-то прибыли, принц Артас. Вы едва успели.' WHERE entry=-1595010; /* content: Ah, you\'ve finally arrived Prince Arthas. You\'re here just in the nick of time. */
UPDATE script_texts SET content_loc8='Да, я рад, что я смог добраться сюда раньше чумы.' WHERE entry=-1595011; /* content: Yes, I\'m glad I could get to you before the plague. */
UPDATE script_texts SET content_loc8='Что это за магия?' WHERE entry=-1595012; /* content: What is this sorcery? */
UPDATE script_texts SET content_loc8='Тебе и не надо этого понимать, Артас. Все, что от тебя требуется – это умереть.' WHERE entry=-1595013; /* content: There\'s no need for you to understand, Arthas. All you need to do is die. */
UPDATE script_texts SET content_loc8='Кажется, у Мал\'Ганиса в распоряжении есть еще кое-что, кроме Плети. Давайте поспешим.' WHERE entry=-1595014; /* content: Mal\'Ganis appears to have more than Scourge in his arsenal. We should make haste. */
UPDATE script_texts SET content_loc8='Снова черная магия! Будьте готовы ко всему!' WHERE entry=-1595015; /* content: More vile sorcery! Be ready for anything! */
UPDATE script_texts SET content_loc8='Идем дальше.' WHERE entry=-1595016; /* content: Let\'s move on. */
UPDATE script_texts SET content_loc8='Будьте начеку. Нас окружили.' WHERE entry=-1595017; /* content: Watch your backs: they have us surrounded in this hall. */
UPDATE script_texts SET content_loc8='Мал\'Ганис не собирается облегчить нам жизнь.' WHERE entry=-1595018; /* content: Mal\'Ganis is not making this easy. */
UPDATE script_texts SET content_loc8='Они упрямы.' WHERE entry=-1595019; /* content: They\'re very persistent. */
UPDATE script_texts SET content_loc8='Что еще он поставит у меня на пути?' WHERE entry=-1595020; /* content: What else can he put in my way? */
UPDATE script_texts SET content_loc8='Принц Артас Менетил, в этот самый день могущественное зло поглотило твою душу. Смерть, которую ты должен был принести другим, сегодня придет за тобой.' WHERE entry=-1595021; /* content: Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own. */
UPDATE script_texts SET content_loc8='Я делаю для Лордерона то, что должен, и никакие слова и поступки меня не остановят.' WHERE entry=-1595022; /* content: I do what I must for Lordaeron, and neither your words nor your actions will stop me. */
UPDATE script_texts SET content_loc8='Кратчайший путь к Мал\'Ганису находится за тем книжным шкафом.' WHERE entry=-1595023; /* content: The quickest path to Mal\'Ganis lies behind that bookshelf ahead. */
UPDATE script_texts SET content_loc8='Это займет совсем немного времени.' WHERE entry=-1595024; /* content: This will only take a moment. */
UPDATE script_texts SET content_loc8='Слава Свету, что потайной ход еще работает.' WHERE entry=-1595025; /* content: I\'m relieved this secret passage still works. */
UPDATE script_texts SET content_loc8='Давайте пройдем этот участок как можно быстрее. Если мы не погибнем от нежити, то можем погибнуть от этого огня.' WHERE entry=-1595026; /* content: Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might. */
UPDATE script_texts SET content_loc8='Отдышитесь немного. Но имейте в виду, нам скоро снова в путь.' WHERE entry=-1595027; /* content: Rest a moment and clear your lungs, but we must move again soon. */
UPDATE script_texts SET content_loc8='Отдых окончен, надо идти. Мал\'Ганис ждет.' WHERE entry=-1595028; /* content: That\'s enough; we must move again. Mal\'Ganis awaits. */
UPDATE script_texts SET content_loc8='Наконец нам хоть как-то повезло. Огонь еще не добрался до Торгового ряда. Мал\'Ганис должен быть на Площади рыцарей, которая находится недалеко отсюда. Скажите мне, как будете готовы идти дальше.' WHERE entry=-1595029; /* content: At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward. */
UPDATE script_texts SET content_loc8='Да свершится правосудие.' WHERE entry=-1595030; /* content: Justice will be done. */
UPDATE script_texts SET content_loc8='Мы покончим с этим сейчас же, Мал\'Ганис. Один на один.' WHERE entry=-1595031; /* content: We\'re going to finish this right now, Mal\'Ganis. Just you... and me. */
UPDATE script_texts SET content_loc8='Твое путешествие начинается, юный принц. Собирай свои войска и отправляйся в царство вечных снегов, в Нордскол. Там мы и уладим все наши дела, там ты узнаешь свою судьбу.' WHERE entry=-1595032; /* content: Your journey has just begun, young prince. Gather your forces and meet me in the arctic land of Northrend. It is there that we shall settle the score between us. It is there that your true destiny will unfold. */
UPDATE script_texts SET content_loc8='Я отыщу тебя на краю земли! Ты слышишь меня? На краю земли!' WHERE entry=-1595033; /* content: I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth! */
UPDATE script_texts SET content_loc8='Вы славно сражались сегодня. Все, что Мал\'Ганис оставил тут – ваша награда. А мне нужно начинать готовиться к экспедиции в Нордскол.' WHERE entry=-1595034; /* content: You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend. */
UPDATE script_texts SET content_loc8='Защищайте своего принца, солдаты Лордерона! Мне нужна помощь!' WHERE entry=-1595035; /* content: Protect your prince, soldiers of Lordaeron! I am in need of aid! */
UPDATE script_texts SET content_loc8='Мал\'Ганис заплатит за это.' WHERE entry=-1595037; /* content: Mal\'Ganis will pay for this. */
UPDATE script_texts SET content_loc8='Подарить тебе жизнь будет слишком рискованно.' WHERE entry=-1595038; /* content: I can\'t afford to spare you. */
UPDATE script_texts SET content_loc8='Одной помехой меньше.' WHERE entry=-1595039; /* content: One less obstacle to deal with. */
UPDATE script_texts SET content_loc8='Плоть не сравнится с железом!' WHERE entry=-1599002; /* content: Flesh is no match for iron! */
UPDATE script_texts SET content_loc8='Армии железа заполонят этот мир!' WHERE entry=-1599003; /* content: Armies of iron will smother the world! */
UPDATE script_texts SET content_loc8='Нет, нет, нет.' WHERE entry=-1599006; /* content: Why must it be this way? */
UPDATE script_texts SET content_loc8='Нечистые мысли ведут к нечестивым поступкам.' WHERE entry=-1599007; /* content: You had it coming! */
UPDATE script_texts SET content_loc8='Потеряно так много времени... и вам его никогда не вернуть!' WHERE entry=-1599010; /* content: So much lost time... that you\'ll never get back! */
UPDATE script_texts SET content_loc8='Надеюсь, это того стоило.' WHERE entry=-1599019; /* content: I hope this is all worth it! */
UPDATE script_texts SET content_loc8='Пора получить ответы на кое-какие вопросы! Поехали!' WHERE entry=-1599020; /* content: Time to get some answers! Let\'s get this show on the road! */
UPDATE script_texts SET content_loc8='Что, во имя Мадорана, ЭТО было? О, погодите. Я почти понял...' WHERE entry=-1599023; /* content: What in the name o\' Madoran did THAT do? Oh! Wait: I just about got it... */
UPDATE script_texts SET content_loc8='Теперь будьте внимательны! Не успеете и глазом моргнуть, как...' WHERE entry=-1599026; /* content: Now keep an eye out! I\'ll have this licked in two shakes of a-- */
UPDATE script_texts SET content_loc8='Ой, это нехорошо. У нас могут возникнуть небольшие затруднения...' WHERE entry=-1599028; /* content: Oh, that doesn\'t sound good. We might have a complication or two... */
UPDATE script_texts SET content_loc8='Ага, значит, играем по-крупному, а? Обожаю такие игры!' WHERE entry=-1599030; /* content: Ah, you want to play hardball, eh? That\'s just my game! */
UPDATE script_texts SET content_loc8='Еще пара минут и я... ой.' WHERE entry=-1599031; /* content: Couple more minutes and I\'ll-- */
UPDATE script_texts SET content_loc8='Повышение? А хорошие новости есть?' WHERE entry=-1599033; /* content: Heightened? What\'s the good news? */
UPDATE script_texts SET content_loc8='Так вот в чем было дело! Теперь я двигаюсь в нужном направлении...' WHERE entry=-1599034; /* content: So that was the problem? Now I\'m makin\' progress... */
UPDATE script_texts SET content_loc8='Критическое значение уровня угрозы. Перенаправление анализа Бездны. Инициирование протокола очищения.' WHERE entry=-1599035; /* content: Critical threat index. Void analysis diverted. Initiating sanitization protocol. */
UPDATE script_texts SET content_loc8='Стойте! Никакого "очищения" не будет, пока я тут командую!' WHERE entry=-1599036; /* content: Hang on! Nobody\'s gonna\' be sanitized as long as I have a say in it! */
UPDATE script_texts SET content_loc8='Какие хранители?' WHERE entry=-1599052; /* content: What protectors? */
UPDATE script_texts SET content_loc8='Сто-оп! Эзир и Ванир стали воевать? Почему?' WHERE entry=-1599056; /* content: Hold everything! The Aesir and Vanir went to war? Why? */
UPDATE script_texts SET content_loc8='В основном, это верно.' WHERE entry=-1599059; /* content: Essentially that is correct. */
UPDATE script_texts SET content_loc8='Локен?! Час от часу не легче… Мы справились с железными дворфами, но готов поспорить, что рядом есть другая машина, выплевывающая железных врайкулов пачками.' WHERE entry=-1599062; /* content: Loken?! That\'s downright bothersome... We might\'ve neutralized the iron dwarves, but I\'d lay odds there\'s another machine somewhere else churnin\' out a whole mess o\' these iron vrykul! */
UPDATE script_texts SET content_loc8='Я думаю, настало время увидеть, что находится за стеной у входа. Я собираюсь проникнуть туда, тихо и незаметно. Ждите меня у двери, и я впущу внутрь нас всех.' WHERE entry=-1599064; /* content: I think it\'s time to see what\'s behind the door near the entrance. I\'m going to sneak over there, nice and quiet. Meet me at the door and I\'ll get us in. */
UPDATE script_texts SET content_loc8='Так голодно! Надо есть!' WHERE entry=-1600001; /* content: So hungry! Must feed! */
UPDATE script_texts SET content_loc8='Труп бдыжь!' WHERE entry=-1600003; /* content: Corpse go boom! */
UPDATE script_texts SET content_loc8='Ваши усилия... напрасны.' WHERE entry=-1600006; /* content: Your efforts... are in vain. */
UPDATE script_texts SET content_loc8='Защищайте меня! Быстрее, будьте вы прокляты!' WHERE entry=-1600008; /* content: Bolster my defenses! Hurry, curse you! */
UPDATE script_texts SET content_loc8='Неужели вы не понимаете всей бесполезности происходящего?' WHERE entry=-1600009; /* content: Surely you can see the futility of it all! */
UPDATE script_texts SET content_loc8='Сдавайся: пришла твоя смерть!' WHERE entry=-1600010; /* content: Just give up and die already! */
UPDATE script_texts SET content_loc8='%s зовет на помощь.' WHERE entry=-1600011; /* content: %s calls for assistance. */
UPDATE script_texts SET content_loc8='Тарон\'джа знает все! Смертным не удастся прервать вечную династию!' WHERE entry=-1600012; /* content: Tharon\'ja sees all! The work of mortals shall not end the eternal dynasty! */
UPDATE script_texts SET content_loc8='Теперь твоя плоть служит Тарон\'джа!' WHERE entry=-1600015; /* content: Your flesh serves Tharon\'ja now! */
UPDATE script_texts SET content_loc8='Тарон\'джа пригодится твоя смертная оболочка!' WHERE entry=-1600016; /* content: Tharon\'ja has a use for your mortal shell! */
UPDATE script_texts SET content_loc8='Нет! Этот вкус... слишком мало!' WHERE entry=-1600017; /* content: No! A taste... all too brief! */
UPDATE script_texts SET content_loc8='Прийти сюда было глупо с твоей стороны.' WHERE entry=-1601002; /* content: You were foolish to come. */
UPDATE script_texts SET content_loc8='По приказу Ануб\'арака!' WHERE entry=-1601003; /* content: As Anub\'Arak commands! */
UPDATE script_texts SET content_loc8='Мы должны защищать ворота. В атаку! Разорвите их на части!' WHERE entry=-1601005; /* content: We must hold the gate. Attack! Tear them limb from limb! */
UPDATE script_texts SET content_loc8='Ворота должны выстоять любой ценой. Растерзайте их в клочья!' WHERE entry=-1601006; /* content: The gate must be protected at all costs. Rip them to shreds! */
UPDATE script_texts SET content_loc8='Я слышу шаги. Будьте начеку!' WHERE entry=-1601008; /* content: I hear footsteps. Be on your guard. */
UPDATE script_texts SET content_loc8='Я чувствую живых. Приготовиться!' WHERE entry=-1601009; /* content: I sense the living. Be ready. */
UPDATE script_texts SET content_loc8='%s поднимается по тоннелю!' WHERE entry=-1601013; /* content: %s moves up the tunnel! */
UPDATE script_texts SET content_loc8='Вам предстоит вечная агония!' WHERE entry=-1601015; /* content: Eternal agony awaits you! */
UPDATE script_texts SET content_loc8='Твой выбор сделан.' WHERE entry=-1601017; /* content: You have made your choice. */
UPDATE script_texts SET content_loc8='Ауум на-л ак-к-к, ишшш!' WHERE entry=-1601020; /* content: Auum na-l ak-k-k-k, isshhh. */
UPDATE script_texts SET content_loc8='Ваша броня бессильна против моей саранчи!' WHERE entry=-1601021; /* content: Your armor is useless against my locusts! */
UPDATE script_texts SET content_loc8='Уунак-хиссс тик-к-к-к!' WHERE entry=-1601023; /* content: Uunak-hissss tik-k-k-k-k! */
UPDATE script_texts SET content_loc8='Враг преодолел ворота! Быстрее перебросьте туда силы и разберитесь с чужаками!' WHERE entry=-1601025; /* content: The gate has been breached! Quickly, divert forces to deal with these invaders! */
UPDATE script_texts SET content_loc8='У нас не остается времени! Всеми оставшимися силами атакуйте захватчиков!' WHERE entry=-1601026; /* content: There\'s no time left! All remaining forces, attack the invaders! */
UPDATE script_texts SET content_loc8='Я величайший из сыновей своего отца! Пришел ваш конец!' WHERE entry=-1602000; /* content: I am the greatest of my father\'s sons! Your end has come! */
UPDATE script_texts SET content_loc8='Плоть... слаба!' WHERE entry=-1602002; /* content: Flesh... is... weak! */
UPDATE script_texts SET content_loc8='...' WHERE entry=-1602003; /* content: ... */
UPDATE script_texts SET content_loc8='Это невозможно... Плоть не может быть сильнее!' WHERE entry=-1602004; /* content: How can it be...? Flesh is not... stronger! */
UPDATE script_texts SET content_loc8='Защищайтесь, и пусть вам повезет...' WHERE entry=-1602005; /* content: Defend yourself, for all the good it will do! */
UPDATE script_texts SET content_loc8='%s переходит в боевую стойку!' WHERE entry=-1602006; /* content: %s switches to Battle Stance! */
UPDATE script_texts SET content_loc8='АААААААА! Узрите же ярость железа и стали!' WHERE entry=-1602007; /* content: GRAAAAAH! Behold the fury of iron and steel! */
UPDATE script_texts SET content_loc8='%s переходит в стойку берсерка!' WHERE entry=-1602008; /* content: %s switches to Berserker Stance! */
UPDATE script_texts SET content_loc8='Покажите мне все, на что вы способны!' WHERE entry=-1602009; /* content: Give me your worst! */
UPDATE script_texts SET content_loc8='%s переходит в оборонительную стойку!' WHERE entry=-1602010; /* content: %s switches to Defensive Stance! */
UPDATE script_texts SET content_loc8='Господин... к вам гости!' WHERE entry=-1602015; /* content: Master... you have guests. */
UPDATE script_texts SET content_loc8='Малейшая искра – и тебе конец!' WHERE entry=-1602016; /* content: The slightest spark shall be your undoing. */
UPDATE script_texts SET content_loc8='Никто не в безопасности!' WHERE entry=-1602017; /* content: No one is safe! */
UPDATE script_texts SET content_loc8='Есть ли у вас надежда? Нет!' WHERE entry=-1602018; /* content: What hope is there for you? None! */
UPDATE script_texts SET content_loc8='Мой повелитель показал мне будущее, и для вас там нет места. Азерот переродится во тьме. Йогг-Сарон будет освобожден! Пантеон падет!' WHERE entry=-1602020; /* content: My master has shown me the future, and you have no place in it. Azeroth will be reborn in darkness. Yogg-Saron shall be released! The Pantheon shall fall! */
UPDATE script_texts SET content_loc8='Всего лишь смертное создание...' WHERE entry=-1602021; /* content: Only mortal... */
UPDATE script_texts SET content_loc8='Я... ВЕЧЕН!' WHERE entry=-1602022; /* content: I... am... FOREVER! */
UPDATE script_texts SET content_loc8='Все твое время потеряно напрасно!' WHERE entry=-1602023; /* content: What little time you had, you wasted! */
UPDATE script_texts SET content_loc8='Моя смерть... предвещает конец этого мира.' WHERE entry=-1602024; /* content: My death... heralds the end of this world. */
UPDATE script_texts SET content_loc8='От судьбы не спрячешься!' WHERE entry=-1602025; /* content: You cannot hide from fate! */
UPDATE script_texts SET content_loc8='Подойдите поближе. Я сделаю все быстро.' WHERE entry=-1602026; /* content: Come closer. I will make it quick. */
UPDATE script_texts SET content_loc8='Ваша плоть долго не продержится.' WHERE entry=-1602027; /* content: Your flesh cannot hold out for long. */
UPDATE script_texts SET content_loc8='Вы слепо смотрите в бездну!' WHERE entry=-1602028; /* content: You stare blindly into the abyss! */
UPDATE script_texts SET content_loc8='Ваша невежественность поражает. Неужели вы не видите конец этого пути?' WHERE entry=-1602029; /* content: Your ignorance is profound. Can you not see where this path leads? */
UPDATE script_texts SET content_loc8='Вы перешли границу забвения!' WHERE entry=-1602030; /* content: You cross the precipice of oblivion! */
UPDATE script_texts SET content_loc8='%s начинает произносить заклинание "Вспышка молний!"' WHERE entry=-1602031; /* content: %s begins to cast Lightning Nova! */
UPDATE script_texts SET content_loc8='Так это вы убили моих детей? Вы... заплатите!' WHERE entry=-1602032; /* content: It is you who have destroyed my children? You... shall... pay! */
UPDATE script_texts SET content_loc8='Армии из железа покорят всех!' WHERE entry=-1602033; /* content: The armies of iron will conquer all! */
UPDATE script_texts SET content_loc8='Из-за тебя я и так уже слишком многого не сделал!' WHERE entry=-1602035; /* content: You have cost me too much work! */
UPDATE script_texts SET content_loc8='Хозяин не зря... беспокоился.' WHERE entry=-1602036; /* content: The master was right... to be concerned. */
UPDATE script_texts SET content_loc8='Я растопчу вас своими сапогами!' WHERE entry=-1602037; /* content: I will crush you beneath my boots! */
UPDATE script_texts SET content_loc8='Вся моя работа... насмарку!' WHERE entry=-1602038; /* content: All my work... undone! */
UPDATE script_texts SET content_loc8='У нас безотходные процедуры. Вот увидите...' WHERE entry=-1602040; /* content: Nothing is wasted in the process. You will see.... */
UPDATE script_texts SET content_loc8='%s бежит к своей наковальне!' WHERE entry=-1602041; /* content: %s runs to his anvil! */
UPDATE script_texts SET content_loc8='%s собирается расколоть своих хрупких големов!' WHERE entry=-1602042; /* content: %s prepares to shatter his Brittle Golems! */
UPDATE script_texts SET content_loc8='Нужно защитить Оранжерею!' WHERE entry=-1603000; /* content: The Conservatory must be protected! */
UPDATE script_texts SET content_loc8='Эонар, твоей прислужнице нужна помощь!' WHERE entry=-1603002; /* content: Eonar, your servant requires aid! */
UPDATE script_texts SET content_loc8='Помогите мне, дети мои!' WHERE entry=-1603003; /* content: Children, assist me! */
UPDATE script_texts SET content_loc8='Вас захлестнет сила стихий!' WHERE entry=-1603004; /* content: The swarm of the elements shall overtake you! */
UPDATE script_texts SET content_loc8='Прости меня.' WHERE entry=-1603005; /* content: Forgive me. */
UPDATE script_texts SET content_loc8='Твоя смерть даст начало новой жизни!' WHERE entry=-1603006; /* content: From your death springs life anew! */
UPDATE script_texts SET content_loc8='Он больше не властен надо мной. Мой взор снова ясен. Благодарю вас, герои.' WHERE entry=-1603007; /* content: His hold on me dissipates. I can see clearly once more. Thank you, heroes. */
UPDATE script_texts SET content_loc8='Эонар, прошу тебя, даруй свое благословение!' WHERE entry=-1603009; /* content: Eonar, your servant calls for your blessing! */
UPDATE script_texts SET content_loc8='Появились союзники природы!' WHERE entry=-1603010; /* content: Allies of Nature have appeared! */
UPDATE script_texts SET content_loc8='%s падает на землю и начинает восстанавливаться.' WHERE entry=-1603011; /* content: The %s withers into the earth and begins to regenerate. */
UPDATE script_texts SET content_loc8='Как пожелаешь, $n.' WHERE entry=-1603012; /* content: As you wish, $N. */
UPDATE script_texts SET content_loc8='Используй меня повторно' WHERE entry=-1603013; /* content: REUSE ME */
UPDATE script_texts SET content_loc8='Госпожа, враг прорвался в Оранжерею!' WHERE entry=-1603014; /* content: Matron, the Conservatory has been breached! */
UPDATE script_texts SET content_loc8='На удобрение.' WHERE entry=-1603015; /* content: Fertilizer. */
UPDATE script_texts SET content_loc8='Госпожа, я умираю.' WHERE entry=-1603017; /* content: Matron, one has fallen! */
UPDATE script_texts SET content_loc8='Смертным здесь не место!' WHERE entry=-1603018; /* content: Mortals have no place here! */
UPDATE script_texts SET content_loc8='Отправляйся восвояси!' WHERE entry=-1603019; /* content: I return you whence you came! */
UPDATE script_texts SET content_loc8='Исчезни!' WHERE entry=-1603020; /* content: BEGONE! */
UPDATE script_texts SET content_loc8='Фрейя! Они идут за тобой.' WHERE entry=-1603021; /* content: Freya! They come for you. */
UPDATE script_texts SET content_loc8='Это место станет вашей могилой.' WHERE entry=-1603022; /* content: This place will serve as your graveyard. */
UPDATE script_texts SET content_loc8='Госпожа, беги! Они беспощадны...' WHERE entry=-1603025; /* content: Matron, flee! They are ruthless.... */
UPDATE script_texts SET content_loc8='Дерзкие глупцы! Ваша кровь закалит оружие, которым был завоеван этот мир!' WHERE entry=-1603026; /* content: Insolent whelps! Your blood will temper the weapons used to reclaim this world! */
UPDATE script_texts SET content_loc8='Да поглотит вас пламя преисподней!' WHERE entry=-1603027; /* content: Let the inferno consume you! */
UPDATE script_texts SET content_loc8='Пройдите очищение огнем!' WHERE entry=-1603029; /* content: I will burn away your impurities! */
UPDATE script_texts SET content_loc8='ПОКОНЧИМ С ЭТИМ!' WHERE entry=-1603033; /* content: Let it be finished! */
UPDATE script_texts SET content_loc8='Я... Проиграл.' WHERE entry=-1603034; /* content: I. Have. Failed. */
UPDATE script_texts SET content_loc8='Дайте время подготовить пушки.' WHERE entry=-1603037; /* content: Give us a moment to prepare to build the turrets. */
UPDATE script_texts SET content_loc8='Не спать! Скоро покажутся буровые машины этих отвратительных железных дворфов!' WHERE entry=-1603038; /* content: Be on the lookout! Mole machines will be surfacing soon with those nasty Iron dwarves aboard! */
UPDATE script_texts SET content_loc8='Готовы выходить, охраняйте тылы от дворфов!' WHERE entry=-1603039; /* content: Ready to move out, keep those dwarves off of our backs! */
UPDATE script_texts SET content_loc8='Быстрее! Сейчас она снова взлетит!' WHERE entry=-1603040; /* content: Move quickly! She won\'t remain grounded for long! */
UPDATE script_texts SET content_loc8='Огонь прекратился! Надо починить пушки!' WHERE entry=-1603042; /* content: Fires out! Let\'s rebuild those turrets! */
UPDATE script_texts SET content_loc8='Гарпунная пушка готова!' WHERE entry=-1603043; /* content: Harpoon Turret is ready for use! */
UPDATE script_texts SET content_loc8='Новые игрушки? Для меня? Обещаю, в этот раз я их не поломаю!' WHERE entry=-1603045; /* content: New toys? For me? I promise I won\'t break them this time! */
UPDATE script_texts SET content_loc8='Кажется, я сломал эту игрушку.' WHERE entry=-1603046; /* content: I... I think I broke it. */
UPDATE script_texts SET content_loc8='Наверное, в эту сторону голова не гнулась...' WHERE entry=-1603047; /* content: I guess it doesn\'t bend that way. */
UPDATE script_texts SET content_loc8='Какие скучные игрушки. Надоело играть!' WHERE entry=-1603048; /* content: I\'m tired of these toys. I don\'t want to play anymore! */
UPDATE script_texts SET content_loc8='Я так устал. Отдохну чуток...' WHERE entry=-1603051; /* content: So tired. I will rest for just a moment! */
UPDATE script_texts SET content_loc8='Продолжаем игру!' WHERE entry=-1603052; /* content: I\'m ready to play! */
UPDATE script_texts SET content_loc8='НЕТ! НЕТ! НЕТ! НЕТ! НЕТ!' WHERE entry=-1603053; /* content: NO! NO! NO! NO! NO! */
UPDATE script_texts SET content_loc8='Сердце |3-1(%s) обнажено и истекает энергией.' WHERE entry=-1603054; /* content: %s\'s heart is exposed and leaking energy. */
UPDATE script_texts SET content_loc8='Замрите и узрите свет!' WHERE entry=-1603057; /* content: Stand still and stare into the light! */
UPDATE script_texts SET content_loc8='Вас ждет бездна безумия!' WHERE entry=-1603059; /* content: You rush headlong into the maw of madness! */
UPDATE script_texts SET content_loc8='Другие позавидуют твоей участи!' WHERE entry=-1603060; /* content: A merciful kill! */
UPDATE script_texts SET content_loc8='ХА!' WHERE entry=-1603061; /* content: HAH! */
UPDATE script_texts SET content_loc8='Заседание Железного Собрания объявляется закрытым!' WHERE entry=-1603062; /* content: This meeting of the Assembly of Iron is adjourned! */
UPDATE script_texts SET content_loc8='И застелют небо грозовые тучи, и прольются смертоносным ливнем!' WHERE entry=-1603063; /* content: Let the storm clouds rise and rain down death from above! */
UPDATE script_texts SET content_loc8='Расшифруйте вот это!' WHERE entry=-1603067; /* content: Decipher this! */
UPDATE script_texts SET content_loc8='Смерть – это расплата за твое высокомерие.' WHERE entry=-1603070; /* content: Death is the price of your arrogance. */
UPDATE script_texts SET content_loc8='Заседание Железного Собрания объявляется закрытым!' WHERE entry=-1603071; /* content: This meeting of the Assembly of Iron is adjourned! */
UPDATE script_texts SET content_loc8='Чужаки! Вам не одолеть Железное Собрание!' WHERE entry=-1603072; /* content: You will not defeat the Assembly of Iron so easily, invaders! */
UPDATE script_texts SET content_loc8='Мое поражение лишь приблизит вашу погибель.' WHERE entry=-1603073; /* content: My death only serves to hasten your demise. */
UPDATE script_texts SET content_loc8='Невозможный!' WHERE entry=-1603074; /* content: Impossible! */
UPDATE script_texts SET content_loc8='Смертные слабы и немощны!' WHERE entry=-1603075; /* content: So fragile and weak! */
UPDATE script_texts SET content_loc8='Плоть... это пройденный этап.' WHERE entry=-1603076; /* content: Flesh... such a hindrance. */
UPDATE script_texts SET content_loc8='Желаете знать, что скрывает Ульдуар? Я покажу вам!' WHERE entry=-1603077; /* content: You seek the secrets of Ulduar? Then take them! */
UPDATE script_texts SET content_loc8='Заседание Железного Собрания объявляется закрытым!' WHERE entry=-1603078; /* content: This meeting of the Assembly of Iron is adjourned! */
UPDATE script_texts SET content_loc8='Вы зря сюда заявились!' WHERE entry=-1603079; /* content: Some things are better left alone! */
UPDATE script_texts SET content_loc8='Бежать некуда.' WHERE entry=-1603080; /* content: There is no escape! */
UPDATE script_texts SET content_loc8='Вы попусту тратите мое время!' WHERE entry=-1603082; /* content: You waste my time! */
UPDATE script_texts SET content_loc8='Вы будете наказаны за это вторжение!' WHERE entry=-1603086; /* content: You will suffer for this trespass! */
UPDATE script_texts SET content_loc8='Как трагично. Проделать такой путь – чтобы умереть...' WHERE entry=-1603087; /* content: Tragic. To come so far, only to fail. */
UPDATE script_texts SET content_loc8='Отправляйся туда, где вечная зима.' WHERE entry=-1603088; /* content: Welcome to the endless winter. */
UPDATE script_texts SET content_loc8='Да поглотят вас северные ветра!' WHERE entry=-1603089; /* content: Winds of the north consume you! */
UPDATE script_texts SET content_loc8='Наконец-то я... свободен от его оков...' WHERE entry=-1603091; /* content: I... I am released from his grasp... at last. */
UPDATE script_texts SET content_loc8='НУ ХВАТИТ! Больше вам не жить!' WHERE entry=-1603092; /* content: Enough! This ends now! */
UPDATE script_texts SET content_loc8='Герои, вас защитит покров зимы!' WHERE entry=-1603093; /* content: The veil of winter will protect you, champions! */
UPDATE script_texts SET content_loc8='Ваша смерть возвестит новую эру страданий!' WHERE entry=-1603096; /* content: Your destruction will herald a new age of suffering! */
UPDATE script_texts SET content_loc8='Неповиновение... это слабость смертных.' WHERE entry=-1603098; /* content: Defiance... a flaw of mortality. */
UPDATE script_texts SET content_loc8='Во мне течет черная кровь Йогг-Сарона! Ме-ня не ос-та-но-вить!' WHERE entry=-1603099; /* content: The black blood of Yogg-Saron courses through me! I. AM. UNSTOPPABLE! */
UPDATE script_texts SET content_loc8='Какие ужасы вас ожидают...' WHERE entry=-1603100; /* content: Oh, what horrors await.... */
UPDATE script_texts SET content_loc8='Поблизости начинают возникать саронитовые испарения!' WHERE entry=-1603103; /* content: A cloud of saronite vapors coalesces nearby! */
UPDATE script_texts SET content_loc8='Генерал Везакс окружен аурой темной мощи, а его рык сотрясает землю!' WHERE entry=-1603104; /* content: General Vezax roars and surges with dark might! */
UPDATE script_texts SET content_loc8='Саронитовые испарения яростно клубятся и струятся, принимая пугающую форму!' WHERE entry=-1603105; /* content: The saronite vapors mass and swirl violently, merging into a monstrous form! */
UPDATE script_texts SET content_loc8='Перемещение завершено. Запуск планетарного анализа Азерота.' WHERE entry=-1603106; /* content: Translocation complete. Commencing planetary analysis of Azeroth. */
UPDATE script_texts SET content_loc8='Назад, смертные. Я здесь не для того, чтобы сражаться с вами.' WHERE entry=-1603107; /* content: Stand back, mortals. I\'m not here to fight you. */
UPDATE script_texts SET content_loc8='Для блага вселенной эту планету необходимо пересоздать. Я запускаю системный анализ, чтобы выявить зоны поражения. Не вмешивайтесь.' WHERE entry=-1603108; /* content: It is in the universe\'s best interest to re-originate this planet should my analysis find systemic corruption. Do not interfere. */
UPDATE script_texts SET content_loc8='Я делаю то, что должен.' WHERE entry=-1603112; /* content: I do what I must. */
UPDATE script_texts SET content_loc8='Узрите чудо созидания!' WHERE entry=-1603115; /* content: Behold the tools of creation! */
UPDATE script_texts SET content_loc8='БЕРЕГИСЬ!' WHERE entry=-1603116; /* content: Beware! */
UPDATE script_texts SET content_loc8='Прощайте, смертные. Я восхищаюсь вашей отвагой, несовершенные существа.' WHERE entry=-1603120; /* content: Farewell, mortals. Your bravery is admirable, for such flawed creatures. */
UPDATE script_texts SET content_loc8='Я видел миры, охваченные пламенем Творцов. Их жители гибли, не успев издать ни звука. Я был свидетелем того, как галактики рождались и умирали в мгновение ока. И все время я оставался холодным... и безразличным. Я. Не чувствовал. Ничего. Триллионы загубленных судеб. Неужели все они были подобны вам? Неужели все они так же любили жизнь?' WHERE entry=-1603121; /* content: I have seen worlds bathed in the Makers\' flames, their denizens fading without as much as a whimper. Entire planetary systems born and razed in the time that it takes your mortal hearts to beat once. Yet all throughout, my own heart devoid of emotion... of empathy. I. Have. Felt. Nothing. A million-million lives wasted. Had they all held within them your tenacity? Had they all loved life as you do? */
UPDATE script_texts SET content_loc8='Похоже, ваша сила кроется именно в несовершенстве. Дарованная вам свобода воли позволяет пренебрегать законами космоса... и выходить победителями тогда, когда идеальные создания титанов оказываются бессильны.' WHERE entry=-1603122; /* content: Perhaps it is your imperfections... that which grants you free will... that allows you to persevere against all cosmically calculated odds. You prevail where the Titan\'s own perfect creations have failed. */
UPDATE script_texts SET content_loc8='Я изменил код отклика: ваша планета будет спасена. Я больше не могу полагаться на свои расчеты.' WHERE entry=-1603123; /* content: I\'ve rearranged the reply code - your planet will be spared. I cannot be certain of my own calculations anymore. */
UPDATE script_texts SET content_loc8='У меня нет сил, чтобы передать сигнал. Поторопитесь... найдите источник энергии... ищите в небесах.' WHERE entry=-1603124; /* content: I lack the strength to transmit the signal. You must... hurry... find a place of power... close to the skies. */
UPDATE script_texts SET content_loc8='Пусть моя судьба не тревожит тебя, Бронзен. Если сигнал не будет передан вовремя, пересоздание планеты запустится. Спасай... свой мир...' WHERE entry=-1603125; /* content: Do not worry about my fate, Bronzen. If the signal is not transmitted in time, re-origination will proceed regardless. Save... your world... */
UPDATE script_texts SET content_loc8='Никто не пройдет!' WHERE entry=-1603126; /* content: None shall pass! */
UPDATE script_texts SET content_loc8='ЗАБВЕНИЕ!' WHERE entry=-1603127; /* content: Oblivion! */
UPDATE script_texts SET content_loc8='Я лишу тебя жизни!' WHERE entry=-1603128; /* content: I will squeeze the life from you! */
UPDATE script_texts SET content_loc8='Царапина...' WHERE entry=-1603129; /* content: Just a scratch! */
UPDATE script_texts SET content_loc8='Всего лишь плоть!' WHERE entry=-1603130; /* content: Only a flesh wound! */
UPDATE script_texts SET content_loc8='КОЛ-ТАРИШ!' WHERE entry=-1603131; /* content: KOL-THARISH! */
UPDATE script_texts SET content_loc8='ТЕБЕ КОНЕЦ!' WHERE entry=-1603132; /* content: YOU FAIL! */
UPDATE script_texts SET content_loc8='Я непобедим!' WHERE entry=-1603133; /* content: I am invincible! */
UPDATE script_texts SET content_loc8='Правая рука отросла заново!' WHERE entry=-1603135; /* content: The Right Arm has regrown! */
UPDATE script_texts SET content_loc8='Левая рука отросла заново!' WHERE entry=-1603136; /* content: The Left Arm has regrown! */
UPDATE script_texts SET content_loc8='Кологарн применяет каменную хватку!' WHERE entry=-1603137; /* content: Kologarn casts Stone Grip! */
UPDATE script_texts SET content_loc8='Не отступать! Уничтожить их!' WHERE entry=-1603141; /* content: Do not hold back! Destroy them! */
UPDATE script_texts SET content_loc8='Мое терпение лопнуло!' WHERE entry=-1603146; /* content: My patience has reached its limit! */
UPDATE script_texts SET content_loc8='Придержите мечи! Я сдаюсь.' WHERE entry=-1603148; /* content: Stay your arms! I yield! */
UPDATE script_texts SET content_loc8='Кошмар, терзавший меня, развеялся, но Тьма еще не отступила от этих мест.' WHERE entry=-1603149; /* content: I feel as though I am awakening from a nightmare, but the shadows in this place yet linger. */
UPDATE script_texts SET content_loc8='Мне нужно все осознать... Если потребуется, я встану на вашу сторону. Я обязан вам куда большим. Удачи.' WHERE entry=-1603151; /* content: I need time to reflect.... I will aid your cause if you should require it. I owe you at least that much. Farewell. */
UPDATE script_texts SET content_loc8='Общий системный сбой. Отказ защитных протоколов. Боевая единица "Левиафан" завершает свою работу.' WHERE entry=-1603161; /* content: Total systems failure. Defense protocols breached. Leviathan Unit shutting down. */
UPDATE script_texts SET content_loc8='Повторный анализ угрозы завершен. Оценка цели проведена. Смена курса.' WHERE entry=-1603162; /* content: Threat re-evaluated. Target assessment complete. Changing course. */
UPDATE script_texts SET content_loc8='Изменение цели преследования. Смена курса.' WHERE entry=-1603163; /* content: Pursuit objective modified. Changing course. */
UPDATE script_texts SET content_loc8='Несанкционированное вмешательство в работу. Активация системы подавления живой силы.' WHERE entry=-1603165; /* content: Unauthorized entity attempting circuit overload. Activating anti-personnel countermeasures. */
UPDATE script_texts SET content_loc8='Функциональный сбой. Активация резервных систем.' WHERE entry=-1603166; /* content: System malfunction. Diverting power to support systems. */
UPDATE script_texts SET content_loc8='Система орбитального подавления включена.' WHERE entry=-1603169; /* content: Orbital countermeasures enabled. */
UPDATE script_texts SET content_loc8='Врача!' WHERE entry=-1603180; /* content: MEDIC! */
UPDATE script_texts SET content_loc8='ПРЕВОСХОДНО! Просто восхитительный результат! Целостность обшивки – 98,9 процента! Почти что ни царапинки! Продолжаем!' WHERE entry=-1603182; /* content: WONDERFUL! Positively marvelous results! Hull integrity at 98.9 percent! Barely a dent! Moving right along. */
UPDATE script_texts SET content_loc8='Представляю вам противопехотную штурмовую пушку VX-001! Советую поискать укрытие.' WHERE entry=-1603183; /* content: Behold the VX-001 Anti-personnel Assault Cannon! You might want to take cover. */
UPDATE script_texts SET content_loc8='Надо запомнить: пушка крайне эффективна против созданий из плоти и крови.' WHERE entry=-1603185; /* content: Note to self: Cannon highly effective against flesh. */
UPDATE script_texts SET content_loc8='Красота, правда? Я называю это непревзойденной воздушной боевой единицей!' WHERE entry=-1603187; /* content: Isn\'t it beautiful? I call it the magnificent aerial command unit! */
UPDATE script_texts SET content_loc8='Обхитрил!' WHERE entry=-1603188; /* content: Outplayed! */
UPDATE script_texts SET content_loc8='Я знаю, ты можешь лучше!' WHERE entry=-1603189; /* content: You can do better than that! */
UPDATE script_texts SET content_loc8='Прогноз: негативный!' WHERE entry=-1603192; /* content: Prognosis: Negative! */
UPDATE script_texts SET content_loc8='От этого тебе не оправиться, дружок.' WHERE entry=-1603193; /* content: You\'re not going to get up from that one, friend. */
UPDATE script_texts SET content_loc8='Очевидно, я совершил небольшую ошибку в расчетах. Пленный злодей затуманил мой разум и заставил меня отклониться от инструкций. Сейчас все системы в норме. Конец связи.' WHERE entry=-1603194; /* content: It would appear that I\'ve made a slight miscalculation. I allowed my mind to be corrupted by the fiend in the prison, overriding my primary directive. All systems seem to be functional now. Clear. */
UPDATE script_texts SET content_loc8='Боевая матрица расширена. Какая невероятная скорость!' WHERE entry=-1603195; /* content: Combat matrix enhanced. Behold wonderous rapidity! */
UPDATE script_texts SET content_loc8='%s применяет "Взрыв плазмы"!' WHERE entry=-1603196; /* content: %s begins to cast Plasma Blast! */
UPDATE script_texts SET content_loc8='Что вам нужно? Прочь от меня!' WHERE entry=-1603198; /* content: What do you want from me? Leave me alone! */
UPDATE script_texts SET content_loc8='Скоро мы сразимся с главарем этих извергов! Обратите гнев и ненависть против его прислужников!' WHERE entry=-1603199; /* content: The time to strike at the head of the beast will soon be upon us! Focus your anger and hatred on his minions! */
UPDATE script_texts SET content_loc8='Да! ДА! Не щадите их! Бейте без промаха!' WHERE entry=-1603201; /* content: Yes! YES! Show them no mercy! Give no pause to your attacks! */
UPDATE script_texts SET content_loc8='Пусть ненависть и ярость направляют ваши удары!' WHERE entry=-1603202; /* content: Let hatred and rage guide your blows! */
UPDATE script_texts SET content_loc8='Был ли у них шанс на спасение?' WHERE entry=-1603203; /* content: Could they have been saved? */
UPDATE script_texts SET content_loc8='Я – это сон наяву.' WHERE entry=-1603206; /* content: I am the lucid dream. */
UPDATE script_texts SET content_loc8='Трепещите, смертные, конец близок!' WHERE entry=-1603207; /* content: Tremble, mortals, before the coming of the end! */
UPDATE script_texts SET content_loc8='Задохнитесь в собственной ненависти!' WHERE entry=-1603208; /* content: Suffocate upon your own hate! */
UPDATE script_texts SET content_loc8='БЕЗУМИЕ ПОГЛОТИТ ВАС!' WHERE entry=-1603209; /* content: MADNESS WILL CONSUME YOU! */
UPDATE script_texts SET content_loc8='%s готовится вызвать Сгущение тьмы!' WHERE entry=-1603211; /* content: %s prepares to unleash Empowering Shadows! */
UPDATE script_texts SET content_loc8='Впереди вечные страдания!' WHERE entry=-1603212; /* content: Eternal suffering awaits! */
UPDATE script_texts SET content_loc8='Вы обречены. Ни вы, никто другой НЕ В СИЛАХ избежать близящегося конца света. Уульви ифис халас гаг эрх\'онг ушшш!' WHERE entry=-1603213; /* content: Your fate is sealed. The end of days is finally upon you and ALL who inhabit this miserable little seedling. Uulwi ifis halahs gag erh\'ongg w\'ssh. */
UPDATE script_texts SET content_loc8='Твоя стойкость достойна восхищения.' WHERE entry=-1603216; /* content: Your resilience is admirable. */
UPDATE script_texts SET content_loc8='А-ааа!' WHERE entry=-1603217; /* content: Arrrrrrgh! */
UPDATE script_texts SET content_loc8='Я не боюсь тебя!' WHERE entry=-1603218; /* content: I\'m not afraid of you! */
UPDATE script_texts SET content_loc8='Ирр н\'лайет... шуул анаг!' WHERE entry=-1603220; /* content: Yrr n\'lyeth... shuul anagg! */
UPDATE script_texts SET content_loc8='Он поймет... ни один король не правит вечно, вечно лишь небытие!' WHERE entry=-1603221; /* content: He will learn... no king rules forever; only death is eternal! */
UPDATE script_texts SET content_loc8='Это страшное свечение... Откуда оно?' WHERE entry=-1603223; /* content: That terrible glow... should that be? */
UPDATE script_texts SET content_loc8='Да свершится предначертанное.' WHERE entry=-1603224; /* content: For it to be as it must, yes. */
UPDATE script_texts SET content_loc8='Это оружие, равного которому не существует. И существовать не может.' WHERE entry=-1603225; /* content: It is a weapon like no other. It must be like no other. */
UPDATE script_texts SET content_loc8='К наступлению ночи Гул\'дан приведет своих чернокнижников. До тех пор клан Черной горы будет пытаться взять приступом восточную стену.' WHERE entry=-1603228; /* content: Gul\'dan is bringing up his warlocks by nightfall. Until then, the Blackrock clan will be trying to take the Eastern Wall. */
UPDATE script_texts SET content_loc8='Или одно убийство.' WHERE entry=-1603230; /* content: or one murder. */
UPDATE script_texts SET content_loc8='Предводители орков согласны с тобой.' WHERE entry=-1603232; /* content: The orc leaders agree with your assessment. */
UPDATE script_texts SET content_loc8='Иллюзия разрушена и путь в центральную комнату открыт!' WHERE entry=-1603235; /* content: The illusion shatters and a path to the central chamber opens! */
UPDATE script_texts SET content_loc8='Сердце |3-1(%s) вырвано из тела.' WHERE entry=-1603236; /* content: %s\'s heart is severed from his body. */
