UPDATE script_texts SET content_loc8='%s начинает закипать, едва коснувшись |3-1($n)!' WHERE entry=-1603261; /* content: %s begins to boil upon touching $n! */
UPDATE script_texts SET content_loc8='Чудовище из ваших кошмаров.' WHERE entry=-1603262; /* content: The monster in your nightmares. */
UPDATE script_texts SET content_loc8='Демон с тысячью лиц.' WHERE entry=-1603263; /* content: The fiend of a thousand faces. */
UPDATE script_texts SET content_loc8='Трепещите, ибо я обретаю истинную форму.' WHERE entry=-1603264; /* content: Cower before my true form. */
UPDATE script_texts SET content_loc8='ПАДИТЕ НИЦ ПЕРЕД БОГОМ СМЕРТИ!' WHERE entry=-1603265; /* content: BOW DOWN BEFORE THE GOD OF DEATH! */
UPDATE script_texts SET content_loc8='%s широко открывает рот!' WHERE entry=-1603266; /* content: %s opens his mouth wide! */
UPDATE script_texts SET content_loc8='Кланы объединились под предводительством Чернорука. Они будут биться бок о бок, пока Штормград не падет.' WHERE entry=-1603267; /* content: The clans are united under Blackhand in this assault. They will stand together until Stormwind has fallen. */
UPDATE script_texts SET content_loc8='%s начинает произносить заклинание "Ядовитая звезда"!' WHERE entry=-1604007; /* content: %s begins to cast Poison Nova! */
UPDATE script_texts SET content_loc8='Приготовьтесь к чему-то... БОЛЬШОМУ!' WHERE entry=-1604013; /* content: Get ready for somethin\'... much... BIGGAH! */
UPDATE script_texts SET content_loc8='%s начинает превращаться!' WHERE entry=-1604018; /* content: %s begins to transform! */
UPDATE script_texts SET content_loc8='Я вытащу ваши кишки, твари!' WHERE entry=-1604019; /* content: I\'m gonna spill your guts, mon! */
UPDATE script_texts SET content_loc8='Хотите увидеть cилу? Я покажу вам... силу!' WHERE entry=-1604021; /* content: You wanna see power? I\'m gonna show you power! */
UPDATE script_texts SET content_loc8='Топчи их! Пронзай их!' WHERE entry=-1604022; /* content: Gut them! Impale them! */
UPDATE script_texts SET content_loc8='Всех убей, один останься!' WHERE entry=-1604023; /* content: Kill them all! */
UPDATE script_texts SET content_loc8='Познакомьтесь с моим большим другом!' WHERE entry=-1604024; /* content: Say hello to my BIG friend! */
UPDATE script_texts SET content_loc8='Что за спешка?' WHERE entry=-1604025; /* content: What a rush! */
UPDATE script_texts SET content_loc8='А я тебе говорил!' WHERE entry=-1604027; /* content: I told ya so! */
UPDATE script_texts SET content_loc8='Даже сильнейшие... могут пасть.' WHERE entry=-1604028; /* content: Even the mighty... can fall. */
UPDATE script_texts SET content_loc8='%s превращается в мамонта!' WHERE entry=-1604029; /* content: %s transforms into a Mammoth! */
UPDATE script_texts SET content_loc8='$n $gпронзен:пронзена;!' WHERE entry=-1604030; /* content: $N is impaled! */
UPDATE script_texts SET content_loc8='Я запираю дверь. Удачи вам и спасибо.' WHERE entry=-1608001; /* content: I\'m locking the door. Good luck, and thank you for doing this. */
UPDATE script_texts SET content_loc8='Теперь дверь удерживает лишь половина печати! Продолжай сражаться!' WHERE entry=-1608003; /* content: Only half of the door seal\'s strength remains! You must fight on! */
UPDATE script_texts SET content_loc8='Дверь вот-вот рухнет! Если синие драконы прорвутся сюда, все будет потеряно!' WHERE entry=-1608004; /* content: The door seal is about to collapse! All is lost if the Blue Dragonflight breaks through the door! */
UPDATE script_texts SET content_loc8='Страж портала защищает новый портал!' WHERE entry=-1608005; /* content: A Portal Guardian defends the new portal! */
UPDATE script_texts SET content_loc8='Из портала появляется элитный отряд синих драконов!' WHERE entry=-1608006; /* content: An elite Blue Dragonflight squad appears from the portal! */
UPDATE script_texts SET content_loc8='Теперь могу--мм--лететь. Кра-аак... Не найти нас--екк-екк! Бежим!' WHERE entry=-1608008; /* content: Free to--mm--fly now. Ra-aak... Not find us--ekh-ekh! Escape! */
UPDATE script_texts SET content_loc8='Я... сама... ярость!' WHERE entry=-1608009; /* content: I... am fury... unrestrained! */
UPDATE script_texts SET content_loc8='Я... снова жив!' WHERE entry=-1608011; /* content: I am... renewed. */
UPDATE script_texts SET content_loc8='Не -- крра -- вздумай -- карр-карр -- бежать!' WHERE entry=-1608012; /* content: Not--caww--get in way of--rrak-rrak--flee! */
UPDATE script_texts SET content_loc8='Мой – каааррр! – фаворит! Аак аак аак! Крраа-кар!' WHERE entry=-1608013; /* content: My---raaak--favorite! Awk awk awk! Raa-kaa! */
UPDATE script_texts SET content_loc8='Прочь с дороги, смертные!' WHERE entry=-1608019; /* content: Stand aside, mortals! */
UPDATE script_texts SET content_loc8='Меня не остановить!' WHERE entry=-1608020; /* content: I will not be contained! Ngyah!! */
UPDATE script_texts SET content_loc8='Вода может принять любую форму, занять любое пространство... смести любую преграду.' WHERE entry=-1608021; /* content: Water can hold any form, take any shape... overcome any obstacle. */
UPDATE script_texts SET content_loc8='Я – сила природы!' WHERE entry=-1608022; /* content: I am a force of nature! */
UPDATE script_texts SET content_loc8='Я смету все!' WHERE entry=-1608023; /* content: I shall pass! */
UPDATE script_texts SET content_loc8='Я... отступаю.' WHERE entry=-1608026; /* content: I... recede. */
UPDATE script_texts SET content_loc8='Поверить не могу! Тебе удалось отразить нападение синих драконов и победить их предводителя. Это потрясающе!' WHERE entry=-1608027; /* content: You did it! You held the Blue Dragonflight back and defeated their commander. Amazing work! */
UPDATE script_texts SET content_loc8='Защитная сфера |3-1(%s) дает трещину и лопается!' WHERE entry=-1608028; /* content: %s\'s Protective Bubble shatters! */
UPDATE script_texts SET content_loc8='Это было твоей ужасной ошибкой, чудовище!' WHERE entry=-1609000; /* content: You have made a grave error, fiend! */
UPDATE script_texts SET content_loc8='Когда-то я была воином Света... И посмотри, что теперь со мной стало...' WHERE entry=-1609001; /* content: I was a soldier of the Light once... Look at what I have become... */
UPDATE script_texts SET content_loc8='Ты безнадежно $gпроиграл:проиграла;, |3-6($r).' WHERE entry=-1609002; /* content: You are hopelessly outmatched, $R. */
UPDATE script_texts SET content_loc8='Ты ведь позволишь мне взять оружие и доспехи?' WHERE entry=-1609004; /* content: You will allow me a weapon and armor, yes? */
UPDATE script_texts SET content_loc8='Я завоюю свободу и покину это проклятое место!' WHERE entry=-1609005; /* content: I will win my freedom and leave this cursed place! */
UPDATE script_texts SET content_loc8='Я разорю это змеиное гнездо!' WHERE entry=-1609006; /* content: I will dismantle this festering hellhole! */
UPDATE script_texts SET content_loc8='Остаться должен только один!' WHERE entry=-1609007; /* content: There can be only one survivor! */
UPDATE script_texts SET content_loc8='Пусть твои страхи пожрут тебя!' WHERE entry=-1609008; /* content: Let your fears consume you! */
UPDATE script_texts SET content_loc8='Ха! Да ты клинок-то еле держишь! Тебя ждет скорая гибель.' WHERE entry=-1609009; /* content: HAH! You can barely hold a blade! Yours will be a quick death. */
UPDATE script_texts SET content_loc8='В атаку!' WHERE entry=-1609011; /* content: To battle! */
UPDATE script_texts SET content_loc8='Надежды нет...' WHERE entry=-1609012; /* content: There is no hope for our future... */
UPDATE script_texts SET content_loc8='Все! Конец!' WHERE entry=-1609014; /* content: It ends here! */
UPDATE script_texts SET content_loc8='Единственное исцеление – смерть!' WHERE entry=-1609015; /* content: Death is the only cure! */
UPDATE script_texts SET content_loc8='Никаких зелий!' WHERE entry=-1609016; /* content: No potions! */
UPDATE script_texts SET content_loc8='Запомни этот день, $n! Именно сегодня ты узнаешь, что такое тотальный разгром.' WHERE entry=-1609017; /* content: Remember this day, $n, for it is the day that you will be thoroughly owned. */
UPDATE script_texts SET content_loc8='Я вырву тебе сердце!' WHERE entry=-1609018; /* content: I\'m going to tear your heart out, cupcake! */
UPDATE script_texts SET content_loc8='Не смеши меня.' WHERE entry=-1609019; /* content: Don\'t make me laugh. */
UPDATE script_texts SET content_loc8='Настало время плакать...' WHERE entry=-1609020; /* content: Here come the tears... */
UPDATE script_texts SET content_loc8='Ты бросаешь вызов самой смерти!' WHERE entry=-1609021; /* content: You have challenged death itself! */
UPDATE script_texts SET content_loc8='Сегодня Король-лич увидит своего истинного воина!' WHERE entry=-1609022; /* content: The Lich King will see his true champion on this day! */
UPDATE script_texts SET content_loc8='Ты проигрываешь!' WHERE entry=-1609023; /* content: You\'re going down! */
UPDATE script_texts SET content_loc8='Ты $gпришел:пришла; добить меня?' WHERE entry=-1609025; /* content: Come to finish the job, have you? */
UPDATE script_texts SET content_loc8='$gПришел:Пришла; добить меня, да?' WHERE entry=-1609026; /* content: Come to finish the job, have ye? */
UPDATE script_texts SET content_loc8='Ты $gпришел:пришла; добить меня, друг?' WHERE entry=-1609027; /* content: Come ta finish da job, mon? */
UPDATE script_texts SET content_loc8='Смотри мне в глаза, когда будешь...' WHERE entry=-1609028; /* content: You\'ll look me in the eyes when... */
UPDATE script_texts SET content_loc8='Да, верная дочь Стальгорна не прочь бы...' WHERE entry=-1609029; /* content: Well this son o\' Ironforge would like... */
UPDATE script_texts SET content_loc8='Забавно, не правда ли? Погибнуть...' WHERE entry=-1609030; /* content: Ironic, isn\'t it? To be killed... */
UPDATE script_texts SET content_loc8='Если позволишь мне лишь одно...' WHERE entry=-1609031; /* content: If you\'d allow me just one... */
UPDATE script_texts SET content_loc8='Хотелось бы мне подняться...' WHERE entry=-1609032; /* content: I\'d like to stand for... */
UPDATE script_texts SET content_loc8='Я хочу умереть, как подобает орку...' WHERE entry=-1609033; /* content: I want to die like an orc... */
UPDATE script_texts SET content_loc8='Тролль примет свою...' WHERE entry=-1609034; /* content: Dis troll gonna stand for da... */
UPDATE script_texts SET content_loc8='$n?' WHERE entry=-1609035; /* content: $N? */
UPDATE script_texts SET content_loc8='$n? Эй, $n!' WHERE entry=-1609036; /* content: $N? Mon? */
UPDATE script_texts SET content_loc8='Я узнаю твое лицо из тысячи, $n! Что... что они с тобой сделали?' WHERE entry=-1609037; /* content: $N, I\'d recognize that face anywhere... What... What have they done to you, $N? */
UPDATE script_texts SET content_loc8='$n! Эти щупальца я узнал бы из тысячи... Что... что они с тобой сделали, $n?' WHERE entry=-1609038; /* content: $N, I\'d recognize those face tentacles anywhere... What... What have they done to you, $N? */
UPDATE script_texts SET content_loc8='О нет, $n, меня не обманешь... Что... $n, что они с тобой сделали?' WHERE entry=-1609040; /* content: $N, I\'d recognize that decay anywhere... What... What have they done to you, $N? */
UPDATE script_texts SET content_loc8='$n, я узнала бы твои клыки из тысячи... Что... что они с тобой сделали, $gдружище:подруга;?' WHERE entry=-1609042; /* content: $N, I\'d recognize dem tusks anywhere... What... What have dey done ta you, mon? */
UPDATE script_texts SET content_loc8='Не помнишь меня, да? Проклятая Плеть... Они изгнали все светлое и праведное из твоей души. До последней капли... Все, что делало тебя дренеем!' WHERE entry=-1609043; /* content: You don\'t remember me, do you? Blasted Scourge... They\'ve tried to drain you of everything that made you a righteous force of reckoning. Every last ounce of good... Everything that made you a draenei! */
UPDATE script_texts SET content_loc8='А помнишь, $n, помнишь Гномреган – каким он был, пока его не захватили эти проклятые трогги! Вспомни блеск тангенциального вращателя, $gбрат:сестра;! Ты когда-то $gбыл лучшим:была лучшей; из гномов! Это – не ты!' WHERE entry=-1609046; /* content: You don\'t remember me, do you? Blasted Scourge... They\'ve tried to drain you of everything that made you a pint-sized force of reckoning. Every last ounce of good... Everything that made you a gnome! */
UPDATE script_texts SET content_loc8='Ты меня не помнишь, да? Проклятая Плеть... Они изгнали все светлое и праведное из твоей души. До последней капли... Все то, что делало тебя тауреном!' WHERE entry=-1609050; /* content: You don\'t remember me, do you? Blasted Scourge... They\'ve tried to drain you of everything that made you a righteous force of reckoning. Every last ounce of good... Everything that made you a tauren! */
UPDATE script_texts SET content_loc8='Слушай меня, $n. Стряхни оковы, опутывающие твой разум! Король-лич – это чудовище, стремящееся разрушить весь мир – наш с тобой мир! Не позволяй ему использовать тебя в своих целях! Некогда ты $gбыл:была; героем – стань им снова! Борись! Борись с его властью!' WHERE entry=-1609062; /* content: Listen to me, $N. You must fight against the Lich King\'s control. He is a monster that wants to see this world - our world - in ruin. Don\'t let him use you to accomplish his goals. You were once a hero and you can be again. Fight, damn you! Fight his control! */
UPDATE script_texts SET content_loc8='Что там происходит? Почему так долго, $n?' WHERE entry=-1609066; /* content: What\'s going on in there? What\'s taking so long, $N? */
UPDATE script_texts SET content_loc8='Мое... мое время вышло. Все кончено. Добей меня, $n. Решайся же, или они убьют нас обоих. $n... помни об Аргусе. Не допусти, чтобы с этим миром случилось то же самое.' WHERE entry=-1609068; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Argus. Don\'t let that happen to this world. */
UPDATE script_texts SET content_loc8='Мое... мое время вышло. Все кончено. Добей меня, $n. Решайся, или они убьют нас обоих. $n... помни о Тирисфале! Этот мир достоин спасения!' WHERE entry=-1609070; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Tirisfal! This world is worth saving! */
UPDATE script_texts SET content_loc8='Мое... мое время вышло. Все кончено. Добей меня, $n. Иначе они убьют нас обоих. $nИ... помни о Гномрегане! Этот мир заслуживает спасения.' WHERE entry=-1609071; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Gnomeregan! This world is worth saving. */
UPDATE script_texts SET content_loc8='Мое... мое время вышло. Все кончено. Добей меня, $n. Давай же, или они убьют нас обоих. $nИ... помни о Тельдрассиле. Этот мир достоин спасения.' WHERE entry=-1609073; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Teldrassil, our beloved home. This world is worth saving. */
UPDATE script_texts SET content_loc8='Мое... мое время вышло. Все кончено. Добей меня, $n. Давай же, или они убьют нас обоих. $nЗа Орду! Этот мир достоин спасения.' WHERE entry=-1609074; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... For the Horde! This world is worth saving. */
UPDATE script_texts SET content_loc8='Мое... мое время вышло. Все кончено. Добей меня, $n. Давай, или они убьют нас обоих. $nПомни о Мулгоре... Этот мир достоин спасения.' WHERE entry=-1609075; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Mulgore. This world is worth saving. */
UPDATE script_texts SET content_loc8='Давай же, $n! Положи конец моим страданиям!' WHERE entry=-1609077; /* content: Do it, $N! Put me out of my misery! */
UPDATE script_texts SET content_loc8='Мне понадобится мой рунический клинок и доспехи... Нужно еще немного времени.' WHERE entry=-1609079; /* content: I\'ll need to get my runeblade and armor... Just need a little more time. */
UPDATE script_texts SET content_loc8='Я все еще слаб, но сил моих должно хватить на поддержание антимагического барьера. Он защитит тебя от вражеских заклинаний.' WHERE entry=-1609080; /* content: I\'m still weak, but I think I can get an anti-magic barrier up. Stay inside it or you\'ll be destroyed by their spells. */
UPDATE script_texts SET content_loc8='Но для поддержания барьера мне нужно максимально сконцентрироваться. Убей их всех!' WHERE entry=-1609081; /* content: Maintaining this barrier will require all of my concentration. Kill them all! */
UPDATE script_texts SET content_loc8='Они наступают. Обороняйся! Не выходи за пределы защитного поля! Без него они разорвут тебя на куски!' WHERE entry=-1609082; /* content: There are more coming. Defend yourself! Don\'t fall out of the anti-magic field! They\'ll tear you apart without its protection! */
UPDATE script_texts SET content_loc8='Идет верховный инквизитор! Приготовься, рыцарь смерти! Не дай ему выманить себя за пределы антимагического барьера! Убей его и отруби ему голову!' WHERE entry=-1609084; /* content: The High Inquisitor comes! Be ready, death knight! Do not let him draw you out of the protective bounds of my anti-magic field! Kill him and take his head! */
UPDATE script_texts SET content_loc8='Не выходи за пределы антимагического барьера! Подпусти их поближе!' WHERE entry=-1609085; /* content: Stay in the anti-magic field! Make them come to you! */
UPDATE script_texts SET content_loc8='Смерть верховного инквизитора из Нового Авалона не останется незамеченной. Тебе надо спасаться! Беги, пока здесь не появился кто-нибудь еще. За меня не беспокойся.' WHERE entry=-1609086; /* content: The death of the High Inquisitor of New Avalon will not go unnoticed. You need to get out of here at once! Go, before more of them show up. I\'ll be fine on my own. */
UPDATE script_texts SET content_loc8='Я отвлеку их на себя, а ты беги.' WHERE entry=-1609087; /* content: I\'ll draw their fire, you make your escape behind me. */
UPDATE script_texts SET content_loc8='Рыцари! Ваш верховный инквизитор – просто безвольный кусок мяса! Никто не устоит против сил Плети!' WHERE entry=-1609088; /* content: Your High Inquisitor is nothing more than a pile of meat, Crusaders! There are none beyond the grasp of the Scourge! */
UPDATE script_texts SET content_loc8='Мамочка?' WHERE entry=-1609091; /* content: Mommy? */
UPDATE script_texts SET content_loc8='ДАЙТЕ МНЕ МОЗГИ!' WHERE entry=-1609092; /* content: GIVE ME BRAINS! */
UPDATE script_texts SET content_loc8='Мне нужно есть...' WHERE entry=-1609093; /* content: Must feed... */
UPDATE script_texts SET content_loc8='Я голоден...' WHERE entry=-1609094; /* content: So hungry... */
UPDATE script_texts SET content_loc8='Или этот вурдалак залезет в яму, или получит плетей!' WHERE entry=-1609096; /* content: It puts the ghoul in the pit or else it gets the lash! */
UPDATE script_texts SET content_loc8='%s выгибается, как бы приглашая вас сесть себе на спину.' WHERE entry=-1609097; /* content: %s rears up, beckoning you to ride it. */
UPDATE script_texts SET content_loc8='Впечатляет, рыцарь смерти. Возвращайся ко мне в мир живых за своей наградой.' WHERE entry=-1609098; /* content: Impressive, death knight. Return to me in the world of the living for your reward. */
UPDATE script_texts SET content_loc8='Воины Плети, соберитесь! Приготовьтесь выплеснуть свою ярость на Серебряный Рассвет!' WHERE entry=-1609201; /* content: Soldiers of the Scourge, stand ready! Prepare to unleash your fury upon the Argent Dawn! */
UPDATE script_texts SET content_loc8='Рыцари смерти Акеруса, смертный поход начался!' WHERE entry=-1609203; /* content: Death knights of Acherus, the death march begins! */
UPDATE script_texts SET content_loc8='Воины Плети, рыцари смерти Акеруса, слуги Тьмы: услышьте зов верховного лорда!' WHERE entry=-1609204; /* content: Soldiers of the Scourge, death knights of Acherus, minions of the darkness: hear the call of the Highlord! */
UPDATE script_texts SET content_loc8='ШИПЫ!' WHERE entry=-1609205; /* content: RISE! */
UPDATE script_texts SET content_loc8='Небеса станут красными от крови павших! Король-лич взирает на нас, своих слуг! Оставляйте за собой лишь пепел и страдание!' WHERE entry=-1609206; /* content: The skies turn red with the blood of the fallen! The Lich King watches over us, minions! Leave only ashes and misery in your destructive wake! */
UPDATE script_texts SET content_loc8='Армия Плети приближается!' WHERE entry=-1609207; /* content: Scourge armies approach! */
UPDATE script_texts SET content_loc8='Готовьтесь, братья и сестры! За Свет!' WHERE entry=-1609208; /* content: Stand fast, brothers and sisters! The Light will prevail! */
UPDATE script_texts SET content_loc8='Склонитесь перед верховным лордом!' WHERE entry=-1609209; /* content: Kneel before the Highlord! */
UPDATE script_texts SET content_loc8='У вас нет шанса!' WHERE entry=-1609210; /* content: You stand no chance! */
UPDATE script_texts SET content_loc8='Плеть разгромит эту часовню!' WHERE entry=-1609211; /* content: The Scourge will destroy this place! */
UPDATE script_texts SET content_loc8='Ты поплатишься жизнью.' WHERE entry=-1609212; /* content: Your life is forfeit. */
UPDATE script_texts SET content_loc8='Сколько еще продержатся ваши войска?' WHERE entry=-1609214; /* content: How much longer will your forces hold out? */
UPDATE script_texts SET content_loc8='Не щадите никого!' WHERE entry=-1609216; /* content: Spare no one! */
UPDATE script_texts SET content_loc8='Что это? Я... не могу нанести удар...' WHERE entry=-1609217; /* content: What is this?! My... I cannot strike... */
UPDATE script_texts SET content_loc8='ПОВИНУЙСЯ МНЕ, МЕЧ!' WHERE entry=-1609218; /* content: Obey me, blade! */
UPDATE script_texts SET content_loc8='Ты сделаешь так, как я скажу! Я тут командую!' WHERE entry=-1609219; /* content: You will do as I command! I am in control here! */
UPDATE script_texts SET content_loc8='Что со мной?' WHERE entry=-1609221; /* content: What is happening to me? */
UPDATE script_texts SET content_loc8='Испепелитель отвергает меня...' WHERE entry=-1609223; /* content: Ashbringer defies me... */
UPDATE script_texts SET content_loc8='Слуги, на помощь!' WHERE entry=-1609224; /* content: Minions, come to my aid! */
UPDATE script_texts SET content_loc8='Дарион, тебе не победить!' WHERE entry=-1609225; /* content: You cannot win, Darion! */
UPDATE script_texts SET content_loc8='Привести их к часовне!' WHERE entry=-1609226; /* content: Bring them before the chapel! */
UPDATE script_texts SET content_loc8='То, что ты чувствуешь сейчас – это муки тысячи загубленных душ! Душ, которые ты и твой господин завлекли сюда. Свет воздаст тебе по заслугам, Дарион!' WHERE entry=-1609230; /* content: What you are feeling right now is the anguish of a thousand lost souls! Souls that you and your master brought here! The Light will tear you apart, Darion! */
UPDATE script_texts SET content_loc8='Цени каждый вздох, старик. Он может стать последним.' WHERE entry=-1609231; /* content: Save your breath, old man. It might be the last you ever draw. */
UPDATE script_texts SET content_loc8='Мой сын! Мой дорогой, милый мальчик!' WHERE entry=-1609232; /* content: My son! My dear, beautiful boy! */
UPDATE script_texts SET content_loc8='Отец!' WHERE entry=-1609233; /* content: Father! */
UPDATE script_texts SET content_loc8='Отец, ты вернулся!' WHERE entry=-1609235; /* content: Father, you have returned! */
UPDATE script_texts SET content_loc8='Тебя так долго не было, отец. Я думал...' WHERE entry=-1609236; /* content: You have been gone a long time, father. I thought... */
UPDATE script_texts SET content_loc8='Ничто не могло помешать мне быть здесь, Дарион. Тут мой дом и семья.' WHERE entry=-1609237; /* content: Nothing could have kept me away from here, Darion. Not from my home and family. */
UPDATE script_texts SET content_loc8='Отец, я хочу сражаться вместе с тобой против нежити. Я жажду битв! Я не могу больше оставаться безучастным!' WHERE entry=-1609238; /* content: Father, I wish to join you in the war against the undead. I want to fight! I can sit idle no longer! */
UPDATE script_texts SET content_loc8='Не забывай...' WHERE entry=-1609242; /* content: Do not forget... */
UPDATE script_texts SET content_loc8='Трогательно...' WHERE entry=-1609243; /* content: Touching... */
UPDATE script_texts SET content_loc8='Жалкое зрелище...' WHERE entry=-1609246; /* content: Pathetic... */
UPDATE script_texts SET content_loc8='Ты гнусное чудовище, Артас!' WHERE entry=-1609247; /* content: You\'re a damned monster, Arthas! */
UPDATE script_texts SET content_loc8='Ты прав, Фордринг. Я действительно послал их на верную смерть. Их жизни ничего не стоят, но вот твоя...' WHERE entry=-1609248; /* content: You were right, Fordring. I did send them in to die. Their lives are meaningless, but yours... */
UPDATE script_texts SET content_loc8='В атаку!!!' WHERE entry=-1609250; /* content: ATTACK!!! */
UPDATE script_texts SET content_loc8='КОНЕЦ СВЕТА!' WHERE entry=-1609251; /* content: APOCALYPSE! */
UPDATE script_texts SET content_loc8='Этот день еще не настал...' WHERE entry=-1609252; /* content: That day is not today... */
UPDATE script_texts SET content_loc8='Тирион!' WHERE entry=-1609253; /* content: Tirion! */
UPDATE script_texts SET content_loc8='АРТАС!!!' WHERE entry=-1609254; /* content: ARTHAS!!!! */
UPDATE script_texts SET content_loc8='Что это?' WHERE entry=-1609255; /* content: What is this? */
UPDATE script_texts SET content_loc8='Конец тебе.' WHERE entry=-1609256; /* content: Your end. */
UPDATE script_texts SET content_loc8='Невозможно...' WHERE entry=-1609257; /* content: Impossible... */
UPDATE script_texts SET content_loc8='Братья и сестры, сегодня я даю вам обет: Король-лич будет повержен! В этот день я объявляю о заключении союза.' WHERE entry=-1609264; /* content: I make a promise to you now, brothers and sisters: The Lich King will be defeated! On this day, I call for a union. */
UPDATE script_texts SET content_loc8='%s, признавая свое поражение, падает на колени перед Тирионом Фордрингом.' WHERE entry=-1609273; /* content: %s kneels in defeat before Tirion Fordring. */
UPDATE script_texts SET content_loc8='%s обнимает отца.' WHERE entry=-1609276; /* content: %s hugs his father. */
UPDATE script_texts SET content_loc8='%s судорожно хватает ртом воздух.' WHERE entry=-1609280; /* content: %s gasps for air. */
UPDATE script_texts SET content_loc8='%s погибает.' WHERE entry=-1609283; /* content: %s collapses. */
UPDATE script_texts SET content_loc8='Я не боюсь ничего! Тем более – вас!' WHERE entry=-1615000; /* content: I fear nothing! Least of all you! */
UPDATE script_texts SET content_loc8='На колени!' WHERE entry=-1615007; /* content: On your knees! */
UPDATE script_texts SET content_loc8='Ученик Шадрона появляется в Зоне сумерек!' WHERE entry=-1615008; /* content: A Shadron Disciple appears in the Twilight! */
UPDATE script_texts SET content_loc8='Неинтересно.' WHERE entry=-1615010; /* content: No contest. */
UPDATE script_texts SET content_loc8='Не надо... было... вмешиваться.' WHERE entry=-1615012; /* content: I should not... have held back... */
UPDATE script_texts SET content_loc8='Моя обязанность – оберегать эти яйца, и вы сгорите, прежде чем хоть пальцем тронете их!' WHERE entry=-1615018; /* content: It is my charge to watch over these eggs. I will see you burn before any harm comes to them! */
UPDATE script_texts SET content_loc8='Эта смехотворное сражение закончится СЕЙЧАС ЖЕ!' WHERE entry=-1615019; /* content: This pathetic siege ends NOW! */
UPDATE script_texts SET content_loc8='Гори, злосчастный сброд!' WHERE entry=-1615020; /* content: Burn, you miserable wretches! */
UPDATE script_texts SET content_loc8='Тенеброн! Ты тоже должна защищать яйца!' WHERE entry=-1615022; /* content: Tenebron! The eggs are yours to protect as well! */
UPDATE script_texts SET content_loc8='Мелкая мошка хорошо горит!' WHERE entry=-1615025; /* content: Such flammable little insects.... */
UPDATE script_texts SET content_loc8='Ваши обугленные кости завалят весь пол!' WHERE entry=-1615026; /* content: Your charred bones will litter the floor! */
UPDATE script_texts SET content_loc8='Посмотрим, как вы переносите жару.' WHERE entry=-1615027; /* content: How much heat can you take? */
UPDATE script_texts SET content_loc8='От вас останется лишь горстка пепла!' WHERE entry=-1615028; /* content: All will be reduced to ash! */
UPDATE script_texts SET content_loc8='Из тебя выйдет хорошая закуска для детенышей.' WHERE entry=-1615029; /* content: You will make a fine meal for the hatchlings. */
UPDATE script_texts SET content_loc8='Лава вокруг |3-1(%s) начинает бурлить!' WHERE entry=-1615032; /* content: The lava surrounding %s churns! */
UPDATE script_texts SET content_loc8='И это все, что ты можешь сделать?' WHERE entry=-1615035; /* content: Was that the best you can do? */
UPDATE script_texts SET content_loc8='Я буду ковыряться в зубах вашими костями!' WHERE entry=-1615037; /* content: I will pick my teeth with your bones! */
UPDATE script_texts SET content_loc8='В Сумраке появляется ученик Весперона!' WHERE entry=-1615041; /* content: A Vesperon Disciple appears in the Twilight! */
UPDATE script_texts SET content_loc8='%s начинает открывать сумеречный портал!' WHERE entry=-1615042; /* content: %s begins to open a Twilight Portal! */
UPDATE script_texts SET content_loc8='Низшие создания, вторгшиеся сюда! Как жаль, что ваш ум не так силен, как ваша храбрость!' WHERE entry=-1616000; /* content: Lesser beings, intruding here! A shame that your excess courage does not compensate for your stupidity! */
UPDATE script_texts SET content_loc8='Меня не остановить!' WHERE entry=-1616007; /* content: I AM UNSTOPPABLE! */
UPDATE script_texts SET content_loc8='Немногие испытывали боль, подобную той, что я сейчас причиню вам!' WHERE entry=-1616012; /* content: Few have experienced the pain I will now inflict upon you! */
UPDATE script_texts SET content_loc8='Пока я дышу, вам не добиться своего!' WHERE entry=-1616013; /* content: You will not succeed while I draw breath! */
UPDATE script_texts SET content_loc8='Я – Хранитель Магии! Моя сила безгранична!' WHERE entry=-1616016; /* content: I am the spell-weaver! My power is infinite! */
UPDATE script_texts SET content_loc8='Твой дух будет вечно скитаться здесь!' WHERE entry=-1616017; /* content: Your spirit will linger here forever! */
UPDATE script_texts SET content_loc8='Покоритесь!' WHERE entry=-1616020; /* content: SUBMIT! */
UPDATE script_texts SET content_loc8='Силы, действовавшие здесь, превосходили все, что ты можешь себе представить!' WHERE entry=-1616024; /* content: The powers at work here exceed anything you could possibly imagine! */
UPDATE script_texts SET content_loc8='Вы проиграли!' WHERE entry=-1616026; /* content: Your cause is lost! */
UPDATE script_texts SET content_loc8='Я расколю твой хрупкий разум на мелкие кусочки!' WHERE entry=-1616027; /* content: Your fragile mind will be shattered! */
UPDATE script_texts SET content_loc8='Я сделала то, что должна была сделать, брат. Ты не оставил мне выбора.' WHERE entry=-1616029; /* content: I did what I had to, brother. You gave me no alternative. */
UPDATE script_texts SET content_loc8='Итоги войны чрезмерно печалят меня, но разрушения и колоссальные потери жизни должны были быть остановлены. Несмотря на последние прегрешения Малигоса, я буду оплакивать его. Когда-то он был заступником, защитником. Сегодня пал один из сильнейших этого мира.' WHERE entry=-1616031; /* content: This resolution pains me deeply, but the destruction, the monumental loss of life had to end. Regardless of Malygos\' recent transgressions, I will mourn his loss. He was once a guardian, a protector. This day, one of the world\'s mightiest has fallen. */
UPDATE script_texts SET content_loc8='Искра мощи появляется из ближайшей расселины!' WHERE entry=-1616033; /* content: A Power Spark forms from a nearby rift! */
UPDATE script_texts SET content_loc8='%s глубоко вдыхает.' WHERE entry=-1616034; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc8='Секреты глубин должны остаться секретами.' WHERE entry=-1619000; /* content: The secrets of the deep shall remain hidden. */
UPDATE script_texts SET content_loc8='Шшшад ак кирииииишшш щакк-к-к!' WHERE entry=-1619002; /* content: Shhhad ak kereeesshh chak-k-k! */
UPDATE script_texts SET content_loc8='За Короля-лича!' WHERE entry=-1619004; /* content: For the Lich King! */
UPDATE script_texts SET content_loc8='Возможно, мы скоро станем друзьями.' WHERE entry=-1619005; /* content: Perhaps we will be allies soon. */
UPDATE script_texts SET content_loc8='Ан\'кахарский страж откладывает яйца!' WHERE entry=-1619007; /* content: An Ahn\'kahar Guardian hatches! */
UPDATE script_texts SET content_loc8='Кто из вас истинно верующий?' WHERE entry=-1619018; /* content: Who among you is devoted? */
UPDATE script_texts SET content_loc8='Эй, ты! Шаг вперед!' WHERE entry=-1619019; /* content: You there! Step forward! */
UPDATE script_texts SET content_loc8='Повелитель, подарок для тебя!' WHERE entry=-1619021; /* content: Master, a gift for you! */
UPDATE script_texts SET content_loc8='Слава Йогг-Сарону!' WHERE entry=-1619022; /* content: Glory to Yogg-Saron! */
UPDATE script_texts SET content_loc8='Поднимайся! Тебе еще предстоит пострадать.' WHERE entry=-1619024; /* content: Get up! You haven\'t suffered enough. */
UPDATE script_texts SET content_loc8='Не думайте, что ваше святотатство останется безнаказанным!' WHERE entry=-1619025; /* content: Do not expect your sacrilege... to go unpunished. */
UPDATE script_texts SET content_loc8='Сами стихии восстанут против живущих в этом мире! Лишь верным суждено спастись!' WHERE entry=-1619026; /* content: The elements themselves will rise up against the civilized world! Only the faithful will be spared! */
UPDATE script_texts SET content_loc8='Я – избранная!' WHERE entry=-1619031; /* content: I have been chosen! */
UPDATE script_texts SET content_loc8='Я предаю себя повелителю!' WHERE entry=-1619032; /* content: I give myself to the master! */
UPDATE script_texts SET content_loc8='Шгла\'йос праф мх\'наус.' WHERE entry=-1619033; /* content: Shgla\'yos plahf mh\'naus. */
UPDATE script_texts SET content_loc8='Гул\'кафх ан\'шел. Йог\'ал шшн ку увак нуул.' WHERE entry=-1619034; /* content: Gul\'kafh an\'shel. Yoq\'al shn ky ywaq nuul. */
UPDATE script_texts SET content_loc8='Уваг пуул скшгн: он\'ма йех\'глу зуг.' WHERE entry=-1619035; /* content: Ywaq puul skshgn: on\'ma yeh\'glu zuq. */
UPDATE script_texts SET content_loc8='Уваг ма фгва\'кул хнакф.' WHERE entry=-1619036; /* content: Ywaq ma phgwa\'cul hnakf. */
