UPDATE script_texts SET content_loc8='<Вы активируете переговорное устройство, и на нем появляется проекция дворфа. Это может быть только Бранн Бронзобород и никто иной.>

<Судя по его лицу, Бранн совсем не ожидал вас увидеть. Он принимается бешено жестикулировать.>' WHERE entry=-1619038; /* content:  */
UPDATE script_texts SET content_loc8='Лилх вааах, ухнагт ффшх за.' WHERE entry=-1619039; /* content: Iilth vwah, uhn\'agth fhssh za. */
UPDATE script_texts SET content_loc8='Смертные! Ваш путь закончится, так и не начавшись. Никто не смеет входить в обитель господина!' WHERE entry=-1631001; /* content: This is the beginning AND the end, mortals. None may enter the master\'s sanctum! */
UPDATE script_texts SET content_loc8='Плеть накроет этот мир волной смерти и разрушения!' WHERE entry=-1631002; /* content: The Scourge will wash over this world as a swarm of death and destruction! */
UPDATE script_texts SET content_loc8='ВИХРЬ КОСТЕЙ!' WHERE entry=-1631003; /* content: BONE STORM! */
UPDATE script_texts SET content_loc8='Костяная хватка!' WHERE entry=-1631004; /* content: Bound by bone! */
UPDATE script_texts SET content_loc8='Постой-ка тут!' WHERE entry=-1631005; /* content: Stick around! */
UPDATE script_texts SET content_loc8='Единственный выход – это смерть!' WHERE entry=-1631006; /* content: The only escape is death! */
UPDATE script_texts SET content_loc8='Кости для жертвоприношений!' WHERE entry=-1631007; /* content: More bones for the offering! */
UPDATE script_texts SET content_loc8='Сгинь в вечных муках!' WHERE entry=-1631008; /* content: Languish in damnation! */
UPDATE script_texts SET content_loc8='ВО МНЕ БУШУЕТ ЯРОСТЬ ГОСПОДИНА!' WHERE entry=-1631010; /* content: THE MASTER\'S RAGE COURSES THROUGH ME! */
UPDATE script_texts SET content_loc8='Вы здесь потому, что наделены особым знанием: вы понимаете, что на мир пало проклятие слепоты.' WHERE entry=-1631011; /* content: You have found your way here, because you are among the few gifted with true vision in a world cursed with blindness. */
UPDATE script_texts SET content_loc8='Вам удалось сквозь пелену лжи разглядеть источник истинной силы.' WHERE entry=-1631012; /* content: You can see through the fog that hangs over this world like a shroud, and grasp where true power lies. */
UPDATE script_texts SET content_loc8='Посмотрите на свои руки. Задумайтесь над нелепостью их строения. Кожа, мускулы, кровь, что пульсирует в жилах...' WHERE entry=-1631013; /* content: Fix your eyes upon your crude hands: the sinew, the soft meat, the dark blood coursing within. */
UPDATE script_texts SET content_loc8='Чем раньше вы осознаете собственную ущербность, тем скорее выберете иной путь.' WHERE entry=-1631015; /* content: The sooner you come to accept your condition as a defect, the sooner you will find yourselves in a position to transcend it. */
UPDATE script_texts SET content_loc8='Для нашего господина нет ничего невозможного. Его сила не знает границ. Ничто не может сломить его волю.' WHERE entry=-1631016; /* content: Through our Master, all things are possible. His power is without limit, and his will unbending. */
UPDATE script_texts SET content_loc8='Тот, кто откажется повиноваться, будет уничтожен. Тот же, кто будет служить ему верой и правдой, достигнет таких высот, о которых вы не в силах даже помыслить.' WHERE entry=-1631017; /* content: Those who oppose him will be destroyed utterly, and those who serve -- who serve wholly, unquestioningly, with utter devotion of mind and soul -- elevated to heights beyond your ken. */
UPDATE script_texts SET content_loc8='Довольно! Пришла пора взять все в свои руки!' WHERE entry=-1631019; /* content: Enough! I see I must take matters into my own hands! */
UPDATE script_texts SET content_loc8='Я освобождаю тебя от проклятия плоти!' WHERE entry=-1631021; /* content: I release you from the curse of flesh! */
UPDATE script_texts SET content_loc8='Ты не в силах противиться моей воле!' WHERE entry=-1631023; /* content: You are weak, powerless to resist my will! */
UPDATE script_texts SET content_loc8='ВО ИМЯ КОРОЛЯ-ЛИЧА!' WHERE entry=-1631028; /* content: BY THE MIGHT OF THE LICH KING! */
UPDATE script_texts SET content_loc8='Земля обагрится вашей кровью!' WHERE entry=-1631029; /* content: The ground runs red with your blood! */
UPDATE script_texts SET content_loc8='Веселитесь, слуги мои!' WHERE entry=-1631030; /* content: Feast, my minions! */
UPDATE script_texts SET content_loc8='Ты – ничтожество!' WHERE entry=-1631031; /* content: You are nothing! */
UPDATE script_texts SET content_loc8='Твоя душа не обретет покоя!' WHERE entry=-1631032; /* content: Your soul will find no redemption here! */
UPDATE script_texts SET content_loc8='Тогда выдвигаемся! Быст...' WHERE entry=-1631035; /* content: Let\'s get a move on then! Move ou... */
UPDATE script_texts SET content_loc8='Сейчас все будет еще хуже! Идите сюда, я покажу вам, какой силой меня наделил Король-лич!' WHERE entry=-1631037; /* content: Things are about to get much worse. Come, taste the power that the Lich King has bestowed upon me! */
UPDATE script_texts SET content_loc8='Рывок' WHERE entry=-1631039; /* content: Charge! */
UPDATE script_texts SET content_loc8='Присоединись ко мне, отец. Перейди на мою сторону, и вместе мы разрушим этот мир во имя Плети и во славу Короля-лича!' WHERE entry=-1631042; /* content: Join me, father. Join me and we will crush this world in the name of the Scourge -- for the glory of the Lich King! */
UPDATE script_texts SET content_loc8='Мой мальчик погиб у Врат Гнева. Я здесь, чтобы забрать его тело.' WHERE entry=-1631043; /* content: My boy died at the Wrath Gate. I am here only to collect his body. */
UPDATE script_texts SET content_loc8='Старый упрямец. У тебя нет шансов. Я сильнее и могущественнее, чем ты можешь представить.' WHERE entry=-1631044; /* content: Stubborn and old. What chance do you have? I am stronger, and more powerful than you ever were. */
UPDATE script_texts SET content_loc8='Я пообещал его матери, когда она умирала, что пройду через Темный портал один. Не важно, умер бы я или выжил – мой сын остался бы цел. И чист...' WHERE entry=-1631046; /* content: I made a promise to his mother before she died; that I would cross the Dark Portal alone - whether I lived or died, my son would be safe. Untainted... */
UPDATE script_texts SET content_loc8='Сегодня я исполняю это обещание.' WHERE entry=-1631047; /* content: Today, I fulfill that promise. */
UPDATE script_texts SET content_loc8='Это был сын Саурфанга – предводитель Орды у Врат Гнева. Страшный конец...' WHERE entry=-1631051; /* content: That was Saurfang\'s boy - the Horde commander at the Wrath Gate. Such a tragic end... */
UPDATE script_texts SET content_loc8='Отступи, Мурадин. Позволь пройти скорбящему отцу.' WHERE entry=-1631057; /* content: Stand down, Muradin. Let a grieving father pass. */
UPDATE script_texts SET content_loc8='%s плачет.' WHERE entry=-1631061; /* content: %s cries. */
UPDATE script_texts SET content_loc8='Все в порядке... Просто... Я очень горжусь своим королем.' WHERE entry=-1631063; /* content: It was nothing, your majesty. Just... I\'m proud of my king. */
UPDATE script_texts SET content_loc8='Так точно, Ваше Величество!' WHERE entry=-1631065; /* content: Right away, yer majesty! */
UPDATE script_texts SET content_loc8='%s кашляет.' WHERE entry=-1631066; /* content: %s coughs. */
UPDATE script_texts SET content_loc8='%s рыдает над трупом своего сына.' WHERE entry=-1631067; /* content: %s weeps over the corpse of his son. */
UPDATE script_texts SET content_loc8='Что? Прелесть? Нееееееееееееет!!!' WHERE entry=-1631070; /* content: What? Precious? Noooooooooo!!! */
UPDATE script_texts SET content_loc8='У-у!' WHERE entry=-1631071; /* content: WEEEEEE! */
UPDATE script_texts SET content_loc8='Отличные новости, народ! Слизь снова потекла!' WHERE entry=-1631074; /* content: Great news, everyone! The slime is flowing again! */
UPDATE script_texts SET content_loc8='Отличные новости, народ! Я починил трубы для подачи ядовитой слизи!' WHERE entry=-1631075; /* content: Good news, everyone! I\'ve fixed the poison slime pipes! */
UPDATE script_texts SET content_loc8='Я это заломал...' WHERE entry=-1631077; /* content: I brokes-ded it... */
UPDATE script_texts SET content_loc8='ТИХИЙ ЧАС!' WHERE entry=-1631078; /* content: Sleepy Time! */
UPDATE script_texts SET content_loc8='Мер-твец, мер-твец, мер-твец!' WHERE entry=-1631088; /* content: Dead, dead, dead! */
UPDATE script_texts SET content_loc8='Веселью конец!' WHERE entry=-1631089; /* content: Fun time over! */
UPDATE script_texts SET content_loc8='Па-па...' WHERE entry=-1631090; /* content: Da ... Ddy... */
UPDATE script_texts SET content_loc8='На вкус, как вишенка! ОЙ! Извиниите!' WHERE entry=-1631096; /* content: Tastes like... Cherry! Oh! Excuse me! */
UPDATE script_texts SET content_loc8='О-о, как неожиданно!' WHERE entry=-1631098; /* content: That was unexpected! */
UPDATE script_texts SET content_loc8='Прекрасные новости, народ!' WHERE entry=-1631099; /* content: Great news, everyone! */
UPDATE script_texts SET content_loc8='Глупые смертные. Думали, что одолели нас? Сан\'лейн – непобедимые воины Короля-лича. Теперь наши силы едины!' WHERE entry=-1631101; /* content: Foolish mortals. You thought us defeated so easily? The San\'layn are the Lich King\'s immortal soldiers! Now you shall face their might combined! */
UPDATE script_texts SET content_loc8='Темная сфера сделала меня НЕУЯЗВИМЫМ!' WHERE entry=-1631103; /* content: Such wondrous power! The Darkfallen Orb has made me INVINCIBLE! */
UPDATE script_texts SET content_loc8='Не стоило и отвлекаться на тебя.' WHERE entry=-1631105; /* content: Were you ever a threat? */
UPDATE script_texts SET content_loc8='Истина в смерти.' WHERE entry=-1631106; /* content: Truth is found in death. */
UPDATE script_texts SET content_loc8='Трепещите перед Талдарамом, смертные, ибо сила Сферы питает меня!' WHERE entry=-1631109; /* content: Tremble before Taldaram, mortals, for the power of the orb flows through me! */
UPDATE script_texts SET content_loc8='Насладитесь болью!' WHERE entry=-1631110; /* content: Delight in the pain! */
UPDATE script_texts SET content_loc8='Пища для червей.' WHERE entry=-1631111; /* content: Worm food. */
UPDATE script_texts SET content_loc8='Никому не будет пощады!' WHERE entry=-1631112; /* content: Beg for mercy! */
UPDATE script_texts SET content_loc8='%s смеется.' WHERE entry=-1631113; /* content: %s laughs. */
UPDATE script_texts SET content_loc8='Наксанар был досадным недоразумением! Сила Сферы позволит Валанару свершить отмщение!' WHERE entry=-1631115; /* content: Naxxanar was merely a setback! With the power of the orb, Valanar will have his vengeance! */
UPDATE script_texts SET content_loc8='Моя чаша полна.' WHERE entry=-1631116; /* content: My cup runneth over. */
UPDATE script_texts SET content_loc8='Кушать... подано.' WHERE entry=-1631117; /* content: Dinner... is served. */
UPDATE script_texts SET content_loc8='Теперь вы видите, насколько мы сильны?' WHERE entry=-1631118; /* content: Do you see NOW the power of the Darkfallen? */
UPDATE script_texts SET content_loc8='ПАДИТЕ НИЦ перед Сан\'лейн!' WHERE entry=-1631119; /* content: BOW DOWN BEFORE THE SAN\'LAYN! */
UPDATE script_texts SET content_loc8='Это было... неразумное... решение.' WHERE entry=-1631121; /* content: You have made an... unwise... decision. */
UPDATE script_texts SET content_loc8='Я только попробую на вкус...' WHERE entry=-1631122; /* content: Just a taste... */
UPDATE script_texts SET content_loc8='Я голодна!' WHERE entry=-1631123; /* content: Know my hunger! */
UPDATE script_texts SET content_loc8='Страдание!' WHERE entry=-1631124; /* content: SUFFER! */
UPDATE script_texts SET content_loc8='Как вам такое?' WHERE entry=-1631125; /* content: Can you handle this? */
UPDATE script_texts SET content_loc8='Сейчас все кончится!' WHERE entry=-1631128; /* content: THIS ENDS NOW! */
UPDATE script_texts SET content_loc8='Обнажите клинки, соратники. Врайкулы насмерть будут стоять за Королеву Льда.' WHERE entry=-1631130; /* content: Ready your arms, my Argent Brothers. The Vrykul will protect the Frost Queen with their lives. */
UPDATE script_texts SET content_loc8='Довольно пустой болтовни! Прибыли наши герои – с их помощью мы пробьемся через зал!' WHERE entry=-1631132; /* content: Enough idle banter! Our champions have arrived - support them as we push our way through the hall! */
UPDATE script_texts SET content_loc8='Однажды мы с тобой сражались плечом к плечу, Крок, но ты оказался предателем! Иди же, встречай свою вторую смерть!' WHERE entry=-1631133; /* content: You may have once fought beside me, Crok, but now you are nothing more than a traitor. Come, your second death approaches! */
UPDATE script_texts SET content_loc8='Нападай, Гроза Плети. Сейчас я покажу господину, кто из нас может называться чемпионом!' WHERE entry=-1631136; /* content: Come Scourgebane, I\'ll show the Lich King which one of us is truly worthy of the title, champion! */
UPDATE script_texts SET content_loc8='Может быть... ты и прав, Крок.' WHERE entry=-1631138; /* content: What a pitiful choice of an ally Crok. */
UPDATE script_texts SET content_loc8='Может быть... Ты прав... Крок.' WHERE entry=-1631139; /* content: Perhaps... you were right... Crok. */
UPDATE script_texts SET content_loc8='Я долго не продержусь.' WHERE entry=-1631143; /* content: I will not last much longer! */
UPDATE script_texts SET content_loc8='Трепещите, смертные, ибо ваша жалкая магия теперь бессильна!' WHERE entry=-1631149; /* content: Suffer, mortals, as your pathetic magic betrays you! */
UPDATE script_texts SET content_loc8='Вы чувствуете, как ледяная ладонь смерти сжимает сердце?' WHERE entry=-1631150; /* content: Can you feel the cold hand of death upon your heart? */
UPDATE script_texts SET content_loc8='Здесь ваше вторжение и окончится! Никто не уцелеет.' WHERE entry=-1631152; /* content: Your incursion ends here! None shall survive! */
UPDATE script_texts SET content_loc8='Умри!' WHERE entry=-1631154; /* content: Perish! */
UPDATE script_texts SET content_loc8='Хватит! Эти игры меня утомляют!' WHERE entry=-1631156; /* content: Enough! I tire of these games! */
UPDATE script_texts SET content_loc8='Да будет так. Герои, в атаку!' WHERE entry=-1631161; /* content: So be it. Champions, attack! */
UPDATE script_texts SET content_loc8='Я оставлю тебя в живых, чтобы ты увидел финал. Не могу допустить, чтобы величайший служитель Света пропустил рождение МОЕГО МИРА.' WHERE entry=-1631162; /* content: I\'ll keep you alive to witness the end, Fordring. I would not want the Light\'s greatest champion to miss seeing this wretched world remade in my image. */
UPDATE script_texts SET content_loc8='Смотрите, как мир рушится вокруг вас!' WHERE entry=-1631165; /* content: Watch as the world around you collapses! */
UPDATE script_texts SET content_loc8='Валь\'кира, твой господин зовет!' WHERE entry=-1631166; /* content: Val\'kyr, your master calls! */
UPDATE script_texts SET content_loc8='Ледяная Скорбь жаждет крови...' WHERE entry=-1631167; /* content: Frostmourne hungers... */
UPDATE script_texts SET content_loc8='Вы пришли, чтобы вершить суд над Артасом? Чтобы уничтожить Короля-лича?' WHERE entry=-1631168; /* content: You have come to bring Arthas to justice? To see the Lich King destroyed? */
UPDATE script_texts SET content_loc8='Помогите мне уничтожить эти истерзанные души! Вместе мы вытянем силу из Ледяной Скорби и ослабим Короля-лича!' WHERE entry=-1631170; /* content: Aid me in destroying these tortured souls! Together we will loosen Frostmourne\'s hold and weaken the Lich King from within! */
UPDATE script_texts SET content_loc8='Ледяная Скорбь поглотит душу вашего товарища...' WHERE entry=-1631172; /* content: Frostmourne feeds on the soul of your fallen ally! */
UPDATE script_texts SET content_loc8='КОНЕЦ СВЕТА!' WHERE entry=-1631173; /* content: APOCALYPSE! */
UPDATE script_texts SET content_loc8='Склонитесь перед своим господином и повелителем' WHERE entry=-1631174; /* content: Bow down before your lord and master! */
UPDATE script_texts SET content_loc8='Пришел конец!' WHERE entry=-1631177; /* content: The end has come! */
UPDATE script_texts SET content_loc8='Смотри, как я буду воскрешать их и превращать в воинов Плети. Они повергнут этот мир в пучину хаоса. Азерот падет от их рук, и ты станешь первой жертвой.' WHERE entry=-1631182; /* content: Watch now as I raise them from the dead to become masters of the Scourge. They will shroud this world in chaos and destruction. Azeroth\'s fall will come at their hands -- and you will be the first to die. */
UPDATE script_texts SET content_loc8='Мне по душе эта ирония.' WHERE entry=-1631183; /* content: I delight in the irony. */
UPDATE script_texts SET content_loc8='Невозможно...' WHERE entry=-1631185; /* content: Impossible... */
UPDATE script_texts SET content_loc8='Хватит, Артас! Твоя ненависть не заберет больше ни одной жизни!' WHERE entry=-1631186; /* content: No more, Arthas! No more lives will be consumed by your hatred! */
UPDATE script_texts SET content_loc8='Наконец я свободен! Все кончено, сын мой. Настал час расплаты.' WHERE entry=-1631187; /* content: Free at last! It is over, my son. This is the moment of reckoning. */
UPDATE script_texts SET content_loc8='Поднимитесь, воины Света!' WHERE entry=-1631188; /* content: Rise up, champions of the Light! */
UPDATE script_texts SET content_loc8='И вот я стою, как лев пред агнцами, и не дрожат они.' WHERE entry=-1631190; /* content: Now I stand, the lion before the lambs... and they do not fear. */
UPDATE script_texts SET content_loc8='%s впадает в исступление!' WHERE entry=-1631193; /* content: %s goes into a frenzy! */
UPDATE script_texts SET content_loc8='Кровавые чудовища |3-1(%s) почуяли запах крови!' WHERE entry=-1631194; /* content: %s\'s Blood Beasts gain the scent of blood! */
UPDATE script_texts SET content_loc8='Какой жалость...' WHERE entry=-1631196; /* content: Such a pity... */
UPDATE script_texts SET content_loc8='%s начинает призывать могучий вихрь!' WHERE entry=-1631198; /* content: %s begins casting Empowered Shock Vortex! */
UPDATE script_texts SET content_loc8='Больше душ для питания машины!' WHERE entry=-1632000; /* content: More souls to power the engine! */
UPDATE script_texts SET content_loc8='Еще одна душа обогатит Вместилище.' WHERE entry=-1632003; /* content: Another soul to strengthen the host! */
UPDATE script_texts SET content_loc8='Моя душа принадлежит вам, хозяин.' WHERE entry=-1632004; /* content: My soul for you, master. */
UPDATE script_texts SET content_loc8='Вихрь погубленных душ взывает к вам!' WHERE entry=-1632005; /* content: The vortex of the harvested calls to you! */
