UPDATE locales_npc_text SET Text0_0_loc6='Puedo enseñarte los mejores trucos para montar un tigre, ¡siempre que tengas suficiente oro para pagar la lección!',Text0_1_loc6='Puedo enseñarte los mejores trucos para montar un tigre, ¡siempre que tengas suficiente oro para pagar la lección!' WHERE entry=4877; /* Text0_0: I can teach you the finer points of Tiger Riding, provided you have enough gold to cover the lesson! */ /* Text0_1: I can teach you the finer points of Tiger Riding, provided you have enough gold to cover the lesson! */
