UPDATE locales_npc_text SET Text0_0_loc6='Hay varios instructores de chamanes en la Sala de Cristal. Puede que quieras ver al clarividente Nobundo. Muéstrate $gcompasivo:compasiva; con los Tábidos. Que la Luz te acompañe.',Text0_1_loc6='Hay varios instructores de chamanes en la Sala de Cristal. Puede que quieras ver al clarividente Nobundo. Muéstrate $gcompasivo:compasiva; con los Tábidos. Que la Luz te acompañe.' WHERE entry=9556; /* Text0_0: You will find a number of shaman trainers in the Crystal Hall. Perhaps you should visit Farseer Nobundo there, and show the Broken some pity. Light be with you. */ /* Text0_1: You will find a number of shaman trainers in the Crystal Hall. Perhaps you should visit Farseer Nobundo there, and show the Broken some pity. Light be with you. */
