UPDATE locales_npc_text SET Text0_0_loc6='El maestro de vuelo de Shattrath es Nutral. Lo encontrarás al este del centro de la ciudad.',Text0_1_loc6='El maestro de vuelo de Shattrath es Nutral. Lo encontrarás al este del centro de la ciudad.' WHERE entry=10385; /* Text0_0: Shattrath\'s flight master is Nutral. You will find him east of the city\'s center. */ /* Text0_1: Shattrath\'s flight master is Nutral. You will find him east of the city\'s center. */
