UPDATE locales_npc_text SET Text0_0_loc6='Sayoc, este feo orco, te enseña de dagas. Si buscas otro lugar, habla con Ileda en la Plaza del Errante de Lunargenta o con Archibald en el Barrio de la Guerra de Entrañas. ¿Vale?',Text0_1_loc6='Sayoc, este feo orco, te enseña de dagas. Si buscas otro lugar, habla con Ileda en la Plaza del Errante de Lunargenta o con Archibald en el Barrio de la Guerra de Entrañas. ¿Vale?' WHERE entry=10782; /* Text0_0: Sayoc, da ugly orc here, teach you daggers. You want teaching in other places, you talk to Ileda in da Farstrider Square of Silvermoon or Archibald in the War Quarter of Undercity, hokay? */ /* Text0_1: Sayoc, da ugly orc here, teach you daggers. You want teaching in other places, you talk to Ileda in da Farstrider Square of Silvermoon or Archibald in the War Quarter of Undercity, hokay? */
