UPDATE locales_npc_text SET Text0_0_loc6='¿Cómo puedo ayudarte, $c?',Text0_1_loc6='¿Cómo puedo ayudarte, $c?' WHERE entry=12541; /* Text0_0: How may I help you, $c? */ /* Text0_1: How may I help you, $c? */
UPDATE locales_npc_text SET Text0_0_loc6='Puedo borrar todos tus talentos para que puedas elegir otros nuevos.$B$BEste proceso es más caro cada vez que lo repites.',Text0_1_loc6='Puedo borrar todos tus talentos para que puedas elegir otros nuevos.$B$BEste proceso es más caro cada vez que lo repites.' WHERE entry=12542; /* Text0_0: I can erase all your talents so that you may choose new ones.$b$bThis becomes more expensive each time you do it. */ /* Text0_1: I can erase all your talents so that you may choose new ones.$b$bThis becomes more expensive each time you do it. */
