UPDATE locales_npc_text SET Text0_0_loc6='¿Estás $gdispuesto:dispuesta; a hacer lo que haga falta para proteger Lordaeron?' WHERE entry=13076; /* Text0_0: Are you ready to do whatever must be done to protect Lordaeron? */
